contract supplychains_request_add {
    data {
        request_type int
        param int
    }

    conditions {
    }

    action {

        var res_str1 string
        var res_str2 string
        var res_str_tmp string

        // suppliers
        if($request_type == 1){
            $steps_map = DBFind("supplychains_steps").Where("holder_id=? and holder_type = 1", $param)
            $i = 0
            while ($i < Len($steps_map)) {
                $vals_step = $steps_map[$i]
                $i = $i + 1

                if (res_str1 != ""){
                    res_str1 = res_str1 + ","
                }
                res_str1 = res_str1  + Str($vals_step["supply_id"])
            }
        }

        // transport
        if($request_type == 2){
            $steps_map = DBFind("supplychains_steps").Where("receiver_id=? and receiver_type = 2", $param)
            $i = 0
            while ($i < Len($steps_map)) {
                $vals_step = $steps_map[$i]
                $i = $i + 1

                if (res_str1 != ""){
                    res_str1 = res_str1 + ","
                }
                res_str1 = res_str1  + Str($vals_step["supply_id"])
            }

            $steps_map2 = DBFind("supplychains_steps").Where("receiver_id=? and receiver_type = 2 and receiver_signature=0 and holder_signature <> 0", $param)
            $i2 = 0
            while ($i2 < Len($steps_map2)) {
                $vals_step2 = $steps_map2[$i2]
                $i2 = $i2 + 1

                if (res_str_tmp != ""){
                    res_str_tmp = res_str_tmp + " or "
                }
                res_str_tmp = res_str_tmp  + "id=" + Str($vals_step2["supply_id"])
            }

            if (res_str_tmp != ""){
                $supply_map = DBFind("supplychains_supply").Where(res_str_tmp)
                $i3 = 0
                while ($i3 < Len($supply_map)) {
                    $vals_supply = $supply_map[$i3]
                    $i3 = $i3 + 1

                    if (res_str2 != ""){
                        res_str2 = res_str2 + ","
                    }
                    res_str2 = res_str2  + Str($vals_supply["cargo_id"])
                }
            }
        }

        // warehouses
        if($request_type == 3){
            $steps_map = DBFind("supplychains_steps").Where("receiver_id=? and receiver_type = 3", $param)
            $i = 0
            while ($i < Len($steps_map)) {
                $vals_step = $steps_map[$i]
                $i = $i + 1

                if (res_str1 != ""){
                    res_str1 = res_str1 + ","
                }
                res_str1 = res_str1  + Str($vals_step["supply_id"])
            }

            $steps_map2 = DBFind("supplychains_steps").Where("receiver_id=? and receiver_type = 3 and receiver_signature=0 and holder_signature <> 0", $param)
            $i2 = 0
            while ($i2 < Len($steps_map2)) {
                $vals_step2 = $steps_map2[$i2]
                $i2 = $i2 + 1

                if (res_str_tmp != ""){
                    res_str_tmp = res_str_tmp + " or "
                }
                res_str_tmp = res_str_tmp  + "id=" + Str($vals_step2["supply_id"])
            }

            if (res_str_tmp != ""){
                $supply_map = DBFind("supplychains_supply").Where(res_str_tmp)
                $i3 = 0
                while ($i3 < Len($supply_map)) {
                    $vals_supply = $supply_map[$i3]
                    $i3 = $i3 + 1

                    if (res_str2 != ""){
                        res_str2 = res_str2 + ","
                    }
                    res_str2 = res_str2  + Str($vals_supply["cargo_id"])
                }
            }
        }

        // recipients
        if($request_type == 4){
            $steps_map = DBFind("supplychains_steps").Where("receiver_id=? and receiver_type = 4", $param)
            $i = 0
            while ($i < Len($steps_map)) {
                $vals_step = $steps_map[$i]
                $i = $i + 1

                if (res_str1 != ""){
                    res_str1 = res_str1 + ","
                }
                res_str1 = res_str1  + Str($vals_step["supply_id"])
            }

            $steps_map2 = DBFind("supplychains_steps").Where("receiver_id=? and receiver_type = 4 and receiver_signature <> 0", $param)
            $i2 = 0
            while ($i2 < Len($steps_map2)) {
                $vals_step2 = $steps_map2[$i2]
                $i2 = $i2 + 1

                if (res_str_tmp != ""){
                    res_str_tmp = res_str_tmp + " or "
                }
                res_str_tmp = res_str_tmp  + "id=" + Str($vals_step2["supply_id"])
            }

            if (res_str_tmp != ""){
                $supply_map = DBFind("supplychains_supply").Where(res_str_tmp)
                $i3 = 0
                while ($i3 < Len($supply_map)) {
                    $vals_supply = $supply_map[$i3]
                    $i3 = $i3 + 1

                    if (res_str2 != ""){
                        res_str2 = res_str2 + ","
                    }
                    res_str2 = res_str2  + Str($vals_supply["cargo_id"])
                }
            }
        }

        //check for NULL result
        if (res_str1 == ""){
            res_str1 = "0"
        }

        if (res_str2 == ""){
            res_str2 = "0"
        }

        DBInsert("supplychains_requests", "request1,request2,request_type,param", res_str1, res_str2, $request_type, $param)  
    }
}

contract supplychains_send_notifics {
    data {
        supply_id int
    }

    conditions {
        $supply_map = DBFind("supplychains_supply").Where("id=?", $supply_id).Row()
        if !$supply_map {
            warning "Supply not found"
        }

        $step = Int($supply_map["step"])
        $total_steps = Int($supply_map["total_steps"])

        $step_map = DBFind("supplychains_steps").Where("supply_id=? and step=?", Int($supply_map["id"]), $step).Row()
        if !$step_map {
            warning "Step not found"
        }
    }

    action {
        if ($step < $total_steps) {

            $receiver_role_id = Int($step_map["receiver_role_id"])
            $holder_role_id = Int($step_map["holder_role_id"])

            var params string
            params = "cargo_id=" + Str($supply_map["cargo_id"]) + ",step_id=" + Str($step_map["id"])

            Notifications_Roles_Send("role_id,closure_type,icon_name,text_header,text_body,page_name,params_val",
                $receiver_role_id, 2, 7, "Supply Chain", "Requires your signature", "supplychains_sign", params)

            Notifications_Roles_Send("role_id,closure_type,icon_name,text_header,text_body,page_name,params_val",
                $holder_role_id, 2, 7, "Supply Chain", "Requires your signature", "supplychains_sign", params)
        }
    }

}

contract supplychains_sign {
    data {
        step_id int
        vFlag int
        photo string "image"
        notific_id int
    }

    conditions {}

    action {

        if ($vFlag == 1) {
            DBUpdateExt("supplychains_steps", "id", $step_id, "holder_signature, timestamp holder_date_signature", $key_id, $block_time)
        } else {
            DBUpdateExt("supplychains_steps", "id", $step_id, "receiver_signature, timestamp receiver_date_signature,photo", $key_id, $block_time, $photo)
        }

        var supply_steps array
        supply_steps = DBFind("supplychains_steps").Columns("holder_signature,receiver_signature,supply_id ").Where("id=?", $step_id)

        var supply_step map
        supply_step = supply_steps[0]

        var holder_signature int
        holder_signature = supply_step["holder_signature"]

        var receiver_signature int
        receiver_signature = supply_step["receiver_signature"]


        if (holder_signature != 0 && receiver_signature != 0) {
            var supply_id int
            supply_id = supply_step["supply_id"]

            var supplies array
            supplies = DBFind("supplychains_supply").Columns("step,total_steps").Where("id=?", supply_id)

            //DBInsert("debug", "data", "52")

            var supply map
            supply = supplies[0]

            var step int
            step = supply["step"]

            var total_steps int
            total_steps = supply["total_steps"]

            step = step + 1

            if step >= Int(Str(total_steps)) {
                DBUpdateExt("supplychains_supply", "id", supply_id, "step,status", step, 4)
            } else {
                DBUpdateExt("supplychains_supply", "id", supply_id, "step", step)
                supplychains_send_notifics("supply_id", supply_id)
            }
        }


        if ($notific_id > 0) {
            Notifications_Single_Close("notific_id", $notific_id)
        }

    }
}

