contract EditContract {
    data {
        Id int
        Value string "optional"
        Conditions string "optional"
        WalletId string "optional"
    }
    func onlyConditions() bool {
        return $Conditions && !$Value && !$WalletId
    }

    conditions {
        RowConditions("contracts", $Id, onlyConditions())
        if $Conditions {
            ValidateCondition($Conditions, $ecosystem_id)
        }
        $cur = DBFind("contracts").Columns("id,value,conditions,active,wallet_id,token_id").WhereId($Id).Row()
        if !$cur {
            error Sprintf("Contract %d does not exist", $Id)
        }
        if $Value {
            ValidateEditContractNewValue($Value, $cur["value"])
        }
        if $WalletId != "" {
            $recipient = AddressToId($WalletId)
            if $recipient == 0 {
                error Sprintf("New contract owner %s is invalid", $WalletId)
            }
            if Int($cur["active"]) == 1 {
                error "Contract must be deactivated before wallet changing"
            }
        } else {
            $recipient = Int($cur["wallet_id"])
        }
    }

    action {
        UpdateContract($Id, $Value, $Conditions, $WalletId, $recipient, $cur["active"], $cur["token_id"])
    }
    func rollback() {
        RollbackEditContract()
    }
}

contract EditBlockCron {
    data {
        Id int
        ContName string
        EveryBlock int
        Conditions string
        StartBlock int
        Limit int
        Deleted int "optional"
    }
    
    conditions {
        if Size($ContName) == 0 {
            error "Contract not found"
        }

        if $EveryBlock < 1 {
            error "Interval can not be less 1"
        }

        ValidateCondition($Conditions,$ecosystem_id)
        
        if $StartBlock < $block {
            error Sprintf("Start block can not be less than the current block number. Current block number %d", $block)
        }

        if $Limit < 0 {
            error "Limit can not be less 0"
        }
        
        if !$Deleted {
            $Deleted = 0
        }
    }

    action {
        EditDelayedContract("Id,Contract,EveryBlock,Conditions,BlockID,Limit,Deleted", $Id, $ContName, $EveryBlock, $Conditions, $StartBlock, $Limit, $Deleted)
    }
}

