contract ExportNewApp {
    data {
        ApplicationId int
    }

    conditions {
        $app_map = DBFind("applications").Columns("id,name").Where({id: $ApplicationId}).Row()
        if !$app_map{
            warning "Application not found"
        }
    }

    action {

        //=====================================================================================================
        //------------------------------------Menu search------------------------------------------------------
        var i int
        var pages_array array
        var menu_name_array array
        var menu_id_array array

        i = 0
        var pages_ret array
        pages_ret = DBFind("pages").Where({app_id: $ApplicationId})
        while i < Len(pages_ret) {
            var page_map map
            page_map = pages_ret[i]

            pages_array = Append(pages_array, Sprintf("%v", Str(page_map["menu"])))
            i = i + 1
        }

        if Len(pages_array) > 0 {
            var where_for_menu map
            where_for_menu["name"] = {"$in" : pages_array}

            i = 0
            var menu_ret array
            menu_ret = DBFind("menu").Where(where_for_menu)
            while i < Len(menu_ret) {
                var menu_map map
                menu_map = menu_ret[i]

                menu_name_array = Append(menu_name_array, Str(menu_map["name"]))
                menu_id_array = Append(menu_id_array, Str(menu_map["id"]))
                i = i + 1
            }
        }

        //=====================================================================================================
        //------------------------------------Creating settings------------------------------------------------

        var value map
        value["app_id"] = Str($ApplicationId)
        value["app_name"] = Str($app_map["name"])

        if Len(menu_name_array) > 0 {
            value["menu_id"] = Str(Join(menu_id_array, ", "))
            value["menu_name"] = Str(Join(menu_name_array, ", "))
            value["count_menu"] = Str(Len(menu_name_array))
        } else {
            value["menu_id"] = "0"
            value["menu_name"] = ""
            value["count_menu"] = "0"
        }

        $buffer_id = DBFind("buffer_data").Where({member_id:$key_id, key: "export"}).One("id")
        if !$buffer_id {
            DBInsert("buffer_data", {"member_id":$key_id,"key": "export", "value": value})
        } else {
            DBUpdate("buffer_data", Int($buffer_id), {"value": value})
        }
    }
}

contract Import {
    data {
        Data string
    }
    func decodeGlobals(s string) string {
        var globs array i lenGlobs int r map prefix from to string
        prefix = "import_"
        globs = ["ecosystem_id", "key_id", "isMobile", "role_id", "ecosystem_name", "app_id", "info_value_pages_count", "info_value_pages", "info_value_blocks_count", "info_value_blocks","info_value_menu_count", "info_value_menu", "info_value_parameters_count", "info_value_parameters","info_value_languages_count", "info_value_languages", "info_value_contracts_count", "info_value_contracts", "info_value_tables_count", "info_value_tables", "DataName", "DataCount", "DataInfo", "info_value_app_name", "import_id"]
        lenGlobs = Len(globs)
        while i < lenGlobs{
            r = globs[i]
            from = ToUpper("#" + prefix + r + "#")
            to = "#" + r + "#"
            s = Replace(s, from, to)
            i = i + 1
        }
        return s
    }

    conditions {
        $Data = decodeGlobals($Data)
        $ApplicationId = 0
        var app_map map
        app_map = DBFind("buffer_data").Columns("value->app_name").Where({key: "import_info", member_id: $key_id}).Row()

        if app_map{
            var app_id int ival string
            ival = Str(app_map["value.app_name"])
            app_id = DBFind("applications").Columns("id").Where({name: ival}).One("id")
            if app_id {
                $ApplicationId = Int(app_id)
            }
        }
    }

    action {
        var editors, creators map
        editors["pages"] = "EditPage"
        editors["blocks"] = "EditBlock"
        editors["menu"] = "EditMenu"
        editors["app_params"] = "EditAppParam"
        editors["languages"] = "EditLang"
        editors["contracts"] = "EditContract"
        editors["tables"] = "" // nothing

        creators["pages"] = "NewPage"
        creators["blocks"] = "NewBlock"
        creators["menu"] = "NewMenu"
        creators["app_params"] = "NewAppParam"
        creators["languages"] = "NewLang"
        creators["contracts"] = "NewContract"
        creators["tables"] = "NewTable"

        var dataImport array
        dataImport = JSONDecode($Data)
        var i int
        while i < Len(dataImport){
            var item cdata map type name string
            cdata = dataImport[i]
            if cdata {
                cdata["ApplicationId"] = $ApplicationId
                type = cdata["Type"]
                name = cdata["Name"]
                // Println(Sprintf("import %v: %v", type, cdata["Name"]))

                item = DBFind(type).Where({name: name}).Row()
                var contractName string
                if item {
                    contractName = editors[type]
                    cdata["Id"] = Int(item["id"])
                    if type == "contracts" {
                        if item["conditions"] == "false"{
                            // ignore updating impossibled
                            contractName = ""
                        }
                    } elif type == "menu"{
                        var menu menuItem string
                        menu = Replace(item["value"], " ", "")
                        menu = Replace(menu, "\n", "")
                        menu = Replace(menu, "\r", "")
                        menuItem = Replace(cdata["Value"], " ", "")
                        menuItem = Replace(menuItem, "\n", "")
                        menuItem = Replace(menuItem, "\r", "")
                        if Contains(menu, menuItem) {
                            // ignore repeated
                            contractName = ""
                        }else{
                            cdata["Value"] = item["value"] + "\n" + cdata["Value"]
                        }
                    }
                } else {
                    contractName = creators[type]
                }

                if contractName != ""{
                    CallContract(contractName, cdata)
                }
            }
            i = i + 1
        }
        // Println(Sprintf("> time: %v", $time))
    }
}

contract ImportUpload {
    data {
        input_file string "file"
    }
    func encodeGlobals(s string) string {
        var globs array i lenGlobs int r map prefix from to string
        prefix = "import_"
        globs = ["ecosystem_id", "key_id", "isMobile", "role_id", "ecosystem_name", "app_id", "info_value_pages_count", "info_value_pages", "info_value_blocks_count", "info_value_blocks","info_value_menu_count", "info_value_menu", "info_value_parameters_count", "info_value_parameters","info_value_languages_count", "info_value_languages", "info_value_contracts_count", "info_value_contracts", "info_value_tables_count", "info_value_tables", "DataName", "DataCount", "DataInfo", "info_value_app_name", "import_id"]
        lenGlobs = Len(globs)
        while i < lenGlobs{
            r = globs[i]
            from = "#" + r + "#"
            to = ToUpper("#" + prefix + r + "#")
            s = Replace(s, from, to)
            i = i + 1
        }
        return s
    }

    conditions {
        $input_file = BytesToString($input_file)
        $input_file = encodeGlobals($input_file)
        $limit = 10 // data piece size of import

        // init buffer_data, cleaning old buffer
        var initJson map
        $import_id = DBFind("buffer_data").Where({member_id:$key_id, key: "import"}).One("id")
        if $import_id {
            $import_id = Int($import_id)
            DBUpdate("buffer_data", $import_id, {"value": initJson})
        } else {
            $import_id = DBInsert("buffer_data", {"member_id":$key_id,"key": "import",
            "value": initJson})
        }

        $info_id = DBFind("buffer_data").Where({member_id:$key_id, key: "import_info"}).One("id")
        if $info_id {
            $info_id = Int($info_id)
            DBUpdate("buffer_data", $info_id, {"value": initJson})
        } else {
            $info_id = DBInsert("buffer_data", {"member_id":$key_id,"key": "import_info",
            "value": initJson})
        }
    }

    action {
        var input map arrData array
        input = JSONDecode($input_file)
        arrData = input["data"]

        var pages_arr blocks_arr menu_arr parameters_arr languages_arr contracts_arr tables_arr array

        // import info
        var i lenArrData int item map
        lenArrData = Len(arrData)
        while i < lenArrData{
            item = arrData[i]

            if item["Type"] == "pages" {
                pages_arr = Append(pages_arr, item["Name"])
            }elif item["Type"] == "blocks" {
                blocks_arr = Append(blocks_arr, item["Name"])
            }elif item["Type"] == "menu" {
                menu_arr = Append(menu_arr, item["Name"])
            }elif item["Type"] == "app_params" {
                parameters_arr = Append(parameters_arr, item["Name"])
            }elif item["Type"] == "languages" {
                languages_arr = Append(languages_arr, item["Name"])
            }elif item["Type"] == "contracts" {
                contracts_arr = Append(contracts_arr, item["Name"])
            }elif item["Type"] == "tables" {
                tables_arr = Append(tables_arr, item["Name"])
            }

            i = i + 1
        }

        var inf map
        inf["app_name"] = input["name"]
        inf["pages"] = Join(pages_arr, ", ")
        inf["pages_count"] = Len(pages_arr)
        inf["blocks"] = Join(blocks_arr, ", ")
        inf["blocks_count"] = Len(blocks_arr)
        inf["menu"] = Join(menu_arr, ", ")
        inf["menu_count"] = Len(menu_arr)
        inf["parameters"] = Join(parameters_arr, ", ")
        inf["parameters_count"] = Len(parameters_arr)
        inf["languages"] = Join(languages_arr, ", ")
        inf["languages_count"] = Len(languages_arr)
        inf["contracts"] = Join(contracts_arr, ", ")
        inf["contracts_count"] = Len(contracts_arr)
        inf["tables"] = Join(tables_arr, ", ")
        inf["tables_count"] = Len(tables_arr)

        if 0 == inf["pages_count"] + inf["blocks_count"] + inf["menu_count"] + inf["parameters_count"] + inf["languages_count"] + inf["contracts_count"] + inf["tables_count"] {
            warning "Invalid or empty import file"
        }

        // import data
        // the contracts is imported in one piece, the rest is cut under the $limit, a crutch to bypass the error when you import dependent contracts in different pieces
        var sliced contracts array
        i = 0
        while i <lenArrData{
            var items array l int item map
            while l < $limit && (i + l < lenArrData) {
                item = arrData[i + l]
                if item["Type"] == "contracts" {
                    contracts = Append(contracts, item)
                }else{
                    items = Append(items, item)
                }
                l = l + 1
            }
            var batch map
            batch["Data"] = JSONEncode(items)
            sliced = Append(sliced, batch)
            i = i + $limit
        }
        if Len(contracts) > 0{
            var batch map
            batch["Data"] = JSONEncode(contracts)
            sliced = Append(sliced, batch)
        }
        input["data"] = sliced

        // storing
        DBUpdate("buffer_data", $import_id, {"value": input})
        DBUpdate("buffer_data", $info_id, {"value": inf})

        var name string
        name = Str(input["name"])
		var cndns string
		cndns = Str(input["conditions"])
		
        if !DBFind("applications").Columns("id").Where({name:name}).One("id") {
            DBInsert("applications", {"name": name, "conditions": cndns})
        }
    }
}

