			DBUpdate("keys", $recipient, {"+amount": $amount})
		}
        DBInsert("history", {"sender_id": $key_id,"recipient_id": $recipient,
             "amount":$amount,"comment": $Comment,"block_id": $block,"txhash": $txhash})
