contract NodeOwnerCondition {
	conditions {
        $raw_full_nodes = SysParamString("full_nodes")
        if Size($raw_full_nodes) == 0 {
            ContractConditions("MainCondition")
        } else {
            $full_nodes = JSONDecode($raw_full_nodes)
            var i int
            while i < Len($full_nodes) {
                $fn = $full_nodes[i]
                if $fn["key_id"] == $key_id {
                    return true
                }
                i = i + 1
            }
            warning "Sorry, you do not have access to this action."
        }
	}
}

contract Restore {
    data {
        Id int
        Type string
        IdHistory int
    }
    conditions {
        var allowed map
        allowed["page"] = 1
        allowed["block"] = 1
        allowed["menu"] = 1
        allowed["contract"] = 1
        if allowed[$Type] != 1 {
            warning "Wrong type"
        }

        if $Type == "page" {
            $history = GetHistory("pages", $Id)
        }elif $Type == "block" {
            $history = GetHistory("blocks", $Id)
        }elif $Type == "menu" {
            $history = GetHistory("menu", $Id)
        }elif $Type == "contract" {
            $history = GetHistory("contracts", $Id)
        }
    }
    action {
        var rec params map
        rec = $history[$IdHistory]
        params["Id"] = $Id
        params["Value"] = rec["value"]
        params["Conditions"] = rec["conditions"]

        if $Type == "page" {
            params["Menu"] = rec["menu"]
            CallContract("@1EditPage", params)
        }elif $Type == "block" {
            CallContract("@1EditBlock", params)
        }elif $Type == "menu" {
            CallContract("@1EditMenu", params)
        }elif $Type == "contract" {
            CallContract("@1EditContract", params)
        }
    }
}

contract SectionLevelDown {
   	data {
		id int
    }
	
    conditions {
		if !DBFind("sections").Columns("id").Where({"id":$id}) {
		    warning Sprintf("Section with id '%v' not exists", $id)
		}
		$next = DBFind("sections").Columns("id").Where({"id":{"$gt":$id}})
        	if !$next {
		    warning "Section already at bottom"
		}
    }
	
	action {
		var next_id int
		var last_id int
		next_id = $id + 1
		$max_id = DBFind("sections").Columns("id").Order({"id":"-1"}).One("id")
		last_id = $max_id + 1
		DBUpdate("sections", next_id, {"id":last_id})
		DBUpdate("sections", $id, {"id":next_id})
		DBUpdate("sections", last_id, {"id":$id})
	}
}

contract SectionLevelUp {
	data {
		id int
	}
	
	conditions {
		if !DBFind("sections").Columns("id").Where({"id":$id}) {
		    warning Sprintf("Section with id '%v' not exists", $id)
		}
		$next = DBFind("sections").Columns("id").Where({"id":{"$lt":$id}})
		if !$next {
		    warning "Section already at bottom"
		}
	}
	
	action {
		var next_id int
		var last_id int
		next_id = $id - 1
		$max_id = DBFind("sections").Columns("id").Order({"id":"-1"}).One("id")
		last_id = $max_id + 1
		DBUpdate("sections", next_id, {"id":last_id})
		DBUpdate("sections", $id, {"id":next_id})
		DBUpdate("sections", last_id, {"id":$id})
	}
}

contract SectionRoles {
	data {
		id int
		rid int
		operation string
	}

	conditions {
		if $operation == "add" {
			var roles_id map
			var json array
			var i int
			i = 0
			roles_id = DBFind("sections").Where({"id":$id}).Columns("roles_access").Row()
			json = JSONDecode(roles_id["roles_access"])
			while i < Len(json) {
				if (json[i]==$rid) {
					warning "This role is already have permission"
				}
				i = i + 1
			}
		}
	}
	
	action {
		var roles_array array
		roles_array = DBFind("sections").Where({"id":$id}).Columns("roles_access").One("roles_access")
		if $operation == "add" {
			if (roles_array=="[]") {			
				var roles_id array
				roles_id[0] = $rid
				roles_id = JSONEncode(roles_id)
				DBUpdate("sections", $id, {"roles_access":roles_id})
			} else {
				var roles_id map
				var json array
				roles_id = DBFind("sections").Where({"id":$id}).Columns("roles_access").Row()
				json = JSONDecode(roles_id["roles_access"])
				json = Append(json, $rid)
				json = JSONEncode(json)
				DBUpdate("sections", $id, {"roles_access":json})
			}
		} 
		if $operation == "remove" {
				var roles_id map
				var json array
				var buffer string
				roles_id = DBFind("sections").Where({"id":$id}).Columns("roles_access").Row()
				json = JSONDecode(roles_id["roles_access"])
				buffer = Join(json, ",")
				if Contains(buffer, Str($rid)) {
					buffer = Replace(buffer, `,` + Str($rid), ``)
				}
				if HasPrefix(buffer, Str($rid)) {
					buffer = Replace(buffer, Str($rid) + `,`, ``)
				}
				json = Split(buffer, ",")
				json = JSONEncode(json)
				DBUpdate("sections", $id, {"roles_access":json})
		}   
	}
}

contract SelectApp {
    data {
        ApplicationId int
    }

    conditions {
        $app = DBFind("applications").Columns("id,name").Where({id: $ApplicationId}).Row()
        if !$app{
            warning "Application not found"
        }
    }

    action {
        var value map bufferId int
        value["app_id"] = Str($ApplicationId)
        value["app_name"] = $app["name"]

        bufferId = Int(DBFind("buffer_data").Where({member_id: $key_id,key: "export"}).One("id"))
        if bufferId >0 {
            DBUpdate("buffer_data", bufferId, {"value": value})
        } else {
            DBInsert("buffer_data", {"member_id": $key_id,"key": "export","value": value})
        }
    }
}

contract UpdateMetrics {
	conditions {
		ContractConditions("MainCondition")
	}
	action {
		var values array
		values = DBCollectMetrics()

		var i, id int
		var v map
		while (i < Len(values)) {
            var inmap map

            v = values[i]
            inmap["time"] = v["time"]
            inmap["key"] = v["key"]
            inmap["metric"] = v["metric"]
            
            id = Int(DBFind("metrics").Columns("id").Where(inmap).One("id"))
            var ival int
			if id != 0 {
                ival = Int(v["value"])
				DBUpdate("metrics", id, {"value": ival})
			} else {
                inmap["value"] = Int(v["value"])
				DBInsert("metrics", inmap )
			}
			i = i + 1
		}
	}
}

contract Export {
    data {}

    func createExport(items array) string {
        var res map
        res["name"] = $AppName
        res["conditions"] = $AppConditions
        res["data"] = items
        return JSONEncodeIndent(res, "    ")
    }
    func mapping(res map, resKey string, item map itemKey string){
        if item[itemKey] {
            res[resKey] = Str(item[itemKey])
        }
    }
    func prepareItem(item map, type string) string {
        var res map
        res["Type"] = type
        mapping(res, "Name", item, "name")
        mapping(res, "Value", item, "value")
        mapping(res, "Conditions", item, "conditions")
        mapping(res, "Menu", item, "menu")
        mapping(res, "Title", item, "title")
        mapping(res, "Trans", item, "res")
        mapping(res, "Columns", item, "columns")
        mapping(res, "Permissions", item, "permissions")
        mapping(res, "Confirmation", item, "confirmation")
        return res
    }

    func getColumnsWithType(table_name string, columnsJSON string) string {
        var colsMap map result columns array
        colsMap = JSONDecode(columnsJSON)
        columns = GetMapKeys(colsMap)
        var i int
        while i < Len(columns){
            if Size(columns[i]) > 0 {
                var col map
                col["name"] = columns[i]
                col["conditions"] = colsMap[col["name"]]
                col["type"] = GetColumnType(table_name, col["name"])
                result = Append(result, col)
            }
            i = i + 1
        }
        return JSONEncode(result)
    }

    func exportTable(type string, result array) array {
        var items array limit offset i lenItems int item map
        limit = 250
        while true{
            var rows array where map
            if type == "menu" {
                if Len($menus_names) > 0 {
                    where["name"] = {"$in": $menus_names}
                }
            }else{
                where["app_id"] = $AppID
            }
            if where {
                rows = DBFind(type).Limit(limit).Offset(offset).Where(where)
            }
            if Len(rows) > 0{
                i = 0
                while i < Len(rows){
                    items = Append(items, rows[i])
                    i = i + 1
                }
            }else{
                break
            }
            offset = offset + limit
        }
        i = 0
        lenItems = Len(items)
        while i < lenItems {
            item = items[i]
            if type == "tables" {
                item["columns"] = getColumnsWithType(item["name"], item["columns"])
            }
            result = Append(result, prepareItem(item, type))
            if type == "pages" {
                $menus_names = Append($menus_names, Sprintf("'%v'", item["menu"]))
            }
            i = i + 1
        }
        return result
    }

    conditions {
        var buf app map
        buf = DBFind("buffer_data").Columns("id,value->app_id,value->app_name").Where({member_id:$key_id, key: "export"}).Row()
        if !buf{
            warning "Application not selected"
        }
        $AppID = Int(buf["value.app_id"])
        $AppName = Str(buf["value.app_name"])

        app = DBFind("applications").Columns("id,conditions").Where({id:$AppID}).Row()
        if !app{
            warning "Application not found"
        }
        $AppConditions = app["conditions"]

        $menus_names = []
    }

    action {
        var exportJSON string items array
        items = exportTable("pages", items)
        items = exportTable("contracts", items)
        items = exportTable("blocks", items)
        items = exportTable("languages", items)
        items = exportTable("app_params", items)
        items = exportTable("tables", items)
        items = exportTable("menu", items)

        exportJSON = createExport(items)
        UploadBinary("Name,Data,ApplicationId,DataMimeType", "export", exportJSON, 1, "application/json")
    }
}
