						If(#col_type# == character){
							SetVar(input_type, "Character")
						}
						If(#col_type# == text){
							SetVar(input_type, "Text")
						}
						If(#col_type# == number){
							SetVar(input_type, "Number")
						}
						If(#col_type# == money){
							SetVar(input_type, "Money")
						}
						If(#col_type# == varchar){
