contract DelApplication {
    data {
        ApplicationId int
        Value int "optional"
    }

    conditions {
        if $Value < 0 || $Value > 1 {
            error "Incorrect value"
        }
        RowConditions("applications", $ApplicationId, false)
    }

    action {
        DBUpdate("applications", $ApplicationId, "deleted", $Value)
    }
}

contract EditAppParam {
    data {
        Id int
        Value string "optional"
        Conditions string "optional"
    }
    func onlyConditions() bool {
        return $Conditions && !$Value
    }

    conditions {
        RowConditions("app_params", $Id, onlyConditions())
        if $Conditions {
            ValidateCondition($Conditions, $ecosystem_id)
        }
    }

    action {
        var pars, vals array
        if $Value {
            pars[0] = "value"
            vals[0] = $Value
        }
        if $Conditions {
            pars = Append(pars, "conditions")
            vals = Append(vals, $Conditions)
        }
        if Len(vals) > 0 {
            DBUpdate("app_params", $Id, Join(pars, ","), vals...)
        }
    }
}

