            if notInArray(delegate["member.member_id"], delegateIds){
                id = Int(delegate["id"])
                @1RolesUnassign("row_id", id)
            }
            i = i + 1
        }

        // assign new roles
        var key map delegateId int
        i = 0
        while i < Len(delegateIds) {
            delegateId = Int(delegateIds[i])
            key = DBFind("@1keys").WhereId(delegateId).Row()
            if key {
