contract TokensLockoutMember {
    data {
        KeyId int
        Value int
    }
    func getPermission() bool {
        var array_permissions array result i int prevContract string
        array_permissions = ["@1VotingTokensRefundCreate", "@1TokensRefundAccept", "@1TokensRefundReject"]

        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        while i < Len(array_permissions) {
            var contract_name string
            contract_name = array_permissions[i]
            if contract_name==prevContract {
                result = 1
            }
            i = i + 1
        }

        if result == 0 {
            error LangRes("@1contract_chain_distorted", "en")
        }
        return 0
    }
    
    conditions {
        getPermission()
        
        if !DBFind("@1keys").Where({ecosystem:1, id:$KeyId}).One("id"){
            error LangRes("@1key_not_found", "en")
        }

        if Int($Value) < 0 || Int($Value) > 1 {
            error LangRes("@1value_between_0_and_1", "en")      
        }
    }

    action {
        DBUpdate("@1keys", $KeyId, {"blocked": $Value})
    }
}

contract TokensRefund {
    data {
        SenderId int
        RecipientId int
        Amount money
    }

    conditions {
        var prevContract string
        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        if prevContract != "@1TokensRefundAccept"{
            error LangRes("@1contract_start_tokensrefundaccept_only", "en")
        }

        $amount = Money($Amount)
        if $amount <= 0 {
            error LangRes("@1amount_less_equal_zero", "en")
        }
        
        $RecipientId = Int($RecipientId)
        $SenderId = Int($SenderId)
        var sender map
        sender = DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$SenderId}).Row()
        if !sender{
            error LangRes("@1sender_invalid", "en")
        }
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$RecipientId}).Row(){
            error LangRes("@1recipient_invalid", "en")
        }
        if sender["blocked"] == 1 {
            error LangRes("@1sender_blocked", "en")
        }
        if sender["amount"] < $amount {
            error Sprintf(LangRes("@1sender_amount_not_enough", "en"), sender["amount"])
        }
    }

    action {
        DBUpdate("@1keys", $SenderId, {"-amount": $amount})
        DBUpdate("@1keys", $RecipientId, {"+amount": $amount})
    }
}

contract TokensRefundAccept {
    data {
        Id string
    }
    func closeNotes() {
        var notes array
        notes = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, page_name:"@1voting_view", "page_params->voting_id":$votingID})
        var i int
        while i < Len(notes){
            var note map
            note = notes[i]
            var noteId int
            noteId = Int(note["id"])
            @1NotificationsClose("notific_id", noteId)
            i = i + 1
        }
    }

    conditions {
        var prevContract string
        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        if prevContract != "@1VotingDecisionCheck"{
            error LangRes("@1contract_start_votingdecisioncheck_only", "en")
        }

        $Id = Int($Id)
        $tokenrefund = DBFind("tokens_refund").Where({"id":$Id}).Row()
        if !$tokenrefund {
            error LangRes("@1tokens_refund_not_found", "en")
        }

        $victimId = Int($tokenrefund["victim_key_id"])
        $attackerId = Int($tokenrefund["attacker_key_id"])
        $amount = Money($tokenrefund["amount"])
        $votingID = Int($tokenrefund["voting_id"])
    }

    action {
        closeNotes()

        // status - 0. Accounts blocked, 1. Discussion, 2. Vote, 3. Closed
        // result - 0. Discussion and voting, 1. Cancelled, 2. Tokens returned
        if $tokenrefund["result"] == 1 || $tokenrefund["result"] == 2 || $tokenrefund["status"] == 3 {
            return
        }

        TokensLockoutMember("KeyId,Value", $victimId, 0)
        TokensLockoutMember("KeyId,Value", $attackerId, 0)
        
        TokensRefund("SenderId,RecipientId,Amount", $attackerId, $victimId, $amount)
        DBUpdate("tokens_refund", $Id, {status:3, result:2})
    }
}

