        if prevContract != "@1VotingDecisionCheck"{
            error LangRes("@1contract_start_votingdecisioncheck_only", "en")
        }
        
        $Id = Int($Id)
        $tokenrefund = DBFind("tokens_refund").Where({"id":$Id}).Row()
        if !$tokenrefund{
            error LangRes("@1tokens_refund_not_found", "en")
        }

        $victimId = Int($tokenrefund["victim_key_id"])
        $attackerId = Int($tokenrefund["attacker_key_id"])
        $amount = Money($tokenrefund["amount"])
        $votingID = Int($tokenrefund["voting_id"])
    }

    action {
        closeNotes()

        // status - 0. Accounts blocked, 1. Discussion, 2. Vote, 3. Closed
        // result - 0. Discussion and voting, 1. Cancelled, 2. Tokens returned
        if $tokenrefund["result"] == 1 || $tokenrefund["result"] == 2 || $tokenrefund["status"] == 3 {
            return
        }
        
        TokensLockoutMember("KeyId,Value", $victimId, 0)
        TokensLockoutMember("KeyId,Value", $attackerId, 0)

        DBUpdate("tokens_refund", $Id, {status:3, result:1})
    }
}

contract TokensTransfer {
    data {
        Amount money
        Sender_AccountId int
        Recipient_AccountId int
        Comment string "optional"
        //Signature string "optional hidden"
    }

    conditions {
        //check amount
        if $Amount == 0 {
            error LangRes("@1amount_zero", "en")
        }
        if $Amount < 0 {
            error LangRes("@1amount_less_zero", "en")
        }

        //check the sender
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$Sender_AccountId}).One("id") {
            error Sprintf(LangRes("@1sender_x_invalid", "en"), $Sender_AccountId)
        }

        //check balance
        $sender_balance = Money(DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$Sender_AccountId}).One("amount"))
        if $Amount > $sender_balance {
            error LangRes("@1amount_less_than_needed", "en")
        }

        //check the recipient
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$Recipient_AccountId}).One("id") {
            error Sprintf(LangRes("@1recipient_x_invalid", "en"), $Recipient_AccountId)
        }
    }

    action {
        DBUpdate("@1keys", $Sender_AccountId, {"-amount":$Amount})
        DBUpdate("@1keys", $Recipient_AccountId, {"+amount":$Amount})
        var m map
        m["sender_id"] = $Sender_AccountId
        m["recipient_id"] = $Recipient_AccountId
        m["amount"] = $Amount
        m["comment"] = $Comment
        m["block_id"] = $block
        m["txhash"] = $txhash
        m["ecosystem"] = $ecosystem_id
        DBInsert("@1history", m)
    }
}

