contract VotingDecisionCheck {
    data {
        votingID int
    }
    
    func runContract(cont, p string) {
        if Size(cont) > 0 {
            var params map
            if Size(p) > 0 {
                params = JSONToMap(p)
            }
            CallContract(cont, params)
        }
    }
    
    func percentPartOfTotal(part, total int) int {
        if total == 0{ total = 1 }
        var percent int
        percent = Int((part * 100) / total)
        if percent > 100 {
            percent = 100
        }
        return percent
    }
    
    func totalSummForTypeVotingVoters(votingId int) int {
        var type_number_voters array
        type_number_voters = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID})
        var i int
        i = 0
        var summ int
        while i < Len(type_number_voters) {
            var type_number_voter map
            type_number_voter = type_number_voters[i]
            summ = summ + Int(type_number_voter["decision"])
            i = i + 1
        }
        return summ
    }
    
    func closeNotes() {
        var notes array i int
        notes = DBFind("@1notifications").Where({closed:0, ecosystem:$ecosystem_id, page_name:"@1voting_view", "page_params->voting_id":$votingID})
        while i < Len(notes){
            var note map
            note = notes[i]
            @1NotificationsClose("notific_id", Int(note["id"]))
            i = i + 1
        }
    }
    
    conditions {
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5

        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id: $votingID }).Columns("voting->name,voting->count_type_voters,voting->type,voting->quorum,voting->type_decision,flags->success,progress->number_voters,progress->percent_voters,optional->role_id,optional->role_vacancies,optional->contract_accept,optional->contract_reject,optional->contract_accept_params,optional->contract_reject_params,status").Row()
        $voting_name = $voting["voting.name"]
        $voting_type = Int($voting["voting.type"]) //system or custom
        $type_decision = Int($voting["voting.type_decision"])
        $optional_role_id = Int($voting["optional.role_id"])
        $number_role_vacancies = Int($voting["optional.role_vacancies"])
        $number_voters = Int($voting["progress.number_voters"]) // already voted
        $contract_accept = $voting["optional.contract_accept"]
        $contract_reject = $voting["optional.contract_reject"]
        $param_accept = $voting["optional.contract_accept_params"]
        $param_reject = $voting["optional.contract_reject_params"]
        $flag_success = $voting["flags.success"]
        $quorum = Int($voting["voting.quorum"])
        $percent_voters = Int($voting["progress.percent_voters"])
        $status = Int($voting["status"])

        var prevContract string
        prevContract = $stack[0]
        if Len($stack) > 2{
            prevContract = $stack[Len($stack) - 2]
        }
        if prevContract != "@1VotingUpdate"{
            // if $percent_voters < $quorum && $quorum > 50 || $status != $FINISHED {
            if $status != $FINISHED {
                warning LangRes("@1voting_not_expired", "en")
            }
        }

        $subject_id = DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID}).One("id")
        if !$subject_id {
            warning LangRes("@1voting_subject_not_found", "en")
        }

        $ACCEPT = 1
        $REJECT = -1
    }

    action {
        var percent_voters flag_decision i int
        flag_decision = -2 // decision can not be made (not enough participants to make a decision)
        if $flag_success == $ACCEPT { //now enough participants to make a decision
            if $type_decision == 1 || $type_decision == 2 {
                var candidates_added_count int
                candidates_added_count = 0 // candidates who have already been added
                flag_decision = $REJECT // none of the candidates has been added yet

                var subjects array
                subjects = DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID}).Order({number_accept:"-1"}).Columns("voting_id,number_accept,subject->member_id,subject->member_name,subject->image_id")
                i = 0
                while i < Len(subjects) {
                    var subject map
                    subject = subjects[i]

                    // if there are still vacancies
                    if candidates_added_count < $number_role_vacancies {
                        percent_voters = percentPartOfTotal(Int(subject["number_accept"]), $number_voters)
                        if (Int($voting["voting.count_type_voters"]) == 0){
                            if percent_voters >= $quorum {
                                // added candidate to the role
                                var buffer_id int
                                var role map
                                buffer_id = Int($optional_role_id)
                                role = DBFind("@1roles").Columns("id,role_type,role_name,image_id").Where({ecosystem:$ecosystem_id, id: buffer_id}).Row()
                                if role {
                                    var member assignedMember map mid rid string
                                    member["member_id"] = subject["subject.member_id"]
                                    mid = member["member_id"]
                                    rid = role["id"]
                                    assignedMember = DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "member->member_id":mid, "role->id":rid, deleted:0}).Row()

                                    if !assignedMember {
                                        member["member_name"] = subject["subject.member_name"]
                                        member["image_id"] = subject["subject.image_id"]

                                        var rolePart appointed m map
                                        rolePart["id"] = role["id"]
                                        rolePart["type"] = role["role_type"]
                                        rolePart["name"] = role["role_name"]
                                        rolePart["image_id"] = role["image_id"]

                                        appointed["voting_id"] = $votingID
                                        appointed["voting_name"] = $voting_name

                                        // add a candidate to the role
                                        m["role"] = rolePart
                                        m["member"] = member
                                        m["appointed"] = appointed
                                        m["date_created"] = BlockTime()
                                        m["ecosystem"] = $ecosystem_id
                                        DBInsert("@1roles_participants", m)

                                        candidates_added_count = candidates_added_count + 1
                                        // if at least one of the candidates was added to the role, then the voting decision is considered successful
                                        flag_decision = $ACCEPT
                                    }
                                }
                            }
                        }
                    }
                    i = i + 1
                }
            }

            if $type_decision == 3 {
                var subject map
                subject = DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID}).Columns("id,number_accept").Row()
                if subject {
                    if (Int($voting["voting.count_type_voters"]) == 0){
                        percent_voters = percentPartOfTotal(Int(subject["number_accept"]), $number_voters)
                        if percent_voters >= $quorum {
                            flag_decision = $ACCEPT
                        } else {
                            flag_decision = $REJECT
                        }
                    } else {
                        var total_summ_type_voter int
                        total_summ_type_voter = totalSummForTypeVotingVoters($votingID)
                        if total_summ_type_voter >= 1 {
                            flag_decision = $ACCEPT
                        } else {
                            flag_decision = $REJECT
                        }
                    }
                }
            }

            if $type_decision == 4 {
                var subject map
                subject = DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID}).Columns("id,number_accept,subject->table,subject->table_id,subject->column,subject->column_value").Row()
                if subject {
                    if (Int($voting["voting.count_type_voters"]) == 0){
                        percent_voters = percentPartOfTotal(Int(subject["number_accept"]), $number_voters)
                        if percent_voters >= $quorum {
                            flag_decision = $ACCEPT

                            var column string
                            column = Str(subject["subject.column"])
                            $colValue = Str(subject["subject.column_value"])
                            // type of voting - custom
                            if $voting_type == 1 {
                                // ALWAYS CHECK THE PERMISSION
                                var colType string
                                colType = GetColumnType(subject["subject.table"], column)
                                if colType == "number"{
                                    $colValue = Int($colValue)
                                }
                                if colType == "money"{
                                    $colValue = Money($colValue)
                                }
                                if colType == "double" {
                                    $colValue = Float($colValue)
                                }
                                var m map
                                m[column] = $colValue
                                DBUpdate(subject["subject.table"], Int(subject["subject.table_id"]), m)
                            }

                            // type of voting - system
                            if $voting_type == 2 {
                                @1UpdateSysParam("Name,Value,Conditions", column, $colValue, "")
                            }
                        } else {
                            flag_decision = $REJECT
                        }
                    } else {
                        var total_summ_type_voter int
                        total_summ_type_voter = totalSummForTypeVotingVoters($votingID)
                        if total_summ_type_voter >= 1 {
                            flag_decision = $ACCEPT

                            var column string
                            column = Str(subject["subject.column"])
                            $colValue = Str(subject["subject.column_value"])
                            // type of voting - custom
                            if $voting_type == 1 {
                                // ALWAYS CHECK THE PERMISSION
                                var colType string
                                colType = GetColumnType(subject["subject.table"], column)
                                if colType == "number"{
                                    $colValue = Int($colValue)
                                }
                                if colType == "money"{
                                    $colValue = Money($colValue)
                                }
                                if colType == "double" {
                                    $colValue = Float($colValue)
                                }
                                var m map
                                m[column] = $colValue
                                DBUpdate(subject["subject.table"], Int(subject["subject.table_id"]), m)
                            }

                            // type of voting - system
                            if $voting_type == 2 {
                                @1UpdateSysParam("Name,Value,Conditions", column, $colValue, "")
                            }
                        } else {
                            flag_decision = $REJECT
                        }
                    }
                }
            }

            if $type_decision == 5 {
                var subject map
                subject = DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID}).Columns("id,number_accept,subject->contract_accept,subject->contract_reject,subject->contract_accept_params,subject->contract_reject_params").Row()
                if subject {
                    $contract_accept = subject["subject.contract_accept"]
                    $contract_reject = subject["subject.contract_reject"]
                    $param_accept = subject["subject.contract_accept_params"]
                    $param_reject = subject["subject.contract_reject_params"]

                    if (Int($voting["voting.count_type_voters"]) == 0){
                        percent_voters = percentPartOfTotal(Int(subject["number_accept"]), $number_voters)
                        if percent_voters >= $quorum {
                            flag_decision = $ACCEPT
                        } else {
                            flag_decision = $REJECT
                        }
                    } else {
                        var total_summ_type_voter int
                        total_summ_type_voter = totalSummForTypeVotingVoters($votingID)
                        if total_summ_type_voter >= 1 {
                            flag_decision = $ACCEPT
                        } else {
                            flag_decision = $REJECT
                        }
                    }
                }
            }

            if flag_decision == $ACCEPT {
                runContract($contract_accept, $param_accept)
                closeNotes()
            }
            if flag_decision == $REJECT {
                runContract($contract_reject, $param_reject)
                closeNotes()
            }
        }

        DBUpdate("@1votings", $votingID, {"flags->decision":flag_decision})
    }
}

contract VotingInvite {
    data {
        votingID int
        var_id int //0, member_id, role_id
    }

    func addVotingKey(key map, votingId int) {
        var m member map kid int name string
        kid = Int(key["id"])
        name = DBFind("@1members").Where({ecosystem:$ecosystem_id, id:kid}).One("member_name")

        member["member_id"] = kid
        if name != "" {
            member["member_name"] = name
        }else{
            member["member_name"] = IdToAddress(kid)
        }
        m["voting_id"] = votingId
        m["member"] = member
        m["ecosystem"] = $ecosystem_id
        DBInsert("@1votings_participants", m)
    }
    
    conditions {
        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id:$votingID, status:[{"$neq":$STARTED},{"$neq":$FINISHED}]}).Columns("progress,progress->number_participants,voting->type_participants,voting->type,id,date_ended").Row()
        if !$voting {
            warning LangRes("@1voting_expired_can_not_add_participants", "en")
        }

        $type_participants = $voting["voting.type_participants"]
        $type_voting = $voting["voting.type"]
        $number_participants = Int($voting["progress.number_participants"])

        if $number_participants > 1 && $type_voting == 2 {
            warning LangRes("@1system_voting_can_not_add_participants", "en")
        }
        var isAdded string
        if $type_participants == 2 {
            if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$var_id}).Row(){
                warning LangRes("@1key_not_found", "en")
            }
            isAdded = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID,"member->member_id":$var_id}).One("id")
            if isAdded {
                warning LangRes("@1member_added_before", "en")
            }
        }

        if $type_participants == 3 {
            isAdded = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID}).One("id")
            if isAdded {
                warning LangRes("@1voting_participants_added_before", "en")
            }
            if Len(DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":$var_id, deleted:0})) == 0 {
                warning LangRes("@1roles_participants_not_found", "en")
            }
        }
        if ($type_participants == 4 || $type_participants == 5) {
            isAdded = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID,"member->role_id":$var_id}).One("id")
            if isAdded {
                if ($type_participants == 4){
                    warning LangRes("@1role_members_added_before", "en")
                }
                if ($type_participants == 5){
                    warning LangRes("@1role_added_before", "en")
                }
            }
            if Len(DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":$var_id, deleted:0})) == 0 {
                warning LangRes("@1roles_participants_not_found", "en")
            }
        }
    }

    action {
        var i int
        if $type_participants == 1 { // everybody
            var keys array
            keys = DBFind("@1keys").Where({id:{"$neq":$guest_key}, ecosystem:$ecosystem_id})
            while i < Len(keys) {
                addVotingKey(keys[i], $votingID)
                $number_participants = $number_participants + 1
                i = i + 1
            }
        }
        if $type_participants == 2 { // manual
            var key map
            key = DBFind("@1keys").WhereId($var_id).Row()
            addVotingKey(key, $votingID)
            $number_participants = $number_participants + 1
        }
        if $type_participants == 3 { // role
            var roles array
            roles = DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":$var_id, deleted:0}).Order("id")
            while i < Len(roles) {
                var role m map
                role = roles[i]
                m["voting_id"] = $votingID
                m["member"] = role["member"]
                m["ecosystem"] = $ecosystem_id
                DBInsert("@1votings_participants", m)
                $number_participants = $number_participants + 1
                i = i + 1
            }
        }
        if $type_participants == 4 { // all participants in the selected roles vote
            var roles array
            roles = DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":$var_id, deleted:0}).Order("id")
            while i < Len(roles) {
                var role map
                role = roles[i]
                var cust map
                cust = JSONToMap(role["member"])
                cust["role_id"] = $var_id
                DBInsert("@1votings_participants", {ecosystem: $ecosystem_id, voting_id: $votingID, member: cust})
                $number_participants = $number_participants + 1
                i = i + 1
            }
        }
        if $type_participants == 5 { // one participant of the selected roles votes
            var role map
            role["role_id"] = $var_id
            role["member_id"] = "0"
            var json string
            json = JSONEncode(role)
            $role_id_send = $var_id
            DBInsert("@1votings_participants", {ecosystem: $ecosystem_id, voting_id: $votingID, member: json})
            $number_participants = $number_participants + 1
        }
        var v map
        v["progress->number_participants"] = $number_participants
        if $type_participants == 3 {
            v["voting->role_id"] = $var_id
            DBUpdate("@1votings", $votingID, v)
        }else{
            DBUpdate("@1votings", $votingID, v)
        }
    }
}

contract VotingUpdate {
    data {
        votingID int
    }

    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    func percentPartOfTotal(part, total int) int {
        if total == 0{ total = 1 }
        var percent int
        percent = Int((part * 100) / total)
        if percent > 100 { percent = 100 }
        return percent
    }

    conditions {
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id:$votingID, status:$STARTED}).Columns("flags,voting,progress,progress->number_voters,voting->count_type_voters").Row()
        if !$voting {
            warning LangRes("@1started_voting_not_found", "en")
        }
    }

    action {
        var progress voting map
        progress = safeJsonToMap($voting["progress"])
        voting = safeJsonToMap($voting["voting"])

        var flag_success percent_success percent_voters number_voters quorum volume int
        // increment count of voters
        quorum = Int(voting["quorum"])
        volume = Int(voting["volume"])
        number_voters = Int(progress["number_voters"]) + 1
        if(Int($voting["voting.count_type_voters"]) == 0){
            percent_voters = percentPartOfTotal(number_voters, Int(progress["number_participants"]))
            percent_success = percentPartOfTotal(percent_voters, volume)
        } else {
            percent_voters = percentPartOfTotal(number_voters, Int(progress["number_participants"]))
            percent_success = percentPartOfTotal(number_voters, volume)
        }

        // update the flag of success
        if percent_success == 100 {
            flag_success = 1
        }
        progress["percent_voters"] = percent_voters
        progress["percent_success"] = percent_success
        progress["number_voters"] = number_voters

        var m map
        m["progress"] = progress
        m["flags->success"] = flag_success

        $check_type = DBFind("@1votings").Columns("id,voting,voting->count_type_voters").Where({ecosystem:$ecosystem_id, id:$votingID}).One("voting.count_type_voters")
        if $check_type == 0 {
            if (percent_voters >= quorum && quorum > 50 && percent_voters >= volume) || percent_voters == 100 {
                // if a quorum is more than 50% we can immediately make the decision
                // OR
                // if all participants have voted we can not wait until the end of the voting period
                m["status"] = $FINISHED
                DBUpdate("@1votings", $votingID, m)
                @1VotingDecisionCheck("votingID", $votingID)
            }else{
                DBUpdate("@1votings", $votingID, m)
            }
        }
        if $check_type == 1 {
            $total_voters = Int($voting["progress.number_voters"]) + 1
            if ($total_voters >= volume) || percent_voters == 100 {
                // if a quorum is more than 50% we can immediately make the decision
                // OR
                // if all participants have voted we can not wait until the end of the voting period
                m["status"] = $FINISHED
                DBUpdate("@1votings", $votingID, m)
                @1VotingDecisionCheck("votingID", $votingID)
            }else{
                DBUpdate("@1votings", $votingID, m)
            }
        }
    }
}

contract VotingCandidateAccept {
    data {
        votingID int
        CandidateID int
        RoleId int
    }
    
    func closeNotification() {
        var role_id_search int
        role_id_search = Int(DBFind("@1notifications").Where({ecosystem:$ecosystem_id, "recipient->role_id":$RoleId, page_name:"@1voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
        if role_id_search > 0 {
            @1NotificationsClose("notific_id", role_id_search)
        } else {
            var id int
            id = Int(DBFind("@1notifications").Where({ecosystem:$ecosystem_id, "recipient->member_id":$key_id, page_name:"@1voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
            if id > 0 {
                @1NotificationsClose("notific_id", id)
            }
        }
    }
    
    conditions {
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
        $voting = DBFind("@1votings").Columns("id,flags->decision,flags->notifics,status").Where({ecosystem:$ecosystem_id, id: $votingID}).Row()
        if !$voting {
            warning LangRes("@1voting_not_found", "en")
        }
        if $voting["status"] == $FINISHED {
            warning LangRes("@1voting_expired_can_not_vote", "en")
        }
        if $voting["status"] != $STARTED {
            warning LangRes("@1voting_not_started", "en")
        }
        $subject_map = DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "subject->member_id":$CandidateID}).Row()
        if !$subject_map {
            warning LangRes("@1voting_subject_not_found", "en")
        }
        $participant_role_id = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "member->role_id":$RoleId,"member->member_id":0}).One("id")

        $check_type = DBFind("@1votings").Columns("id,voting,voting->type_participants").Where({ecosystem:$ecosystem_id, id: $votingID}).Row()
        if (Int($check_type["voting.type_participants"]) < 5){
            $participant_id = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "member->member_id":$key_id,decision:0}).One("id")
            if !$participant_id {
                warning LangRes("@1voting_not_participant", "en")
            }
        }
    }

    action {
        if $participant_role_id {
            $rid = DBFind("@1votings_participants").Columns("id,voting_id,member->role_id").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "member->role_id":$RoleId}).Row()
            if $rid {
                var mem_role int
                mem_role = Int($rid["member.role_id"])
                $participant_id = DBFind("@1roles_participants").Columns("id,role,role->id,member,member->member_id").Where({ecosystem:$ecosystem_id, "member->member_id":$key_id,"role->id":mem_role,"deleted":0}).One("member.member_id")
                if !$participant_id{
                    warning LangRes("@1voting_not_participant", "en")
                } else {
                    var solo_role_vot map
                    $membership_role_name = DBFind("@1members").Where({ecosystem:$ecosystem_id, id:$key_id}).Row()
                    solo_role_vot["role_id"] = Int($rid["member.role_id"])
                    solo_role_vot["member_id"] = $key_id
                    solo_role_vot["member_name"] = $membership_role_name["member_name"]
                    solo_role_vot["image_id"] = $membership_role_name["image_id"]
                    var json string
                    json = JSONEncode(solo_role_vot)
                    DBUpdate("@1votings_participants", Int($rid["id"]), {"member":json})
                    $participant_id = Int($rid["id"])
                }
            } else {
                warning LangRes("@1voting_not_participant", "en")
            }
        }
        
        if $voting["flags.notifics"] == 1 {
            closeNotification()
        }

        if $voting["flags.decision"] != 1 {
            var bt string
            bt = BlockTime()

            // write the result of the voting
            var m map
            m["decision_date"] = bt
            m["decision"] = $CandidateID
            DBUpdate("@1votings_participants", Int($participant_id), m)

            // increment count of accept
            DBUpdate("@1votings_subject", Int($subject_map["id"]), {"+number_accept":1})

            @1VotingUpdate("votingID", $votingID)
        }
    }
}

