            @1EditAppParam("Id,Value", Int(DBFind("@1app_params").Where({name: "voting_ves_template_id",ecosystem: $ecosystem_id}).One("id")), Int(DBFind("@1voting_templates").Where({title: "Voting for Valued Ecosystem",ecosystem: $ecosystem_id}).One("id")))
        }

        // tokenrefund
        template["title"] = "Token Refund"
        template["init_contract"] = ""
        template["contract_accept"] = "TokensRefundAccept"
        template["contract_reject"] = "TokensRefundReject"
        template["vacancies"] = 0
        template["candidates"] = 0
        template["type_decision"] = 5
        template["type_voting"] = 2
        template["subject"] = "Token refund"
        applyTemplate(template)
        
        if $fill_ids == "yes" {
            @1EditAppParam("Id,Value", Int(DBFind("@1app_params").Where({name: "voting_tokenrefund_template_id",ecosystem: $ecosystem_id}).One("id")), Int(DBFind("@1voting_templates").Where({title: "Token Refund",ecosystem: $ecosystem_id}).One("id")))
        }
    }
}

contract VotingTokensRefundCreate {
    data {
        VictimAccount string
        SuspectAccount string
        Amount money
        Note string
    }

    conditions {
        $victimId = AddressToId($VictimAccount)
        $attackerId = AddressToId($SuspectAccount)
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$victimId}).Row(){
            error LangRes("@1victim_key_invalid", "en")
        }
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$attackerId}).Row(){
            error LangRes("@1attacker_key_invalid", "en")
        }

        if $Amount <= 0 {
            error LangRes("@1amount_invalid", "en")
        }
        if Size($Note) == 0 {
            error LangRes("@1add_description", "en")
        }

        var app_id template_id int
        app_id = Int(DBFind("@1applications").Where({ecosystem:1, name:"Basic"}).Columns("name,id").One("id"))
        template_id = Int(AppParam(app_id,"voting_tokenrefund_template_id", 1))
        if template_id <= 0 {
            warning LangRes("@1template_id_not_found", "en")
        }

        $template = DBFind("@1voting_templates").Where({ecosystem:$ecosystem_id, id:template_id}).Row()
        $votersRoleId = Int($template["voters"])
        $votingName = $template["title"]
        $typeParticipants = Int($template["type_participants"])
        $typeDecision = Int($template["type_decision"])
        $typeVoting = Int($template["type_voting"])
        $volume = Int($template["volume"])
        $quorum = Int($template["quorum"])
        $contractAccept = $template["contract_accept"]
        $contractReject = $template["contract_reject"]
        $initContract = $template["init_contract"]

        if !RoleAccess($votersRoleId){
            var roleName string
            roleName = DBFind("@1roles").Where({ecosystem:1, id:$votersRoleId}).One("role_name")
            warning Sprintf(LangRes("@template_only_role_allowed", "en"), roleName)
        }
        if !DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":$votersRoleId, deleted:0}).One("id") {
            warning Sprintf(LangRes("@1role_id_no_members", "en"), $votersRoleId)
        }

        $desc = Sprintf("%v (" + LangRes("@1tokens_refund_victim", "en") + ": %v, " + LangRes("@1tokens_refund_attacker", "en") + ": %v, " + LangRes("@1amount", "en") + ": %v)", $votingName, $victimId, $attackerId, $Amount)
    }

    action {
        if Size($initContract) > 4 { // greater then "NULL"
            var m map
            CallContract($initContract, m)
        }

        TokensLockoutMember("KeyId,Value", $victimId, 1)
        TokensLockoutMember("KeyId,Value", $attackerId, 1)
        
        var votingId int params m map paramsJSON interval_days string
        interval_days = "3"
        votingId = @1VotingCreate("voting_name,voting_type,description,type_participants,type_decision,volume,quorum,interval", $votingName, $typeVoting, $desc, $typeParticipants, $typeDecision, $volume, $quorum, interval_days)

        m["victim_key_id"] = $victimId
        m["attacker_key_id"] = $attackerId
        m["amount"] = $Amount
        m["note"] = $Note
        m["validator_key_id"] = $key_id
        m["blocked_at"] = BlockTime()
        m["voting_id"] = votingId
        m["status"] = 1 // 0. Accounts blocked, 1. Discussion, 2. Vote, 3. Closed
        m["result"] = 0 // 0. Discussion and voting, 1. Cancelled, 2. Tokens returned
        params["Id"] = DBInsert("tokens_refund", m) //no $ecosystem_id

        paramsJSON = JSONEncode(params)
        @1VotingSubjectContract("votingID,contract_reject,contract_accept,contract_reject_params,contract_accept_params", votingId, $contractReject, $contractAccept, paramsJSON, paramsJSON)
        @1VotingInvite("votingID,var_id", votingId, $votersRoleId)
    }
}

contract VotingDecisionReject {
    data {
        votingID int
        RoleId int
    }
    
    func closeNotification() {
        var role_id_search int
        role_id_search = Int(DBFind("@1notifications").Where({ecosystem:$ecosystem_id, "recipient->role_id":$RoleId, page_name:"@1voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
        if role_id_search > 0 {
            @1NotificationsClose("notific_id", role_id_search)
        } else {
            var id int
            id = Int(DBFind("@1notifications").Where({ecosystem:$ecosystem_id, "recipient->member_id":$key_id, page_name:"@1voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
            if id > 0 {
                @1NotificationsClose("notific_id", id)
            }
        }
    }
    
    conditions {
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id: $votingID}).Columns("voting->type_participants,voting->role_id,flags->decision,flags->notifics,status").Row()
        if !$voting {
            warning LangRes("@1voting_not_found", "en")
        }
        if $voting["voting.type_participants"] == 3{
            if $voting["voting.role_id"] != $RoleId{
                warning LangRes("@1voting_role_incorrect", "en")
            }
        }
        if $voting["status"] == $FINISHED {
            warning LangRes("@1voting_expired_can_not_vote", "en")
        }
        if $voting["status"] != $STARTED {
            warning LangRes("@1voting_not_started", "en")
        }
        $participant_role_id = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "member->role_id":$RoleId,"member->member_id":0}).One("id")

        $check_type = DBFind("@1votings").Columns("id,voting,voting->type_participants").Where({ecosystem:$ecosystem_id, id: $votingID}).Row()
        if (Int($check_type["voting.type_participants"]) < 5){
            $participant_id = DBFind("@1votings_participants").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "member->member_id":$key_id,decision:0}).One("id")
            if !$participant_id {
                warning LangRes("@1voting_not_participant", "en")
            }
        }
    }

    action {
        if $participant_role_id {
            $rid = DBFind("@1votings_participants").Columns("id,voting_id,member->role_id").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "member->role_id":$RoleId}).Row()
            if $rid {
                var mem_role int
                mem_role = Int($rid["member.role_id"])
                $participant_id = DBFind("@1roles_participants").Columns("id,role,role->id,member,member->member_id").Where({ecosystem:$ecosystem_id, "member->member_id":$key_id,"role->id":mem_role,"deleted":0}).One("member.member_id")
                if !$participant_id{
                    warning LangRes("@1voting_not_participant", "en")
                } else {
                    var solo_role_vot map
                    $membership_role_name = DBFind("@1members").Where({ecosystem:$ecosystem_id, id: $key_id}).Row()
                    solo_role_vot["role_id"] = Int($rid["member.role_id"])
                    solo_role_vot["member_id"] = $key_id
                    solo_role_vot["member_name"] = $membership_role_name["member_name"]
                    solo_role_vot["image_id"] = $membership_role_name["image_id"]
                    var json string
                    json = JSONEncode(solo_role_vot)
                    DBUpdate("@1votings_participants",Int($rid["id"]),{"member":json})
                    $participant_id = Int($rid["id"])
                }
            } else {
                warning LangRes("@1voting_not_participant", "en")
            }
        }
        
        if $voting["flags.notifics"] == 1 {
            closeNotification()
        }
        if $voting["flags.decision"] != 1 {
            var bt string
            bt = BlockTime()

            DBUpdate("@1votings_participants", Int($participant_id), {decision_date:bt, decision:"-1"})
            @1VotingUpdate("votingID", $votingID)
        }
    }
}

contract VotingValidatorAccept {
    data {
        VotingTemplateId string
        KeyId string
        VotingId string
    }
    
    func getAppRoleId(id int, name string, warn bool) int {
        var rid int
        rid = Int(AppParam(id, name, 1))
        if rid == 0 && warn{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }
    
    func conditions {
        var prev string
        prev = $stack[0]
        if Len($stack) > 2{
            prev = $stack[Len($stack) - 2]
        }
        if prev != "@1VotingDecisionCheck"{
            warning LangRes("@1contract_start_votingdecisioncheck_only", "en")
        }

        var appId templateId int params map
        appId = Int(DBFind("@1applications").Where({ecosystem:1, name:"Basic"}).Columns("name,id").One("id"))
        templateId = Int(AppParam(appId,"voting_validators_template_id", 1))
        if !DBFind("@1voting_templates").Where({ecosystem:$ecosystem_id, id:templateId}).One("id"){
            warning LangRes("@1voting_template_not_found", "en")
        }
        if $VotingTemplateId != templateId{
            warning LangRes("@1template_incorrect", "en")
        }

        var candidateId oldRoleId roleId int roleName string
        oldRoleId = getAppRoleId(appId, "role_candidate_for_validators", false)
        roleId = getAppRoleId(appId, "role_validator", true)

        candidateId = DBFind("sysparams_candidate").Where({candidate_key_id:$KeyId}).One("candidate_key_id") //no $ecosystem_id
        if !candidateId {
            warning LangRes("@1candidate_key_not_found", "en")
        }
        $oldRoleRowId = Int(DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, deleted:0, "role->id":oldRoleId, "member->member_id":candidateId}).One("id"))

        $roleId = Int(DBFind("@1roles").Where({ecosystem:$ecosystem_id, id:roleId, deleted:0}).One("id"))
        if $roleId == 0 {
            warning LangRes("@1validator_role_not_found", "en")
        }
    }

    func action {
        if $oldRoleRowId > 0 {
            @1RolesUnassign("row_id", $oldRoleRowId)
        }
        @1RolesAssign("rid,member_id", $roleId, Int($KeyId))
    }
}

contract VotingValidatorInit {
    data {
        KeyId int
    }
    func getAppRoleId(id int, name string, warn bool) int {
        var rid int
        rid = Int(AppParam(id, name, 1))
        if rid == 0 && warn{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    conditions {
        ContractConditions("MainCondition")

        var appId roleId participant_id int
        appId = Int(DBFind("@1applications").Where({ecosystem:1, name:"Basic"}).Columns("name,id").One("id"))
        roleId = getAppRoleId(appId, "role_validator", true)
        participant_id = Int(DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, deleted:0, "role->id":roleId, "member->member_id":$KeyId}).One("id"))
        if (participant_id > 0){
            warning LangRes("@1candidate_already_validator", "en")  
        }

        $candidate = DBFind("sysparams_candidate").Where({candidate_key_id:$KeyId, deleted:0}).Row() //no $ecosystem_id
        if !$candidate{
            warning LangRes("@1candidate_not_found", "en")
        }
    }
    
    action {
        @1DelegateRefresh()
    }
}
