        $groupId = Int(group["id"])
        $groupType = Int(group["group_type"])

        if $groupId == 0 {
            warning LangRes("@1group_not_found", "en")
        }
        if $groupType == 1 {
            if Int(assigned["member.member_id"]) != $key_id {
