contract GroupsDelete {
    data {
        gr_id int
        ops string
    }

    conditions {
        @1GroupsAccessCheck("gr_id", $gr_id)
    }

    action {
        // delete all participants
        var participants array i int
        participants = DBFind("@1groups_participants").Where({"groups_info->id": $gr_id, "deleted": 0}).Columns("id")
        while i < Len(participants) {
            var p map
            p = participants[i]
            @1GroupsUnassign("row_id", Int(p["id"]))
            i = i + 1
        }

        var m map
        if $ops == "D" {
            m["deleted"] = 1
            m["date_deleted"] = $block_time
        }
        if $ops == "R" {
            m["deleted"] = 0
        }
        DBUpdate("@1groups", $gr_id, m)
    }
}

contract RolesAssign {
    data {
        rid int
        member_id int
    }

    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("@1members").Where({"ecosystem": $ecosystem_id, "id": kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": kid}).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }

    conditions {
        $role = DBFind("@1roles").Where({"id": $rid}).Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Row()
        if (Int($role["role_type"]) == 2){
            warning LangRes("@1elective_role_voting_only", "en")
        }
        if !$role {
            warning LangRes("@1role_not_found", "en")
        }

        if !DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": $member_id}).Row() {
            warning LangRes("@1key_not_found", "en")
        }

        if $role["deleted"] == 1 {
            warning LangRes("@1role_deleted_request_impossible", "en")
        }

        var specRules map prev_contract string
        prev_contract = $stack[0]
        if Len($stack) > 2{
            prev_contract = $stack[Len($stack) - 2]
        }
        specRules["@1VotingValidatorAccept"] = 1
        specRules["@1DelegateRefresh"] = 1
        specRules["@1ValidatorRequest"] = 1
        specRules["@1InvestorRefresh"] = 1

        if specRules[prev_contract] != 1 {
            @1RolesAccessCheck("rid", $rid)
        }
    }

    action {
        if DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "role->id": $rid, "member->member_id": $member_id, "deleted": 0}).One("id") {
            // do not re-assign and do not warning because this contract may be called for other
            return
        }
        var role member appointer map
        role["id"] = $role["id"]
        role["type"] = $role["role_type"]
        role["name"] = $role["role_name"]
        role["image_id"] = $role["image_id"]

        member = getMemberInfo($member_id)
        appointer = getMemberInfo($key_id)
        var m map
        m["role"] = role
        m["member"] = member
        m["appointed"] = appointer
        m["date_created"] = $block_time
        m["ecosystem"] = $ecosystem_id
        DBInsert("@1roles_participants", m)
    }
}

contract MembershipAdd {
    data {
        memberskeys string
    }

    func addPub(pub string) {
        pub = Replace(pub, " ", "")
        if Size(pub) < 128 {
            warning LangRes("@1key_too_short", "en")
        }
        if Size(pub) > 130 {
            warning LangRes("@1key_too_long", "en")
        }

        var k_id int
        k_id = PubToID(pub)
        if k_id == 0 {
            warning LangRes("@1public_key_invalid", "en")
        }
        $check_user = DBFind("@1keys").Where({"id": k_id, "ecosystem": $ecosystem_id})
        if $check_user {
            warning LangRes("@1key_already_exists", "en")
        }
        var pub_bytes bytes
        pub_bytes = HexToPub(pub)
        DBInsert("@1keys",{id:k_id, pub:pub_bytes, ecosystem:$ecosystem_id})
    }

    conditions {
        $system_app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "System"}).One("id")
        if $memberskeys == "" {
            warning LangRes("@1public_key_empty", "en")
        }
        $memberskeys = Replace($memberskeys, " ", "")
        $memberskeys = Replace($memberskeys, "\n", "")
        $memberskeys = Replace($memberskeys, "\r", "")

        var admin_id int
        admin_id = Int(AppParam(Int($system_app_id), "role_admin", $ecosystem_id))

        if !RoleAccess(admin_id) {
            warning LangRes("@1access_denied", "en")
        }

        if ($ecosystem_id == 1) {
            warning LangRes("@1impossible_for_platform_ecosystem", "en")
        }
    }

    action {
        if Contains($memberskeys, ",") {
            var pub_array array
            pub_array = Split($memberskeys, ",")

            var i int
            while i < Len(pub_array) {
                var pub_key string
                pub_key = Str(pub_array[i])
                addPub(pub_key)
                i = i + 1
            }
        } else {
            addPub($memberskeys)
        }
    }
}

contract NotificationsClose {
    data {
        notific_id int
    }

    func getPermission() int {
        var array_permissions array result i int prevContract string
        array_permissions = ["@1TokensRefundAccept", "@1TokensRefundReject", "@1VotingDecisionCheck", "@1VotingDelete", "@1QAnswer", "@1QOperations", "@1ErrandDelete"]

        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        while i < Len(array_permissions) {
            var contract_name string
            contract_name = array_permissions[i]
            if contract_name==prevContract {
                result = 1
            }
            i = i + 1
        }
        return result
    }


    conditions {
        $notify = DBFind("@1notifications").Where({"ecosystem": $ecosystem_id, "id": $notific_id}).Columns("id,date_start_processing,processing_info->member_id,closed,notification->type,recipient->member_id,recipient->role_id").Row()
        if !$notify {
            warning LangRes("@1notification_not_found", "en")
        }

        // checking the type of notification
        if $notify["notification.type"] != 1 && $notify["notification.type"] != 2 {
            warning LangRes("@1notification_type_not_found", "en")
        }

        // getting permission
        var flag_permission int
        flag_permission = getPermission()

        // checking personal notification
        if $notify["notification.type"] == 1 && flag_permission == 0 {
            if $notify["recipient.member_id"] != $key_id {
                warning LangRes("@1notification_another_member", "en")
            }
        }

        // checking role notification
        if $notify["notification.type"] == 2 && flag_permission == 0 {
            if $notify["date_start_processing"] == "" {
                warning LangRes("@1notification_processing_not_started", "en")
            }

            if $notify["processing_info.member_id"] != $key_id {
                warning LangRes("@1notice_processing_started_another_member", "en")
            }
        }
    }

    action {
        if $notify["closed"] > 0 {
            return
        }
        if $notify["notification.type"] == 1 {
            @1NotificationsProcess("notific_id", $notific_id)
        }
        var m map
        m["date_closed"] = $block_time
        m["closed"] = 1
        DBUpdate("@1notifications", $notific_id, m)

        if $notify["notification.type"] == 1 {
            UpdateNotifications($ecosystem_id, Int($notify["recipient.member_id"]))
        } else {
            UpdateRolesNotifications($ecosystem_id, Int($notify["recipient.role_id"]))
        }
    }
}

