contract RolesAccessManager {
    data {
        Action string
        Rid int
        ManagerRid string "optional"

        //filled inputs
        role_name string "optional"
        role_type int "optional"
    }

    func updateAccess(ra array) {
        var roles_access string
        roles_access = JSONEncode(ra)
        DBUpdate("@1roles", $Rid, {"roles_access": roles_access})
    }

    func updateBufferAccess(ra array) {
        var roles_access string
        roles_access = JSONEncode(ra)
        var filled map
        filled["roles_access"] = roles_access
        filled["role_name"] = $role_name
        filled["role_type"] = $role_type

        if !$buffer_id {
            DBInsert("@1buffer_data", {ecosystem:$ecosystem_id, "member_id": $key_id, "key": "roles_access", "value": filled})
        } else {
            DBUpdate("@1buffer_data", Int($buffer_id), {"value": filled})
        }
    }

    conditions {
        $ManagerRid = Int($ManagerRid)
        if $Rid > 0 {
            @1RolesAccessCheck("rid", $Rid)

            $role = DBFind("@1roles").Where({"ecosystem": $ecosystem_id, "id": $Rid}).Columns("id,roles_access").Row()
            if !$role {
                warning LangRes("@1role_not_found", "en")
            }
            if $ManagerRid > 0 {
                if !DBFind("@1roles").Where({"ecosystem": $ecosystem_id, "id": $ManagerRid}).One("id") {
                    warning LangRes("@1role_not_found", "en")
                }
            }
            var ids array, i int
            $ids = ids
            ids = JSONDecode($role["roles_access"])
            while i < Len(ids){
                if ids[i] > 0 {
                    $ids = Append($ids, ids[i])
                }
                i = i + 1
            }
        } else {
            $buffer_managers = 0
            $buffer_id = 0
            $ids = []
            var buffer map
            buffer = DBFind("@1buffer_data").Where({"ecosystem": $ecosystem_id, "member_id": $key_id, "key": "roles_access"}).Columns("id,value,value->roles_access").Row()
            if buffer {
                $buffer_managers = buffer["value.roles_access"]
                $buffer_id = buffer["id"]
            }
        }
    }

    action {
        if $Rid == 0 {
            if $buffer_managers {
                $ids = JSONDecode($buffer_managers)
            }
        }
        if $Action == "clean" {
            updateAccess([])
        }
        if $ManagerRid > 0 {
            var i int, idsNew array
            if $Action == "remove" {
                while i < Len($ids) {
                    if $ids[i] != $ManagerRid{
                        idsNew = Append(idsNew, $ids[i])
                    }
                    i = i + 1
                }
                if $Rid == 0 {
                    updateBufferAccess(idsNew)
                } else {
                    updateAccess(idsNew)
                }
            }
            if $Action == "add" {
                var double bool
                while i < Len($ids) {
                    if $ids[i] == $ManagerRid{
                        double = true
                    }
                    i = i + 1
                }
                if !double {
                    $ids = Append($ids, $ManagerRid)
                }
                if $Rid == 0 {
                    updateBufferAccess($ids)
                } else {
                    updateAccess($ids)
                }
            }
        }
    }
}

contract DelegateRefresh {

    func notInArray(val string, arr array) bool {
        var i int
        // info arr
        while i < Len(arr) {
            if val == arr[i] {
                return false
            }
            i = i + 1
        }
        return true
    }

    func getRoleId(name string) int {
        var rid int
        rid = Int(AppParam(Int($system_app_id), name, $ecosystem_id))
        if rid == 0 {
            warning Sprintf(LangRes("@role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    conditions {
        MainCondition()
        $system_app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "System"}).One("id")
        var roleId int
        roleId = getRoleId("role_delegate")
        $role = DBFind("@1roles").Where({"id": roleId, "deleted": 0}).Row()
        if !$role {
            warning LangRes("@1role_not_found", "en")
        }
    }

    action {
        // get delegates ids
        var delegateIds currentDelegates ecosystems array i ecoId int rid string
        ecosystems = DBFind("@1ecosystems").Columns("id")
        while i < Len(ecosystems) {
            var eco delegateParam map
            eco = ecosystems[i]
            ecoId = Int(eco["id"])
            delegateParam = DBFind("@1parameters").Where({"name": "delegate_account", "ecosystem": ecoId}).Row()
            if !delegateParam{
                delegateParam = DBFind("@1parameters").Where({"name": "founder_account", "ecosystem": ecoId}).Row()
            }

            if notInArray(delegateParam["value"], delegateIds){
                delegateIds = Append(delegateIds, delegateParam["value"])
            }
            i = i + 1
        }

        // remove old role assigns
        rid = Int($role["id"])
        currentDelegates = DBFind("@1roles_participants").Where({"role->id": rid, "deleted": 0}).Columns("id,member->member_id")
        var delegate map id int
        i = 0
        while i < Len(currentDelegates) {
            delegate = currentDelegates[i]
            if notInArray(delegate["member.member_id"], delegateIds){
                id = Int(delegate["id"])
                @1RolesUnassign("row_id", id)
            }
            i = i + 1
        }

        // assign new roles
        var key map delegateId int
        i = 0
        while i < Len(delegateIds) {
            delegateId = Int(delegateIds[i])
            key = DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": delegateId}).Row()
            if key {
                // check role dup
                if !DBFind("@1roles_participants").Where({"member->member_id": delegateId, "role->id": rid, "deleted": 0}).Row(){
                    @1RolesAssign("rid,member_id", rid, Int(key["id"]))
                }
            }
            i = i + 1
        }
    }
}

contract RolesCreate {
    data {
        role_name string
        role_type int
    }

    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("@1members").Where({"ecosystem": $ecosystem_id, "id": kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        } else {
            member = DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": kid}).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }

    conditions {
        $system_app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "System"}).One("id")
        // system role
        if $role_type == 3 {
            warning LangRes("@1system_role_can_not_create", "en")
        }

         // check role_creates_roles
        $check_access = AppParam(Int($system_app_id), "role_creates_roles", $ecosystem_id)
        if $check_access && Str($check_access) != "0" {
            var rids array
            rids = JSONDecode("["+$check_access+"]")
            if !DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "role->id": {"$in": rids}, "member->member_id": $key_id, "deleted": 0}).Row() {
                warning LangRes("@1access_denied", "en")
            }
        } else {
            var admin_id int
            admin_id = AppParam(Int($system_app_id), "role_admin", $ecosystem_id)
            var rids array
            rids = JSONDecode("["+admin_id+"]")
            if !DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "role->id": {"$in": rids}, "member->member_id": $key_id, "deleted": 0}).Row() {
                warning LangRes("@1access_denied", "en")
            }
        }
        if $role_name == DBFind("@1roles").Where({"ecosystem": $ecosystem_id, "role_name": $role_name}).One("role_name") {
            warning LangRes("@1role_exists", "en")
        }
        if Size($role_name) < 3 || Size($role_name) > 30 {
            warning LangRes("@1name", "en") + " " + LangRes("@1validate_role_name", "en")
        }
    }

    action {
        var roles_access_arr array, buffer map
        buffer = DBFind("@1buffer_data").Where({"ecosystem": $ecosystem_id, "member_id": $key_id, "key": "roles_access"}).Columns("id,value,value->roles_access").Row()
        if buffer {
            roles_access_arr = JSONDecode(buffer["value.roles_access"])
            var filled map
            filled["roles_access"] = []
            filled["role_name"] = ""
            filled["role_type"] = ""
            DBUpdate("@1buffer_data", Int(buffer["id"]), {"value": filled})
        }
        var m map
        m["creator"] = getMemberInfo($key_id)
        m["role_name"] = $role_name
        m["role_type"] = $role_type
        m["date_created"] = $block_time
        m["roles_access"] = JSONEncode(roles_access_arr)
        m["ecosystem"] = $ecosystem_id
        $result = DBInsert("@1roles", m)
    }
}

contract InvestorRefresh {
    data {
        RoleId int
    }

    func notInArray(val string, arr array) bool {
        var i lenArr int
        lenArr = Len(arr)
        while i < lenArr {
            if val == Str(arr[i]) {
                return false
            }
            i = i + 1
        }
        return true
    }

    func getParamInt(id int, name string) int {
        var param int
        param = Int(AppParam(id, name, 1))
        if param == 0 {
            warning Sprintf(LangRes("@1application_parameter_not_set", "en"), name)
        }
        return param
    }

    func getRoleId(name string) int {
        var rid int
        rid = Int(AppParam(Int($system_app_id), name, $ecosystem_id))
        if rid == 0 {
            warning Sprintf(LangRes("@role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    conditions {
        $system_app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "System"}).One("id")
        var appId int
        appId = Int(DBFind("@1applications").Where({"ecosystem": 1, "name": "Basic", "deleted": 0}).One("id"))
        if appId == 0 {
            warning LangRes("@1app_not_found", "en")
        }

        var roleValidatorId roleInvestorId string

        roleValidatorId = getRoleId("role_validator")
        if !RoleAccess(roleValidatorId) {
            warning LangRes("@1not_validator_can_not_run", "en")
        }

        roleInvestorId = getRoleId("role_investor_with_voting_rights")
        $roleId = Int(DBFind("@1roles").Where({"id": roleInvestorId, "deleted": 0}).One("id"))
        if $roleId == 0 {
            warning LangRes("@1investor_role_not_found_or_removed", "en")
        }

        $limitInvestors = getParamInt(appId, "investors_number")
    }

    action {
        // get investors ids
        var keysInvestors assignedInvestors investorsIds assignedIds array
        keysInvestors = DBFind("@1keys").Where({"ecosystem": $ecosystem_id}).Columns("id").Order([{amount:"-1"},{id:"-1"}]).Limit($limitInvestors)
        assignedInvestors = DBFind("@1roles_participants").Where({"role->id": $roleId, "deleted": 0}).Columns("member->member_id")

        // get ids
        var i int
        while i < Len(keysInvestors) {
            var inv map
            inv = keysInvestors[i]
            investorsIds[i] = inv["id"]
            i = i + 1
        }
        i = 0
        while i < Len(assignedInvestors) {
            var inv map
            inv = assignedInvestors[i]
            assignedIds[i] = inv["member.member_id"]
            i = i + 1
        }

        // remove old investors
        var assigned map mid string
        i = 0
        while i < Len(assignedIds) {
            if notInArray(assignedIds[i], investorsIds){
                mid = assignedIds[i]
                assigned = DBFind("@1roles_participants").Where({"member->member_id": mid, "role->id": $roleId}).Row()
                if assigned {
                    @1RolesUnassign("row_id", Int(assigned["id"]))
                }
            }
            i = i + 1
        }
        // add new investors
        i = 0
        while i < Len(investorsIds) {
            if notInArray(investorsIds[i], assignedIds){
                var id int
                id = Int(investorsIds[i])
                if DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": id}).Row(){
                    @1RolesAssign("rid,member_id", $roleId, id)
                }
            }
            i = i + 1
        }
    }
}

contract RolesInstall {

    func updateParam(name string, value int) {
        var app_param map
        app_param = DBFind("@1app_params").Where({"ecosystem": $ecosystem_id, "app_id": $app_id, "name": name}).Row()
        if app_param {
            var params map
            params["Id"] = Int(app_param["id"])
            params["Value"] = value
            params["Conditions"] = "ContractConditions(\"MainCondition\")"
            CallContract("@1EditAppParam", params)
        } else {
            var params map
            params["ApplicationId"] = $app_id
            params["Name"] = name
            params["Value"] = value
            params["Conditions"] = "ContractConditions(\"MainCondition\")"
            CallContract("@1NewAppParam", params)
        }
    }

    func addRole(name string, withParticipant bool, managerId int) int{
        var m map paramName string id int
        m["role_name"] = name
        m["role_type"] = 3
        m["ecosystem"] = $ecosystem_id
        id = Int(DBFind("@1roles").Where(m).One("id"))
        if id == 0 {
            m["default_page"] = ""
            m["deleted"] = 0
            m["creator->image_id"] = 0
            m["creator->member_name"] = "founder"
            m["creator->member_id"] = Str($key_id)
            m["date_created"] = $block_time
            if managerId > 0{
                m["roles_access"] = Sprintf("[%v]", managerId)
            }else{
                m["roles_access"] = "[]"
            }
            id = DBInsert("@1roles", m)
        }
        paramName = "role_" + Replace(ToLower(name), " ", "_")
        updateParam(paramName, id)

        if withParticipant {
            var participant map
            participant["role->id"] = id
            participant["role->type"] = m["role_type"]
            participant["role->name"] = m["role_name"]
            participant["role->image_id"] = 0
            participant["member->member_id"] = Str($key_id)
            participant["member->member_name"] = "founder"
            participant["member->image_id"] = 0
            participant["ecosystem"] = $ecosystem_id

            if !DBFind("@1roles_participants").Where(participant).One("id"){
                participant["date_created"] = $block_time
                DBInsert("@1roles_participants", participant)
            }
        }
        return id
    }

    conditions {
        MainCondition()
    }

    action {
        $app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "System"}).One("id")
        var managerId int
        managerId = addRole("Admin", true, 0)
        @1RolesAccessManager("Action,Rid,ManagerRid", "add", managerId, managerId)
        addRole("Developer", true, managerId)

        if $ecosystem_id == 1 {
            addRole("Consensus", false, managerId)
            addRole("Candidate for validators", false, managerId)
            addRole("Validator", false, managerId)
            addRole("Investor with voting rights", false, managerId)
            addRole("Delegate", false, managerId)
        }

        updateParam("role_creates_roles", 0)
    }
}

contract RolesRequest {
    data {
        rid int
    }

    conditions {
        if !DBFind("@1roles").WhereId($rid).One("id") {
            warning LangRes("@1role_not_found", "en")
        }

        if DBFind("@1roles_participants").Where({"role->id": $rid, "member->member_id":$key_id, "deleted": 0}).One("id") {
            warning LangRes("@1role_you_added_already", "en")
        }

        if DBFind("@1notifications").Where({"ecosystem": $ecosystem_id, "page_name": "@1roles_request_view", "page_params->rid": $rid, "sender->member_id": $key_id, "closed": 0}).One("id") {
            warning LangRes("@1request_sent_already", "en")
        }
    }

    action {
        var params map
        params["rid"] = $rid

