contract VotingDecisionCheck {
    data {
        votingID int
    }

    func runContract(cont, p string) {
        if Size(cont) > 0 {
            var params map
            if Size(p) > 0 {
                params = JSONToMap(p)
            }
            CallContract(cont, params)
        }
    }

    func closeNotes() {
        var notes array i int
        notes = DBFind("@1notifications").Where({"closed": 0, "ecosystem": $ecosystem_id, "page_name": "@1voting_view", "page_params->voting_id": $votingID})
        while i < Len(notes){
            var note map
            note = notes[i]
            @1NotificationsClose("notific_id", Int(note["id"]))
            i = i + 1
        }
    }

    conditions {
        $ACCEPT = 1
        $REJECT = -1

        $INVALID = 1
        $WAITING = 2
        $STARTED = 3
        $FINISHED = 4

        $voting = DBFind("@1votings").Where({"ecosystem": $ecosystem_id, "id": $votingID}).Columns("voting->name,voting->count_type_voters,voting->type,voting->volume,voting->type_decision,flags->success,progress->number_voters,progress->percent_voters,optional->role_id,optional->role_vacancies,optional->contract_accept,optional->contract_reject,optional->contract_accept_params,optional->contract_reject_params,status").Row()
        $type_decision = Int($voting["voting.type_decision"])
        $volume = $voting["voting.volume"]

        var prevContract string
        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        if prevContract != "@1VotingUpdate" {
            if Int($voting["status"]) != $FINISHED {
                warning LangRes("@1voting_not_expired", "en")
            }
        }

        $subject_id = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID}).One("id")
        if !$subject_id {
            warning LangRes("@1voting_subject_not_found", "en")
        }
    }

    action {
        var flag_decision i int
        flag_decision = -2 // decision can not be made (not enough participants to make a decision)
        if $voting["flags.success"] == $ACCEPT { //now enough participants to make a decision
            if $type_decision == 1 || $type_decision == 2 {
                var candidates_added_count int
                candidates_added_count = 0 // candidates who have already been added
                flag_decision = $REJECT // none of the candidates has been added yet

                var subjects array
                subjects = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID, "results": {"$gt":0}}).Order({"results":"-1"}).Columns("subject->member_id,subject->member_name")
                i = 0
                while i < Len(subjects) {
                    var subject map
                    subject = subjects[i]

                    // if there are still vacancies
                    if candidates_added_count < Int($voting["optional.role_vacancies"]) {
                        // added candidate to the role
                        var buffer_id int role map
                        buffer_id = Int($voting["optional.role_id"])
                        role = DBFind("@1roles").Columns("id,role_type,role_name,image_id").Where({"ecosystem": $ecosystem_id, "id": buffer_id}).Row()
                        if role {
                            var member assignedMember map mid rid string
                            member["member_id"] = subject["subject.member_id"]
                            mid = member["member_id"]
                            rid = role["id"]
                            assignedMember = DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "member->member_id": mid, "role->id": rid, "deleted": 0}).Row()

                            if !assignedMember {
                                member["member_name"] = subject["subject.member_name"]

                                var rolePart appointed m map
                                rolePart["id"] = role["id"]
                                rolePart["type"] = role["role_type"]
                                rolePart["name"] = role["role_name"]
                                rolePart["image_id"] = role["image_id"]
                                appointed["voting_id"] = $votingID
                                appointed["voting_name"] = $voting["voting.name"]

                                // add a candidate to the role
                                m["role"] = rolePart
                                m["member"] = member
                                m["appointed"] = appointed
                                m["date_created"] = $block_time
                                m["ecosystem"] = $ecosystem_id
                                DBInsert("@1roles_participants", m)

                                candidates_added_count = candidates_added_count + 1

                                // if at least one of the candidates was added to the role, then the voting decision is considered successful
                                flag_decision = $ACCEPT
                            }
                        }
                    }
                    i = i + 1
                }
            }

            if $type_decision == 3 {
                var subject map
                subject = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID}).Columns("id,results->percent_accepted,results->percent_rejected").Row()
                if subject {
                    var percent_accepted percent_rejected float
                    percent_accepted = Float(subject["results.percent_accepted"])
                    percent_rejected = Float(subject["results.percent_rejected"])
                    if ( Int($voting["voting.count_type_voters"]) == 0 && percent_accepted >= Float($volume)   ) ||
                       ( Int($voting["voting.count_type_voters"]) == 1 && percent_accepted >  percent_rejected ) {
                        flag_decision = $ACCEPT
                    } else {
                        flag_decision = $REJECT
                    }
                }
            }

            if $type_decision == 4 {
                var subject map
                subject = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID}).Columns("id,results->percent_accepted,results->percent_rejected,subject->contract_accept,subject->contract_reject,subject->contract_accept_params,subject->contract_reject_params").Row()
                if subject {
                    var percent_accepted percent_rejected float
                    percent_accepted = Float(subject["results.percent_accepted"])
                    percent_rejected = Float(subject["results.percent_rejected"])
                    if ( Int($voting["voting.count_type_voters"]) == 0 && percent_accepted >= Float($volume)   ) ||
                       ( Int($voting["voting.count_type_voters"]) == 1 && percent_accepted >  percent_rejected ) {
                        flag_decision = $ACCEPT
                        runContract(subject["subject.contract_accept"], subject["subject.contract_accept_params"])
                    } else {
                        flag_decision = $REJECT
                        runContract(subject["subject.contract_reject"], subject["subject.contract_reject_params"])
                    }
                }
            }

            if flag_decision == $ACCEPT {
                runContract($voting["optional.contract_accept"], $voting["optional.contract_accept_params"])
            }
            if flag_decision == $REJECT {
                runContract($voting["optional.contract_reject"], $voting["optional.contract_reject_params"])
            }

            closeNotes()
        }

        DBUpdate("@1votings", $votingID, {"flags->decision": flag_decision})
    }
}

contract VotingStatusUpdate {
    data {}

    func notificationSend(voting map) {
        var title string id i lenVoters int voters array
        if Int(voting["voting.type"]) == 3 {
            $page_name = "@1polls_view"
        } else {
            $page_name = "@1voting_view"
        }
        title = voting["voting.name"]
        id = Int(voting["id"])
        if voting["voting.type_participants"] == 5 {
            voters = DBFind("@1votings_participants").Columns("member->role_id").Limit(10000).Where({"ecosystem": $ecosystem_id, "voting_id": id, "decision": 0})
            lenVoters = Len(voters)
            while i < lenVoters {
                var voter roleId params map
                voter = voters[i]
                roleId = Int(voter["member.role_id"])
                params["voting_id"] = id
                @1NotificationsSend("rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type", roleId, 1, "fa-check", LangRes("@1voting", "en"), title, $page_name, params,1)
                i = i + 1
            }
        } else {
            voters = DBFind("@1votings_participants").Columns("member->member_id").Limit(10000).Where({"ecosystem": $ecosystem_id, "voting_id": id, "decision": 0})
            lenVoters = Len(voters)
            while i < lenVoters {
                var voter memberId params map
                voter = voters[i]
                memberId = Int(voter["member.member_id"])
                params["voting_id"] = id
                @1NotificationsSend("member_id,sender,icon_name,text_header,text_body,page_name,params_map", memberId, 1, "fa-check", LangRes("@1voting", "en"), title, $page_name, params)
                i = i + 1
            }
        }
    }

    func updateRatings(voting map) {
        if voting["voting.rating"] == 1 && voting["voting.type_participants"] != 5 {
            var id i lenVoters int voters array
            id = Int(voting["id"])
            voters = DBFind("@1votings_participants").Columns("id,member->member_id").Limit(10000).Where({"ecosystem": $ecosystem_id, "voting_id": id})
            lenVoters = Len(voters)

            while i < lenVoters {
                var voter map memberId int
                voter = voters[i]
                memberId = Int(voter["member.member_id"])

                var where rating map
                where[$rating_wallet] = IdToAddress(memberId)
                where["ecosystem"] = $ecosystem_id
                rating = DBFind($rating_table).Columns($request_columns).Where(where).Row()

                var rating_amount_i int rating_amount_f float
                rating_amount_f = Float(rating[$rating_amount]) * Float($rating_weight)
                if rating_amount_f < 1.0 {
                    rating_amount_f = 1.0
                }
                rating_amount_f = 1 + Log(rating_amount_f)
                rating_amount_i = Int(rating_amount_f * 1000)

                //Println("wallet: " + Str(IdToAddress(memberId)) + "  rating_f: " + Str(rating_amount_f) + "  rating_i: " + Str(rating_amount_i))
                DBUpdate("@1votings_participants", Int(voter["id"]), {rating: rating_amount_i})
                i = i + 1
            }
        }
    }

    func changeVotings(from, to map) {
        var votings array i lenVotings int v map
        from["deleted"] = 0
        votings = DBFind("@1votings").Columns("id,date_started,date_ended,voting->name,voting->type,voting->rating,voting->type_participants").Where(from)

        lenVotings = Len(votings)
        while i < lenVotings {
            v = votings[i]
            if to["status"] == $STARTED {
                notificationSend(v)
                updateRatings(v)
                to["flags->notifics"] = 1
            }
            DBUpdate("@1votings", Int(v["id"]), to)
            i = i + 1
        }
    }
    func checkPrev(names array) {
        var i lenNames int prev string
        lenNames = Len(names)
        prev = $stack[0]
        if Len($stack) > 2 {
            prev = $stack[Len($stack) - 2]
        }
        while i < lenNames {
            if prev == names[i] {
                return
            }
            i = i + 1
        }
        @1AdminCondition()
    }
    conditions {
        checkPrev(["@1VotingSubjectCheck", "@1VotingInvite"])

        $INVALID = 1
        $WAITING = 2
        $STARTED = 3
        $FINISHED = 4

        var app_id int
        app_id = Int(DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Basic"}).One("id"))
        $rating_table = AppParam(app_id, "voting_rating_table", $ecosystem_id)
        $rating_wallet = AppParam(app_id, "voting_rating_wallet", $ecosystem_id)
        $rating_amount = AppParam(app_id, "voting_rating_amount", $ecosystem_id)
        $rating_weight = AppParam(app_id, "voting_rating_weight", $ecosystem_id)
        $request_columns = "id," + $rating_wallet + "," + $rating_amount
    }

    action {
        //setting status INVALID
        changeVotings({status:$WAITING, "flags->full_data":0}, {"status":$INVALID})
        changeVotings({status:$WAITING, "progress->number_participants":0}, {"status":$INVALID})
        //setting status WAITING
        changeVotings({status:$INVALID, "flags->full_data":1, "progress->number_participants":{"$gt":0}}, {"status":$WAITING})
        //setting status STARTED
        changeVotings({status:$INVALID, "date_started":{"$lte":$block_time}, "flags->full_data":1, "progress->number_participants":{"$gt":0}}, {"status":$STARTED})
        changeVotings({status:$WAITING, "date_started":{"$lte":$block_time}}, {status:$STARTED})
        //setting status FINISHED
        changeVotings({status:$STARTED, "date_ended":{"$lte":$block_time}}, {"status":$FINISHED})
    }
}

contract VotingUpdate {
    data {
        votingID int
    }

    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    func percentPartOfTotal(part, total int) int {
        if total == 0{ total = 1 }
        var percent int
        percent = Int((part * 100) / total)
        if percent > 100 { percent = 100 }
        return percent
    }

    conditions {
        $INVALID = 1
        $WAITING = 2
        $STARTED = 3
        $FINISHED = 4

        $voting = DBFind("@1votings").Where({"ecosystem": $ecosystem_id, "id": $votingID, "status": $STARTED}).Columns("flags,voting,progress,progress->number_voters,voting->count_type_voters").Row()
        if !$voting {
            warning LangRes("@1started_voting_not_found", "en")
        }
    }

    action {
        var progress voting map
        progress = safeJsonToMap($voting["progress"])
        voting = safeJsonToMap($voting["voting"])

        var flag_success percent_success percent_voters number_voters quorum volume int
        // increment count of voters
        quorum = Int(voting["quorum"])
        volume = Int(voting["volume"])
        number_voters = Int(progress["number_voters"]) + 1
        if Int($voting["voting.count_type_voters"]) == 0 {
            percent_voters = percentPartOfTotal(number_voters, Int(progress["number_participants"]))
            percent_success = percentPartOfTotal(percent_voters, quorum)
        } else {
            percent_voters = percentPartOfTotal(number_voters, Int(progress["number_participants"]))
            percent_success = percentPartOfTotal(number_voters, quorum)
        }

        // update the flag of success
        if percent_success == 100 {
            flag_success = 1
        }
        progress["percent_voters"] = percent_voters
        progress["percent_success"] = percent_success
        progress["number_voters"] = number_voters

        var m map
        m["progress"] = progress
        m["flags->success"] = flag_success

        if percent_success == 100 || percent_voters == 100 {
            m["status"] = $FINISHED
            DBUpdate("@1votings", $votingID, m)
            @1VotingDecisionCheck("votingID", $votingID)
        } else {
            DBUpdate("@1votings", $votingID, m)
        }
    }
}

