    }

    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 {
            res = JSONToMap(m)
        }
        return res
    }

    conditions {
        $voting = DBFind("@1votings").Columns("id,date_ended").Where({"ecosystem": $ecosystem_id, "id": $votingID, "date_ended":[{"$gt": $block_time}]}).One("id")
        if !$voting {
            warning LangRes("@1voting_expired_can_not_notify", "en")
        }

        $voting = DBFind("@1votings").Columns("voting->name,creator->member_id,flags->notifics,flags,date_started").Where({"ecosystem": $ecosystem_id, "id": $votingID, "date_started":[{"$gt": $block_time}]}).Row()
