contract VotingCreate {
    data {
        voting_name string
        voting_type int
        type_participants int
        type_decision int

        quorum int
        rating string "optional"
        volume int "optional"

        count_type_voters string "optional"
        description string "optional"
        interval string "optional"
        start_date string "optional"
        start_time string "optional"
        end_date string "optional"
        end_time string "optional"
    }

    func trimZeroTime(s string) string {
        if Contains(s, "T00:00:00Z") {
            s = Replace(s, "T00:00:00Z", "")
        }
        return s
    }

    func dateAddTime(d, t string) string {
        var dt string
        if Size(t) == 5 {
            dt = Sprintf("%v %v:00", trimZeroTime(d), t)
        }
        return dt
    }

    func isCalledFromPage() bool {
        return $stack[0] == Sprintf("@%v%v", $ecosystem_id, $this_contract)
    }

    func fixDatetimes() {
        $date_started = UnixDateTime(dateAddTime($start_date, $start_time))
        $date_ended = UnixDateTime(dateAddTime($end_date, $end_time))

        if $date_started == 0 || $date_ended == 0 { // invalid datetimes
            if isCalledFromPage(){
                var errs array
                if Size($start_date) < 10 {
                    errs = Append(errs, "starting date")
                }
                if Size($start_time) < 5 {
                    errs = Append(errs, "starting time")
                }
                if Size($end_date) < 10 {
                    errs = Append(errs, "ending date")
                }
                if Size($end_time) < 5 {
                    errs = Append(errs, "ending time")
                }
                warning Sprintf(LangRes("@1voting_creation_error_x_unspecified", "en"), Join(errs, ", "))
            }else{
                $date_started = $block_time
                $interval = Int($interval)
                if $interval == 0 {
                    $interval = 3
                }
                $date_ended = $date_started + 3600 * 24 * $interval
            }
        }
        if $date_started < $block_time{
            warning LangRes("@1starting_date_invalid", "en")
        }
        if $date_started >= $date_ended{
            warning LangRes("@1ending_date_invalid", "en")
        }
    }

    conditions {
        $INVALID = 1
        $WAITING = 2
        $STARTED = 3
        $FINISHED = 4

        if (($type_decision == 1 || $type_decision == 2) && $count_type_voters == 1){
            info LangRes("@1voting_type_not_suitable", "en")
        }
        if ($quorum < 1) || ($quorum > 100) {
            warning Sprintf(LangRes("@1quorum_range_must_be", "en"), $quorum)
        }
        if $count_type_voters == 0 && $type_decision != 1 && $type_decision != 2 {
            if ($volume < 1) || ($volume > 100) {
                warning Sprintf(LangRes("@1volume_range_must_be", "en"), $volume)
            }
        }
        if $count_type_voters == 1 {
            if $quorum < 1 {
                info LangRes("@1voters_number_less_one", "en")
            }
            $volume = 0
        }
        if $voting_type == 2 {
            var systemAllowed map
            systemAllowed["@1VotingRunEditPlatformParam"] = 1
            systemAllowed["@1VotingRunNewValidator"] = 1
            systemAllowed["@1VotingRunRemoveValidator"] = 1
            systemAllowed["@1VotingRunVes"] = 1
            systemAllowed["@1VotingRunTokenRefund"] = 1
            systemAllowed["@1VotingRunEditEcosysParam"] = 1
            if systemAllowed[$stack[0]] != 1 {
                warning Sprintf(LangRes("@1system_voting_can_not_start_from_x", "en"), $stack[0])
            }
        }

        $key = DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": $key_id}).Row()
        if !$key {
            warning LangRes("@1ecosystem_not_member", "en")
        }
 
        fixDatetimes()
    }

    action {
        var voting progress flags creator map votingId int
        voting["name"] = $voting_name
        voting["type"] = $voting_type
        voting["description"] = $description
        voting["type_participants"] = $type_participants
        voting["type_decision"] = $type_decision
        voting["volume"] = $volume
        voting["quorum"] = $quorum
        voting["count_type_voters"] = $count_type_voters
        if $rating == "true" || $rating == "1" {
            voting["rating"] = 1
        } else {
            voting["rating"] = 0
        }

        progress["number_participants"] = "0"
        progress["number_voters"] = "0"
        progress["percent_success"] = "0"
        progress["percent_voters"] = "0"

        flags["success"] = "0"
        flags["decision"] = "0"
        flags["full_data"] = "0"
        flags["notifics"] = "0"

        creator["member_id"] = $key["id"]
        creator["member_name"] = IdToAddress($key_id)
        var m map
        m["voting"] = voting
        m["progress"] = progress
        m["flags"] = flags
        m["creator"] = creator
        m["status"] = $INVALID
        m["date_started"] = $date_started
        m["date_ended"] = $date_ended
        m["ecosystem"] = $ecosystem_id,

        votingId = DBInsert("@1votings", m)

        // if all members are voting participants
        if $type_participants == 1 {
            @1VotingInvite("votingID,var_id", votingId, 0)
        }

        var buffer_data map
        buffer_data["voting_id"] = votingId
        @1BufferManager("Action,Key,Val", "set", "voting", JSONEncode(buffer_data))

        $result = votingId
    }
}

contract VotingSubjectCheck {
    data {
        votingID int
    }

    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        $voting_map = DBFind("@1votings").Where({"ecosystem": $ecosystem_id, "id": $votingID}).Columns("id,optional,voting->type,voting->type_decision,flags").Row()
        if !$voting_map {
            warning LangRes("@1voting_not_found", "en")
        }

        $type_decision = $voting_map["voting.type_decision"]
        if $type_decision < 1 || $type_decision > 4 {
            warning LangRes("@1decision_type_invalid", "en")
        }

        $subject_map = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID}).Row()
        if !$subject_map {
            warning LangRes("@1voting_subject_not_found", "en")
        }
    }

    action {
        var flags, subject map
        flags = safeJsonToMap($voting_map["flags"])
        if $type_decision > 2 && $type_decision <= 4 {
            subject = safeJsonToMap($subject_map["subject"])
        }

        var fullData string

        if $type_decision == 1 || $type_decision == 2 {
            var optional map
            optional = safeJsonToMap($voting_map["optional"])

            if Int(optional["number_candidates"]) > 0 && Int(optional["role_vacancies"]) > 0 && Int(optional["role_id"]) > 0 {
                fullData = 1
            }
        }

        if $type_decision == 3 {
            if subject["text"] != "" && subject["hash"] != "" {
                fullData = 1
            }
        }

        if $type_decision == 4 {
            if subject["contract_accept"] != "" {
                fullData = 1
            }
        }

        if fullData == 1 {
            DBUpdate("@1votings", $votingID, {"flags->full_data":fullData})
            @1VotingStatusUpdate()
        }
    }
}

contract VotingSubjectDocument {
    data {
        votingID int
        text_document string

        contract_accept string "optional"
        contract_accept_params string "optional"
        contract_reject string "optional"
        contract_reject_params string "optional"
    }

    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("@1votings").Where({"ecosystem": $ecosystem_id, "id": $votingID, "status":[{"$neq":$STARTED},{"$neq":$FINISHED}]}).Columns("id,date_started,flags->full_data,voting->type,optional,creator->member_id").Row()
        if !$voting {
            warning LangRes("@1voting_started_can_not_edit", "en")
        }
        if $voting["creator.member_id"] != $key_id {
            warning LangRes("@1access_denied", "en")
        }

        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
            warning LangRes("@1system_voting_can_not_edit", "en")
        }
    }

    action {
        var subject map
        subject["text"] = $text_document
        subject["hash"] = Sha256($text_document)

        $subject_id = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID}).One("id")
        if $subject_id {
            DBUpdate("@1votings_subject", Int($subject_id), {subject:subject})
        } else {
            DBInsert("@1votings_subject", {ecosystem: $ecosystem_id, voting_id:$votingID, subject:subject})
        }
        var accept_params reject_params map
        accept_params = safeJsonToMap($contract_accept_params)
        reject_params = safeJsonToMap($contract_reject_params)

        var optional map
        optional = safeJsonToMap($voting["optional"])

        optional["contract_accept"] = $contract_accept
        optional["contract_accept_params"] = accept_params
        optional["contract_reject"] = $contract_reject
        optional["contract_reject_params"] = reject_params

        DBUpdate("@1votings", $votingID, {optional:optional})

        @1VotingSubjectCheck("votingID", $votingID)
    }
}

contract VotingSubjectSettings {
