        return res
    }

    conditions {
        if $vacancies < 1 {
            warning LangRes("@1vacancies_number_less_one", "en")
        }

        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("@1votings").Where({"ecosystem": $ecosystem_id, "id": $votingID, "status":[{"$neq": $STARTED},{"$neq": $FINISHED}]}).Columns("optional,voting->type,flags->full_data,creator->member_id").Row()

        if !$voting {
            warning LangRes("@1voting_started_can_not_edit", "en")
        }
        if $voting["creator.member_id"] != $key_id {
            warning LangRes("@1access_denied", "en")
        }
        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
