contract VotingDecisionReject {
    data {
        votingID int
        RoleId int
    }

    func closeNotification() {
        var role_id_search int
        role_id_search = Int(DBFind("@1notifications").Where({"ecosystem": $ecosystem_id, "recipient->role_id": $RoleId, "page_params->voting_id": $votingID, "closed": 0}).One("id"))
        if role_id_search > 0 {
            @1NotificationsClose("notific_id", role_id_search)
        } else {
            var id int
            id = Int(DBFind("@1notifications").Where({"ecosystem": $ecosystem_id, "recipient->member_id": $key_id, "page_params->voting_id": $votingID, "closed": 0}).One("id"))
            if id > 0 {
                @1NotificationsClose("notific_id", id)
            }
        }
    }

    conditions {
        $INVALID = 1
        $WAITING = 2
        $STARTED = 3
        $FINISHED = 4

        $voting = DBFind("@1votings").Where({"ecosystem": $ecosystem_id, "id": $votingID}).Columns("id,flags->decision,flags->notifics,voting->type_participants,voting->role_id,status,date_ended").Row()
        if !$voting {
            warning LangRes("@1voting_not_found", "en")
        }
        if $voting["voting.type_participants"] == 3 {
            if $voting["voting.role_id"] != $RoleId{
                warning LangRes("@1voting_role_incorrect", "en")
            }
        }
        if $voting["status"] == $FINISHED || $block_time > Int($voting["date_ended"]) {
            warning LangRes("@1voting_expired_can_not_vote", "en")
        }
        if $voting["status"] != $STARTED {
            warning LangRes("@1voting_not_started", "en")
        }
        $subject = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID}).Columns("id,number_accept,results->rating_accepted,results->rating_rejected").Row()
        if !$subject {
            warning LangRes("@1voting_subject_not_found", "en")
        }

        if Int($voting["voting.type_participants"]) != 5 {
            $participant = DBFind("@1votings_participants").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID, "member->member_id": $key_id, "decision": 0}).Columns("id,rating").Row()
        }
        if Int($voting["voting.type_participants"]) == 5 {
            $participant = DBFind("@1votings_participants").Where({"ecosystem": $ecosystem_id, "voting_id": $votingID, "member->member_id": 0, "member->role_id": $RoleId, "decision": 0}).Columns("id,rating").Row()
        }
        if !$participant {
            warning LangRes("@1voting_not_participant", "en")
        }
    }

    action {
        if Int($voting["voting.type_participants"]) == 5 {
            if !DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "member->member_id": $key_id, "role->id": $RoleId, "deleted": 0}).One("id") {
                warning LangRes("@1voting_not_participant", "en")
            } else {
                var member map
                member["member_id"] = $key_id
                member["member_name"] = IdToAddress($key_id)
                member["role_id"] = $RoleId
                var member_json string
                member_json = JSONEncode(member)
                DBUpdate("@1votings_participants", Int($participant["id"]), {"member":member_json})
            }
        }

        if $voting["flags.notifics"] == 1 {
            closeNotification()
        }

        if $voting["flags.decision"] != 1 {
            var m map
            m["decision_date"] = $block_time
            m["decision"] = -1
            DBUpdate("@1votings_participants", Int($participant["id"]), m)

            var p map
            var rating_accepted rating_rejected summ_rating int
            rating_accepted = Int($subject["results.rating_accepted"])
            rating_rejected = Int($subject["results.rating_rejected"]) + Int($participant["rating"])
            summ_rating = rating_accepted + rating_rejected
            var percent_accepted percent_rejected int
            percent_accepted = (Float(rating_accepted)*Float(100))/Float(summ_rating)
            percent_rejected = (Float(rating_rejected)*Float(100))/Float(summ_rating)
            p["results->rating_accepted"] = rating_accepted
            p["results->rating_rejected"] = rating_rejected
            p["results->percent_accepted"] = percent_accepted
            p["results->percent_rejected"] = percent_rejected
            DBUpdate("@1votings_subject", Int($subject["id"]), p)

            @1VotingUpdate("votingID", $votingID)
        }
    }
}

contract VotingValidatorAccept {
    data {
        TemplateId string
        VotingId string
    }

    func getRoleId(name string, warn bool) int {
        var rid int
        rid = Int(AppParam(Int($system_app_id), name, $ecosystem_id))
        if rid == 0 && warn {
            warning Sprintf(LangRes("@role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    conditions {
        $system_app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "System"}).One("id")
        var prev string
        prev = $stack[0]
        if Len($stack) > 2 {
            prev = $stack[Len($stack) - 2]
        }
        if prev != "@1VotingDecisionCheck" {
            warning LangRes("@1contract_start_votingdecisioncheck_only", "en")
        }

        var appId tId int
        appId = Int(DBFind("@1applications").Where({"ecosystem": 1, "name": "Basic"}).Columns("name,id").One("id"))
        tId = Int(AppParam(appId,"voting_template_validators", 1))
        if !DBFind("@1voting_templates").Where({"ecosystem": $ecosystem_id, "id": tId}).One("id"){
            warning LangRes("@1voting_template_not_found", "en")
        }
        if $TemplateId != tId {
            warning LangRes("@1template_incorrect", "en")
        }

        var oldRoleId roleId int roleName string
        oldRoleId = getRoleId("role_candidate_for_validators", false)
        roleId = getRoleId("role_validator", true)

        var validator_map map
        validator_map = DBFind("@1votings_subject").Where({"ecosystem": $ecosystem_id, "voting_id": $VotingId}).Columns("subject->contract_accept_params").One("subject->contract_accept_params")
        if !validator_map {
            warning LangRes("@1voting_subject_not_found", "en")
        }
        validator_map = JSONDecode(validator_map)
        $validator_id = Int(validator_map["ValidatorId"])
        $flag = validator_map["flag"]

        $oldRoleRowId = Int(DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "deleted": 0, "role->id": oldRoleId, "member->member_id": $validator_id}).One("id"))
        $roleRowId = Int(DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "deleted": 0, "role->id": roleId, "member->member_id": $validator_id}).One("id"))

        $roleId = Int(DBFind("@1roles").Where({"ecosystem": $ecosystem_id, "id": roleId, "deleted": 0}).One("id"))
        if $roleId == 0 {
            warning LangRes("@1validator_role_not_found", "en")
        }
    }

    action {
        if $flag == "add" {
            if $oldRoleRowId > 0 {
                @1RolesUnassign("row_id", $oldRoleRowId)
            }
            @1RolesAssign("rid,member_id", $roleId, Int($validator_id))
        }
        if $flag == "remove" {
            if $roleRowId > 0 {    
                @1RolesUnassign("row_id", $roleRowId)
            }
        }
    }
}

