contract BufferManager {
    data {
        Action string
        Key string
        Val string "optional"
    }
    func decodeArray(s string) array{
        var ar array
        if HasPrefix($buf["value"], "[") {
            ar = JSONDecode($buf["value"])
        }
        return ar
    }
    conditions {
        $emptyVal = JSONEncode("")
        $buf = DBFind("buffer_data").Where({key:$Key, member_id:$key_id}).Row()
        if !$buf {
            var buf map, id int
            id = DBInsert("buffer_data", {key:$Key, value:$emptyVal, member_id:$key_id})
            buf["id"] = Str(id)
            buf["value"] = ""
            buf["member_id"] = Str($key_id)
            buf["key"] = Str($Key)
            $buf = buf
        }
    }

    action {
        var id int
        id = Int($buf["id"])
        if $Action == "clean" {
            DBUpdate("buffer_data", id, {value:$emptyVal})
        }
        if $Action == "set" {
            DBUpdate("buffer_data", id, {value:$Val})
        }
        if $Action == "array_add_value" {
            var ar array enc string
            ar = decodeArray($buf["value"])
            ar = Append(ar, $Val)
            enc = JSONEncode(ar)
            DBUpdate("buffer_data", id, {value:enc})
        }
        if $Action == "array_remove_value" {
            var ar arNew array i int enc string
            ar = decodeArray($buf["value"])
            while i<Len(ar){
                if ar[i] != $Val{
                    arNew = Append(arNew, ar[i])
                }
                i=i+1
            }
            enc = JSONEncode(arNew)
            DBUpdate("buffer_data", id, {value:enc})
        }
    }
}

contract NotificationsBroadcast {
    data {
        Header string
        Body string
        RoleId int
    }
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("members").WhereId(kid).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            if DBFind("keys").Columns("id").WhereId(kid).Row() {
                res["member_id"] = Str(kid)
                res["member_name"] = IdToAddress(kid)
                res["image_id"] = "0"
            }
        }
        return res
    }
    conditions {
        if $role_id !=1 {
            error "The action allowed only for admin"
        }
        if $ecosystem_id!=1 {
            error "The action allowed only for admin from first ecosystem"
        }
        $ecos = DBFind("ecosystems").Ecosystem(1).Columns("id")
        $lenEcos = Len($ecos)
        if $lenEcos == 1{
            error "Not found other ecosystems"
        }
    }
    action {
        var i lenIds int eco map ids array table roleName bt string sender recipient params note map
        bt = BlockTime()
        roleName = DBFind("roles").WhereId($role_id).One("role_name")
        sender = getMemberInfo($key_id)
        sender["type"] = "1"

        note["header"] = $Header
        note["type"] = "1"
        note["icon"] = "icon-envelope"
        note["body"] = $Body

        while i<$lenEcos{
            eco = $ecos[i]
            if eco["id"] != 1{
                ids = DBFind("roles_participants").Where({"role->id":$RoleId, "role->type":3}).Columns("member->member_id")
                lenIds = Len(ids)
                table = Sprintf("@%v_notifications", eco["id"])
                var i_ids int
                while i_ids<lenIds{
                    recipient = ids[i_ids]
                    recipient = getMemberInfo(Int(recipient["member.member_id"]))
                    var m map
                    m["recipient"] = recipient
                    m["sender"] = sender
                    m["notification"] = note
                    m["page_name"] = "notifications_show"
                    m["page_params"] = params
                    m["date_created"] = bt
                    DBInsert(table, m)
                    i_ids=i_ids+1
                }
            }
            i=i+1
        }
    }
}

contract NotificationsProcess {
    data {
        notific_id int
    }
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("members").WhereId(kid).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("keys").WhereId(kid).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }
    conditions {
        $notify = DBFind("notifications").WhereId($notific_id).Columns("recipient->role_id,sender->type,date_start_processing").Row()
        if !$notify {
            warning "Notification not found"
        }

        if $notify["sender.type"] == 2{
            if $notify["date_start_processing"] != "" {
                warning "Sorry, processing of this notification has already begun"
            }
            var rid string
            rid = $notify["recipient.role_id"]
            if !DBFind("roles_participants").Where({"role->id":rid, "member->member_id":$key_id, deleted:0}).One("id") {
                warning "Sorry, you are not part of the role for which this notification is intended"
            }
        }else{
            if $notify["sender.type"] != 1 {
                warning "Notification type not found"
            }
        }
    }

    action {
        var model map
        model["processing_info"] = getMemberInfo($key_id)
        model["date_start_processing"] = BlockTime()
        DBUpdate("notifications", $notific_id, model)
    }
}

contract NotificationsClose {
    data {
        notific_id int
    }

    conditions {
        $notify = DBFind("notifications").WhereId($notific_id).Columns("id,date_start_processing,processing_info->member_id,closed,sender->type").Row()
        if !$notify {
            warning "Notification not found"
        }
        if $notify["sender.type"] == 2{
            //role has processing check
            if $notify["date_start_processing"] == "" {
                warning "Sorry, processing of this notification has not yet begun"
            }

            if $notify["processing_info.member_id"] != $key_id {
                warning "Sorry, processing of this notice began another member"
            }

            if $notify["closed"] > 0 {
                warning "Sorry, this notification has already been closed before"
            }
        }else {
            if $notify["sender.type"] != 1 { // not single
                warning "Notification type not found"
            }
        }
    }

    action {
        if $notify["sender.type"] == 1{
            NotificationsProcess("notific_id", $notific_id)
        }
        var m map
        m["date_closed"] = BlockTime()
        m["closed"] = 1
        DBUpdate("notifications", $notific_id, m)
    }
}

contract NotificationsSend {
    data {
        member_id int "optional"
        rid int "optional"
        closure_type int "optional"

        sender int
        icon_name string "optional"
        text_header string
        text_body string "optional"
        page_name string
        params_map string // json encoded expected
    }
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("members").WhereId(kid).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            if DBFind("keys").WhereId(kid).Columns("id").Row() {
                res["member_id"] = Str(kid)
                res["member_name"] = IdToAddress(kid)
                res["image_id"] = "0"
            }
        }
        return res
    }
    func sendNotify(recipient map){
        var notification map
        notification["header"] = $text_header
        notification["type"] = $notify_type
        notification["icon"] = $icon_name
        notification["body"] = $text_body
        var sender map
        if $sender == 1 && $senderMember {
            // send from member
            sender["type"] = $sender
            sender["member_id"] = $senderMember["member_id"]
            sender["member_name"] = $senderMember["member_name"]
            sender["image_id"] = $senderMember["image_id"]
        }else{
            if $sender == 2 && $senderRole{
                // send from role
                sender["type"] = $sender
                sender["role_id"] = $senderRole["id"]
                sender["role_name"] = $senderRole["role_name"]
                sender["image_id"] = $senderRole["image_id"]
            }
        }
        var model map
        model["recipient"] = recipient
        model["sender"] = sender
        model["notification"] = notification
        model["page_name"] = $page_name
        model["page_params"] = $params_map
        model["date_created"] = BlockTime()
        DBInsert("notifications", model)
    }

    func sendToRole(roleId int){
        var role recipient map
        role = DBFind("roles").WhereId(roleId).Row()
        recipient["role_id"] = role["id"]
        recipient["role_name"] = role["role_name"]
        recipient["image_id"] = role["image_id"]
        sendNotify(recipient)
    }

    conditions {
        $member_id = Int($member_id)
        $rid = Int($rid)
        if Size($icon_name) < 1{
            $icon_name = "icon-envelope"
        }
        if $member_id != 0 {
            if !DBFind("keys").WhereId($member_id).Row(){
                warning "Key not found"
            }
            $notify_type = 1 // is "member"
        }else{
            if $rid > 0{
                $notify_type = 2 // is "role"
                if !DBFind("roles").WhereId($rid).One("id") {
                    warning $this_contract+": Recipient role not found"
                }
                if !DBFind("roles_participants").Where({"role->id":$rid, deleted:0}).One("id") {
                    warning $this_contract+": Recipient role does not contain members"
                }
            } else{
                warning $this_contract+": Notification type not defined"
            }
        }
        $senderMember = nil
        $senderRole = nil
        if $sender == 1 {
            $senderMember = getMemberInfo($key_id)
        }else{
            if $sender == 2 {
                $senderRole = DBFind("roles").WhereId($role_id).Row()
                if !$senderRole {
                    warning $this_contract+": Sender role not found"
                }
            }else{
                warning $this_contract+": Sender invalid"
            }
        }
    }
    action {
        if $member_id != 0 {
            sendNotify(getMemberInfo($member_id))
        }else{
            if $rid > 0 {
                if $closure_type == 1 {
                    // Single closure: one begins the process - the others do not see
                    sendToRole($rid)
                } else {
                    // Multiple closure: to each his own notification
                    $notify_type = 1
                    var participants array
                    participants = DBFind("roles_participants").Where({"role->id":$rid, deleted:0}).Columns("member->member_id")
                    var i int
                    while i < Len(participants) {
                        var p map
                        p = participants[i]
                        sendNotify(getMemberInfo(Int(p["member.member_id"])))
                        i=i+1
                    }
                }
            }
        }
    }
}

