contract RolesAccessCheck {
    data {
        rid int
    }
    conditions {
        var role map
        role = DBFind("roles").WhereId($rid).Columns("id,role_name,deleted,roles_access->rids,role_type,creator->member_id,company_id").Row()
        if !role {
            warning Sprintf("%v: Role not found", $this_contract)
        }

        if role["creator.member_id"] == $key_id {
            return
        }

        var rids rawRids array, i int
        rawRids = Split(role["roles_access.rids"], ",")
        while i < Len(rawRids){
            if rawRids[i] > 0 {
                rids = Append(rids, rawRids[i])
            }
            i=i+1
        }
        i=0
        while i<Len(rids){
            if rids[i] == $role_id {
                return
            }
            i=i+1
        }

        if role["role_type"] == 4 {
            // company role
            if role["company_id"] == $key_id {
                return
            }else{
                warning "Sorry, you are not the creator of this company"
            }
        }
        warning Sprintf("Sorry, you don't have permission to change this role (%q)", role["role_name"])
    }
}

contract RolesAccessManager {
    data {
        Action string
        Rid int
        ManagerRid string "optional"
    }
    func updateAccess(ra string){
        var model map
        model["roles_access"] = {rids:ra}
        DBUpdate("roles", $Rid, model)
    }
	func updateBufferAccess(ra array){
        var roles_access string
        roles_access = JSONEncode(ra)
        if !$buffer_id {
            DBInsert("buffer_data", {"member_id": $key_id, "key": "roles_access", "value": roles_access})
        } else {
            DBUpdate("buffer_data", Int($buffer_id), {"value": roles_access})
        }
    }
    conditions {
		$ManagerRid = Int($ManagerRid)
        if $Rid > 0 {
			RolesAccessCheck("rid", $Rid)

			$role = DBFind("roles").WhereId($Rid).Columns("id,roles_access->rids").Row()
			if !$role {
				warning "Role not found"
			}
			if $ManagerRid > 0{
				if !DBFind("roles").WhereId($ManagerRid).One("id") {
					warning "Role not found"
				}
			}
			var ids array, i int
			$ids = ids
			ids = Split($role["roles_access.rids"],",")
			while i < Len(ids){
				if ids[i] > 0 {
					$ids = Append($ids, ids[i])
				}
				i=i+1
			}
		} else {
            $buffer_managers = 0
            $buffer_id = 0
            $ids = []
            var buffer map
            buffer = DBFind("buffer_data").Where({"member_id": $key_id, "key": "roles_access"}).Row()
            if buffer {
                $buffer_managers = buffer["value"]
                $buffer_id = buffer["id"]
            }
        }
    }

    action {
		if $Rid == 0 {
            if $buffer_managers {
                $ids = JSONDecode($buffer_managers)
            }
        }
        if $Action == "clean"{
            updateAccess("")
        }
        if $ManagerRid > 0{
            var i int, idsNew array
            if $Action == "remove"{
                while i<Len($ids){
                    if $ids[i] != $ManagerRid{
                        idsNew = Append(idsNew, $ids[i])
                    }
                    i=i+1
                }
				if $Rid == 0 {
					updateBufferAccess(idsNew)
				}else{
					updateAccess(Join(idsNew, ","))
				}
            }
            if $Action == "add"{
                var double bool
                while i<Len($ids){
                    if $ids[i] == $ManagerRid{
                        double = true
                    }
                    i=i+1
                }
                if !double {
                    $ids = Append($ids, $ManagerRid)
                }
                if $Rid == 0 {
					updateBufferAccess($ids)
				}else{
					updateAccess(Join($ids, ","))
				}
            }
        }
    }
}

contract RolesAssign {
    data {
        rid int
        member_id int
    }
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("members").WhereId(kid).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("keys").WhereId(kid).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }
    conditions {
        $role = DBFind("roles").WhereId($rid).Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Row()
        if (Int($role["role_type"]) == 2){
            warning "Elective role can be assigned only during voting"
        }
        if !$role {
            warning "Role not found"
        }

        if !DBFind("keys").WhereId($member_id).Row() {
            warning "Key not found"
        }

        if $role["deleted"] == 1 {
            warning "This role has been deleted. The appointment of members is impossible"
        }

        var specialRules map prev_contract string stack array
        stack = JSONDecode(JSONEncode($stack))
        prev_contract = stack[0]
        if Len(stack)>2{
            prev_contract = stack[Len(stack)-2]
        }
        specialRules["@1VotingValidatorAccept"] = "Validator"
        specialRules["@1DelegateRefresh"] = "Delegate"
        specialRules["@1CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["@1VotingTemplateRun"] = "Investor with voting rights"
        specialRules["@1VotingTokensRefundCreate"] = "Investor with voting rights"
        if specialRules[prev_contract] != $role["role_name"] {
            RolesAccessCheck("rid", $rid)
        }

        $assigned = DBFind("roles_participants").Where({"role->id":$rid, "member->member_id":$member_id, deleted:0}).One("id")
    }

    action {
        if $assigned {
            // do not re-assign and do not warning because this contract may be called for other
            return
        }
        var role member appointer map
        role["id"] = $role["id"]
        role["type"] = $role["role_type"]
        role["name"] = $role["role_name"]
        role["image_id"] = $role["image_id"]

        member = getMemberInfo($member_id)
        appointer = getMemberInfo($key_id)
        var m map
        m["role"] = role
        m["member"] = member
        m["appointed"] = appointer
        m["date_created"] = BlockTime()
        DBInsert("roles_participants", m)
    }
}

