contract VotingSubjectSettings {
    data {
        votingID int

        roleID int
        vacancies int

        contract_accept string "optional"
        contract_accept_params string "optional"
        contract_reject string "optional"
        contract_reject_params string "optional"
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        if $vacancies < 1 {
            warning "Number of vacancies can not be less than 1"
        }

        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("votings").Where({id:$votingID, status:[{"$neq":$STARTED},{"$neq":$FINISHED}]}).Columns("optional,voting->type,flags->full_data").Row()

        if !$voting {
            warning "Voting already started. Can not edit document"
        }

        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
            warning "This vote is a system. Change settings not allowed"
        }
        var role map
        role = DBFind("roles").Where({id:$roleID, deleted:0}).Row()
        if !role {
            warning "Specified role not found"
        }

        // roles_types: 1. assigned_role, 2. elective_role, 3. system_role, 4. company_role
        if !(role["role_type"] == 2 || role["role_type"] == 3) {
            warning "The chosen role is not elective or has been removed"
        }
    }

    action {
        var optional map
        optional = safeJsonToMap($voting["optional"])

        optional["contract_accept"] = $contract_accept
        optional["contract_accept_params"] = safeJsonToMap($contract_accept_params)
        optional["contract_reject"] = $contract_reject
        optional["contract_reject_params"] = safeJsonToMap($contract_reject_params)
        optional["role_id"] = $roleID
        optional["role_vacancies"] = $vacancies

        DBUpdate("votings", $votingID, {optional:optional})

        VotingSubjectCheck("votingID", $votingID)
    }
}

contract VotingTemplateCreate{
    data{
        Title string
        TypeParticipants int
        TypeVoting int
        TypeDecision int
        Volume int
        Quorum int
        Subject string
        Voters string "optional" // role of participant
        Candidates string "optional" // role of candidate
        InitContract string "optional" // Execute before the vote
        Vacancies int "optional"
        ContractAccept string "optional"
        ParamsAccept string "optional"
        ContractReject string "optional"
        ParamsReject string "optional"
    }

    conditions{
        if $TypeParticipants == 3 {
            if Size($Voters) == 0 || $Voters == "NULL" || $Voters == 0 {
                warning "Voters role not found"
            }
        }
        if Size($Title) <3 {
            warning "Please enter title"
        }
        if $Volume <0 || $Volume > 100 {
            warning "Wrong volume range"
        }
        if $Quorum <0 || $Quorum > 100 {
            warning "Wrong quorum range"
        }

        if $TypeDecision == 1 || $TypeDecision == 2 {
            if $Vacancies < 1 {
                warning "Wrong number vacancies"
            }
            if $Candidates == "NULL" || $Candidates == 0 || Size($Candidates) == 0 {
                warning "Wrong candidate role"
            }
        }

        if Size($InitContract) > 0{
            if GetContractByName($InitContract) == 0 {
                warning "Init Contract not found"
            }
        }
        if Size($ContractAccept) > 0{
            if GetContractByName($ContractAccept) == 0 {
                warning "Contract for accepted decision not found"
            }
        }
        if Size($ContractReject) > 0{
            if GetContractByName($ContractReject) == 0{
                warning "Contract for rejected decision not found"
            }
        }
    }

    action{
        var m map
        m["title"] = $Title
        m["subject"] = $Subject
        m["voters"] = $Voters
        m["candidates"] = $Candidates
        m["init_contract"] = $InitContract
        m["volume"] = $Volume
        m["quorum"] = $Quorum
        m["type_voting"] = $TypeVoting
        m["type_participants"] = $TypeParticipants
        m["type_decision"] = $TypeDecision
        m["vacancies"] = $Vacancies
        m["contract_accept"] = $ContractAccept
        m["accept_params"] = $ParamsAccept
        m["contract_reject"] = $ContractReject
        m["reject_params"] = $ParamsReject
        $result = DBInsert("voting_templates", m)
    }
}

contract VotingTemplateRun{
    data{
        TemplateId int
        Duration int

        KeyId int "optional"
        TableWritten string "optional"
        RowId string "optional"
        ColumnName string "optional"
        NewValue string "optional"
        TextDocument string "optional"
    }
    func checkInput(value, name string){
        if Size(value) == 0 || value == "NULL" {
            warning Sprintf("%q not found", name)
        }
    }
    func emptyOrValue(value string) string{
        if value == "NULL" {
            value = ""
        }
        return value
    }

    conditions{
        var template map
        template = DBFind("voting_templates").WhereId($TemplateId).Row()
        if !template{
            warning "Voting template not found"
        }
        if $KeyId != 0{
            $keyFind = DBFind("keys").WhereId($KeyId).Row()
            if !$keyFind{
                warning "Key id not found"
            }
        }
        if $Duration <= 0{
            $Duration = 3
        }

        var initContract string
        initContract = template["init_contract"]
        $voters = template["voters"]
        $candidates = template["candidates"]
        $votingName = Sprintf("%v [template id:%v]", template["title"], $TemplateId)
        $volume = Int(template["volume"])
        $quorum = Int(template["quorum"])
        $type_voting = Int(template["type_voting"])
        $participants = Int(template["type_participants"])
        $decision = Int(template["type_decision"])
        $vacancies = Int(template["vacancies"])

        if initContract != "" && initContract != "NULL"{
            var initParam map
            if $decision == 4 && $type_voting == 2 {
                // voting for new value of sysparam
                initParam["Name"] = $ColumnName
                initParam["Value"] = $NewValue
            }else{
                initParam["KeyId"] = $KeyId
            }
            CallContract(initContract, initParam)
        }

        $desc = Sprintf("template #%v", $TemplateId)
        if $type_voting < 1 || $type_voting > 2 {
            warning "Invalid type of voting"
        }
        $contractAccept = emptyOrValue(template["contract_accept"])
        $acceptParams = emptyOrValue(template["accept_params"])
        $contractReject = emptyOrValue(template["contract_reject"])
        $rejectParams = emptyOrValue(template["reject_params"])

        if $decision == 1 || $decision == 2 {
            // role-candidates
            checkInput($candidates, "Candidate role")
            var candidate map
            candidate = DBFind("roles").Where({id:$candidates, deleted:0}).Row()
            if !candidate {
                warning Sprintf("Candidates role (id: %v) not found", $candidates)
            }
            $candidateId = Int(candidate["id"])
        }

        if $decision == 3 {
            // document approval
            checkInput($TextDocument, "TextDocument")
        }

        if $decision == 4 {
            // formal decision
            // in the system vote need only a field and a value
            checkInput($ColumnName, "ColumnName")
            checkInput($NewValue, "NewValue")
            if $type_voting == 2{
                $desc = Sprintf("Parameter: %v, Value: %v", $ColumnName, $NewValue)
            }

            if $type_voting == 1{
                checkInput($TableWritten, "TableWritten")
                checkInput($RowId, "RowId")
                $desc = Sprintf("Table: %v, Column: %v, ID: %v, Value: %v", $TableWritten, $ColumnName, $RowId, $NewValue)
            }
            if $RowId == "" || $RowId == "NULL"{
                $RowId = 0
            }

        }
        // check duplicated
        if DBFind("votings").Where({"voting->name":$votingName, "flags->decision":{"$nin":["1", "-1"]}, deleted:0}).Row(){
            error "Same voting already created"
        }


        if $participants == 3 {
            // if the type of the participants "in role" - check the role
            var role map
            role = DBFind("roles").Where({id:$voters, deleted:0}).Row()
            if !role {
                warning Sprintf("Voters role (id: %v) not found", $voters)
            }
            $roleId = Int(role["id"])
        }
    }

    action{
        var votingId int
        votingId = VotingCreate("voting_name,voting_type,description,type_participants,type_decision,volume,quorum,interval", $votingName, $type_voting, $desc, $participants, $decision, $volume, $quorum, $Duration)

        if votingId == 0 {
            warning "votingId is zero"
        }
        // the default argument for Contract
        var defaultParams string
        defaultParams = Sprintf(`{"VotingTemplateId":"%v","KeyId":"%v","VotingId":"%v"}`, $TemplateId, $KeyId, votingId)

        if Size($acceptParams) == 0{
            $acceptParams = defaultParams
        }
        if Size($rejectParams) == 0{
            $rejectParams = defaultParams
        }

        if $decision == 1 || $decision == 2{
            // 1. Role candidates with the registration of participants on request
            // 2. Role candidates with manual registration of the participants
            VotingSubjectCandidates("votingID,memberID", votingId, $KeyId)
            VotingSubjectSettings("votingID,roleID,vacancies,contract_accept,contract_accept_params,contract_reject,contract_reject_params", votingId, $candidateId, $vacancies, $contractAccept, $acceptParams, $contractReject,$rejectParams)
        }
        if $decision == 3 {
            // Document approval
            VotingSubjectDocument("votingID,text_document,contract_accept,contract_accept_params,contract_reject,contract_reject_params", votingId,$TextDocument, $contractAccept, $acceptParams, $contractReject, $rejectParams)
        }

        if $decision == 4 {
            // Formal decision
            VotingSubjectFormal("votingID,description,table_to_written,row_id,column_name,written_value,contract_accept,contract_accept_params,contract_reject,contract_reject_params",votingId, $desc, $TableWritten, $RowId, $ColumnName, $NewValue, $contractAccept, $acceptParams, $contractReject, $rejectParams)

        }

        if $decision == 5 {
            // Execution of the contract
            VotingSubjectContract("votingID,contract_accept,contract_accept_params,contract_reject,contract_reject_params",votingId,$contractAccept, $acceptParams, $contractReject, $rejectParams)
        }
        if $participants == 3 {
            // participants: "by role"
            VotingInvite("votingID,var_id", votingId, $roleId)
        }
        if $participants == 1 {
            // if the participants are "all", the invite has already occurred in "VotingCreate"
        }
    }
}

contract VotingDecisionReject {
    data {
        votingID int
    }
    func closeNotification(){
        var id int
        id = Int(DBFind("notifications").Where({"recipient->member_id":$key_id, page_name:"voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
        if id > 0 {
            NotificationsClose("notific_id", id)
        }
    }
    conditions {
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
        $voting = DBFind("votings").WhereId($votingID).Columns("voting->type_participants,voting->role_id,flags->decision,flags->notifics,status").Row()
        if !$voting {
            warning "Voting is not found"
        }
        if $voting["voting.type_participants"] == 3{
            if $voting["voting.role_id"] != $role_id{
                warning "Incorrect role for voting"
            }
        }
        if $voting["status"] == $FINISHED {
            warning "Voting has expired. Voting is now not possible"
        }
        if $voting["status"] != $STARTED {
            warning "Voting has not yet begun. Try again later, please"
        }
        $participant_id = DBFind("votings_participants").Where({voting_id:$votingID, "member->member_id":$key_id}).One("id")
        if !$participant_id {
            warning "You are not a participant in this voting"
        }
    }

    action {
        if $voting["flags.notifics"] == 1 {
            closeNotification()
        }
        if $voting["flags.decision"] != 1 {
            var bt string
            bt = BlockTime()

            DBUpdate("votings_participants", Int($participant_id), {decision_date:bt, decision:"-1"})
            VotingUpdate("votingID", $votingID)
        }
    }
}
