contract TokensLockoutMember {
    data {
        KeyId int
        Value int
    }
    func getPermission() {
        var array_permissions array result i int prevContract string
        array_permissions = ["@1VotingTokensRefundCreate", "@1TokensRefundAccept", "@1TokensRefundReject", "@1BlockMember"]

        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        while i < Len(array_permissions) {
            var contract_name string
            contract_name = array_permissions[i]
            if contract_name==prevContract {
                result = 1
            }
            i = i + 1
        }

        if result == 0 {
            error LangRes("@1contract_chain_distorted", "en")
        }
    }
    
    conditions {
        getPermission()
        
        if !DBFind("@1keys").Where({ecosystem:1, id:$KeyId}).One("id"){
            error LangRes("@1key_not_found", "en")
        }

        if Int($Value) < 0 || Int($Value) > 1 {
            error LangRes("@1value_between_0_and_1", "en")      
        }
    }

    action {
        DBUpdate("@1keys", $KeyId, {"blocked": $Value})
    }
}

contract BufferManager {
    data {
        Action string
        Key string
        Val string "optional"
    }
    
    func decodeArray(s string) array {
        var ar array
        if HasPrefix($buf["value"], "[") {
            ar = JSONDecode($buf["value"])
        }
        return ar
    }
    
    action {
        $emptyVal = JSONEncode("")
        $buf = DBFind("@1buffer_data").Where({ecosystem:$ecosystem_id, key:$Key, member_id:$key_id}).Row()
        if !$buf {
            var buf map, id int
            id = DBInsert("@1buffer_data", {ecosystem:$ecosystem_id, key:$Key, value:$emptyVal, member_id:$key_id})
            buf["id"] = Str(id)
            buf["value"] = ""
            buf["member_id"] = Str($key_id)
            buf["key"] = Str($Key)
            $buf = buf
        }
        var id int
        id = Int($buf["id"])
        if $Action == "clean" {
            DBUpdate("@1buffer_data", id, {value:$emptyVal})
        }
        if $Action == "set" {
            DBUpdate("@1buffer_data", id, {value:$Val})
        }
        if $Action == "array_add_value" {
            var ar array enc string
            ar = decodeArray($buf["value"])
            ar = Append(ar, $Val)
            enc = JSONEncode(ar)
            DBUpdate("@1buffer_data", id, {value:enc})
        }
        if $Action == "array_remove_value" {
            var ar arNew array i int enc string
            ar = decodeArray($buf["value"])
            while i < Len(ar){
                if ar[i] != $Val{
                    arNew = Append(arNew, ar[i])
                }
                i = i + 1
            }
            enc = JSONEncode(arNew)
            DBUpdate("@1buffer_data", id, {value:enc})
        }
    }
}

contract RolesAccessCheck {
    data {
        rid int
    }

    conditions {
        var role map
        role = DBFind("@1roles").Where({ecosystem:$ecosystem_id, id:$rid}).Columns("id,role_name,deleted,roles_access,role_type,creator->member_id").Row()
        if !role {
            warning Sprintf("%v: " + LangRes("@1role_not_found", "en"), $this_contract)
        }

        if role["creator.member_id"] == $key_id {
            return
        }

        var rids array
        rids = JSONDecode(role["roles_access"])
        if !DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":{"$in":rids}, "member->member_id":$key_id, deleted:0}).Row() {
        // if !RoleAccess(...rids){
            warning Sprintf(LangRes("@1role_change_no_permission", "en"), role["role_name"])
        }
    }
}

contract RolesUnassign {
    data {
        row_id int
    }

    conditions {
        var assigned map roleId int
        assigned = DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, id:$row_id, deleted:0 }).Columns("id,role->id").Row()
        if !assigned {
            warning LangRes("@1role_participant_not_found", "en")
        }
        var buffer_id int
        buffer_id = Int(assigned["role.id"])
        roleId = Int(DBFind("@1roles").Where({ecosystem:$ecosystem_id, id:buffer_id}).One("id"))
        if roleId == 0 {
            warning LangRes("@1role_not_found_in_registry", "en")
        }

        var specialRules map prev_contract string
        prev_contract = $stack[0]
        if Len($stack) > 2{
            prev_contract = $stack[Len($stack) - 2]
        }
        specialRules["@1VotingValidatorAccept"] = 1
        specialRules["@1DelegateRefresh"] = 1
        specialRules["@1CandidateValidatorRequest"] = 1
        specialRules["@1InvestorRefresh"] = 1
        specialRules["@1VotingTokensRefundCreate"] = 1
        specialRules["@1sysparams_ValidatorResign"] = 1

        if specialRules[prev_contract] != 1 {
            @1RolesAccessCheck("rid", roleId)
        }
    }

    action {
        // delete the member
        var m map
        m["deleted"] = 1
        m["date_deleted"] = BlockTime()
        DBUpdate("@1roles_participants", $row_id, m)
    }
}

contract RolesAssign {
    data {
        rid int
        member_id int
    }

    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("@1members").Where({ecosystem:$ecosystem_id, id:kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:kid}).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }

    conditions {
        $role = DBFind("@1roles").Where({id:$rid}).Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Row()
        if (Int($role["role_type"]) == 2){
            warning LangRes("@1elective_role_voting_only", "en")
        }
        if !$role {
            warning LangRes("@1role_not_found", "en")
        }

        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$member_id}).Row() {
            warning LangRes("@1key_not_found", "en")
        }

        if $role["deleted"] == 1 {
            warning LangRes("@1role_deleted_request_impossible", "en")
        }

        var specRules map prev_contract string
        prev_contract = $stack[0]
        if Len($stack) > 2{
            prev_contract = $stack[Len($stack) - 2]
        }
        specRules["@1VotingValidatorAccept"] = 1
        specRules["@1DelegateRefresh"] = 1
        specRules["@1CandidateValidatorRequest"] = 1
        specRules["@1VotingTemplateRun"] = 1
        specRules["@1VotingTokensRefundCreate"] = 1
        specRules["@1VotingValidatorCreate"] = 1
        specRules["@1VotingParamCreate"] = 1
        if specRules[prev_contract] != 1 {
            @1RolesAccessCheck("rid", $rid)
        }
    }

    action {
        if DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":$rid, "member->member_id":$member_id, deleted:0}).One("id") {
            // do not re-assign and do not warning because this contract may be called for other
            return
        }
        var role member appointer map
        role["id"] = $role["id"]
        role["type"] = $role["role_type"]
        role["name"] = $role["role_name"]
        role["image_id"] = $role["image_id"]

        member = getMemberInfo($member_id)
        appointer = getMemberInfo($key_id)
        var m map
        m["role"] = role
        m["member"] = member
        m["appointed"] = appointer
        m["date_created"] = BlockTime()
        m["ecosystem"] = $ecosystem_id
        DBInsert("@1roles_participants", m)
    }
}

