    data {
        KeyId int
    }
    func getPermission() {
        var array_permissions array result i int prevContract string
        array_permissions = ["@1DeleteMember"]

        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        while i < Len(array_permissions) {
            var contract_name string
            contract_name = array_permissions[i]
            if contract_name==prevContract {
                result = 1
            }
            i = i + 1
        }

        if result == 0 {
            error LangRes("@1contract_chain_distorted", "en")
        }
    }

    conditions {
        ContractConditions("@1AdminCondition")

        getPermission()
        
        if !DBFind("@1keys").Where({ecosystem:1, id:$KeyId}).One("id"){
            error LangRes("@1key_not_found", "en")
        }
    }

    action {
        DBUpdate("@1keys", $KeyId, {"deleted": 1})
    }
}

contract CandidateValidatorRequest {
    data {
        HostIp string
        // CommissionPub string
        CommissionAddress string
        Description string "optional"
        Comment string "optional"
    }

    func isNotValidIp(ip string) bool {
        var parts array
        var i, lenParts, part int
        parts = Split(ip, ".")

        lenParts = Len(parts)
        if lenParts != 4{
            return true
        }
        while i < lenParts {
            part = Int(parts[i])
            if part < 0{
                return true
            }
            if part > 255{
                return true
            }
            i = i + 1
        }

        return false
    }

    conditions {
        if $ecosystem_id != 1 {
            warning LangRes("@1contract_first_ecosystem_only", "en")
        }
        $CommissionAddress = AddressToId($CommissionAddress)
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$CommissionAddress}).Row(){
            warning LangRes("@1address_invalid", "en")
        }

        if isNotValidIp($HostIp){
            warning LangRes("@1enter_valid_ip", "en")
        }
    }

    action {
        var roleParam string roleId int
        roleParam = "role_candidate_for_validators"
        roleId = Int(EcosysParam(roleParam))

        if !DBFind("@1roles").Where({ecosystem:1, id:roleId, deleted:0}).One("id"){
            warning Sprintf(LangRes("@1role_app_param_not_found", "en"), roleParam)
        }
        
        @1RolesAssign("rid,member_id", roleId, $key_id)
        //no $ecosystem_id
        var m map
        m["host_ip"] = $HostIp
        m["commission_pub"] = ""
        m["commission_address"] = $CommissionAddress
        m["description"] = $Description
        m["comment"] = $Comment
        m["candidate_key_id"] = $key_id
        m["date_created"] = BlockTime()
        m["deleted"] = 0
        DBInsert("@1sysparams_candidate", m)
    }
}

contract MembershipAdd {
    data {
        memberskeys string
    }

    func addPub(pub_key string) {
        if Size(pub_key) < 130 {
            error LangRes("@1key_too_short", "en")
        }
        if Size(pub_key) > 130 {
            error LangRes("@1key_too_long", "en")
        }

        var k_id int
        k_id = PubToID(pub_key)
        if k_id == 0 {
            warning LangRes("@1public_key_invalid", "en")
        }
        $check_user = DBFind("@1keys").Where({id:k_id, ecosystem:$ecosystem_id})
        if $check_user {
            error LangRes("@1key_already_exists", "en")
        }

        DBInsert("@1keys",{id:k_id, pub:pub_key, ecosystem:$ecosystem_id})
    }

    conditions {
        if $memberskeys == "" {
            warning LangRes("@1public_key_empty", "en")
        }
        $memberskeys = Replace($memberskeys, " ", "")
        $memberskeys = Replace($memberskeys, "\n", "")
        $memberskeys = Replace($memberskeys, "\r", "")

        var admin_id int
        admin_id = EcosysParam("role_admin")
       
        if !RoleAccess(Int(admin_id)) {
            warning LangRes("@1access_denied", "en")
        }

        if ($ecosystem_id == 1) {
            error LangRes("@1impossible_for_platform_ecosystem", "en")
        }
    }

    action {
        if Contains($memberskeys, ",") {
            var pub_array array
            pub_array = Split($memberskeys, ",")

            var i int
            while i < Len(pub_array) {
                var pub_key string
                pub_key = Str(pub_array[i])
                addPub(pub_key)
                i = i + 1
            }
        } else {
            addPub($memberskeys)
        }
    }
}

contract NotificationsProcess {
    data {
        notific_id int
    }
    
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("@1members").Where({ecosystem:$ecosystem_id, id:kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:kid}).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }
    
    conditions {
        $notify = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, id:$notific_id}).Columns("recipient->role_id,sender->type,date_start_processing").Row()
        if !$notify {
            warning LangRes("@1notification_not_found", "en")
        }

        if $notify["sender.type"] == 2{
            if $notify["date_start_processing"] != "" {
                warning LangRes("@1notification_processing_started_already", "en")
            }
            var rid string
            rid = Int($notify["recipient.role_id"])
            if rid > 0{
                if !DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":rid, "member->member_id":$key_id, deleted:0}).One("id") {
                    warning LangRes("@1notification_not_part_of_role", "en")
                }
            }
        }else{
            if $notify["sender.type"] != 1 {
                warning LangRes("@1notification_type_not_found", "en")
            }
        }
    }

    action {
        var model map
        model["processing_info"] = getMemberInfo($key_id)
        model["date_start_processing"] = BlockTime()
        DBUpdate("@1notifications", $notific_id, model)
    }
}

contract NotificationsSend {
    data {
        member_id int "optional"
        rid int "optional"
        closure_type int "optional"

        sender int
        icon_name string "optional"
        text_header string
        text_body string "optional"
        page_name string
        params_map string // json encoded expected
        current_role_id string "optional"
        eco_id string "optional"
        popup string "optional"
    }

    func getMemberInfo(kid, ecoId int) map {
        var res member map
        member = DBFind("@1members").Where({ecosystem:ecoId, id:kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            if DBFind("@1keys").Where({ecosystem:ecoId, id:kid}).Columns("id").Row() {
                res["member_id"] = Str(kid)
                res["member_name"] = IdToAddress(kid)
                res["image_id"] = "0"
            }
        }
        return res
    }

    func sendNotify(recipient map) {
        var notification map
        notification["header"] = $text_header
        notification["type"] = $notify_type
        notification["icon"] = $icon_name
        notification["body"] = $text_body
        if $popup == "true" || Int($popup) == 1 {
            notification["popup"] = 1
        }else{
            notification["popup"] = 0
        }
        var sender map
        if $sender == 1 && $senderMember {
            // send from member
            sender["type"] = $sender
            sender["member_id"] = $senderMember["member_id"]
            sender["member_name"] = $senderMember["member_name"]
            sender["image_id"] = $senderMember["image_id"]
        }else{
            if $sender == 2 && $senderRole{
                // send from role
                sender["type"] = $sender
                sender["role_id"] = $senderRole["id"]
                sender["role_name"] = $senderRole["role_name"]
                sender["image_id"] = $senderRole["image_id"]
            }
        }
        var model map
        model["recipient"] = recipient
        model["sender"] = sender
        model["notification"] = notification
        model["page_name"] = $page_name
        model["page_params"] = $params_map
        model["date_created"] = BlockTime()
        model["ecosystem"] = $eco_id
        DBInsert("@1notifications", model)
    }

    func sendToRole(roleId, ecoId int) {
        var role recipient map
        role = DBFind("@1roles").Where({ecosystem:ecoId, id:roleId}).Row()
        recipient["role_id"] = role["id"]
        recipient["role_name"] = role["role_name"]
        recipient["image_id"] = role["image_id"]
        sendNotify(recipient)
    }

    conditions {
        $member_id = Int($member_id)
        $rid = Int($rid)
        $current_role_id = Int($current_role_id)
        $eco_id = Int($eco_id)
        if $eco_id == 0{
            $eco_id = $ecosystem_id
        }
        if Size($icon_name) < 1{
            $icon_name = "icon-envelope"
        }
        if $member_id != 0 {
            if !DBFind("@1keys").Where({ecosystem:$eco_id, id:$member_id}).Row(){
                warning LangRes("@1key_not_found", "en")
            }
            $notify_type = 1 // is "member"
        }elif $rid > 0{
            $notify_type = 2 // is "role"
            if !DBFind("@1roles").Where({ecosystem:$eco_id, id:$rid}).One("id") {
                warning $this_contract + ": " + LangRes("@1recipient_role_not_found", "en")
            }
            if !DBFind("@1roles_participants").Where({ecosystem:$eco_id, "role->id":$rid, deleted:0}).One("id") {
                warning $this_contract + ": " + LangRes("@1recipient_role_no_members", "en")
            }
        } else{
            warning $this_contract + ": " + LangRes("@1notification_type_not_defined", "en")
        }
        $senderMember = nil
        $senderRole = nil
        if $sender == 1 {
            $senderMember = getMemberInfo($key_id, $ecosystem_id)
        }elif $sender == 2 {
            $senderRole = DBFind("@1roles").Where({ecosystem:$ecosystem_id, id:$current_role_id}).Row()
            if !$senderRole {
                warning $this_contract + ": " + LangRes("@1sender_role_not_found", "en")
            }
        }else{
            warning $this_contract + ": " + LangRes("@1sender_invalid", "en")
        }
    }

    action {
        if $member_id != 0 {
            sendNotify(getMemberInfo($member_id, $eco_id))
            UpdateNotifications($eco_id, Int($member_id))
        }elif $rid > 0 {
            if $closure_type == 1 {
                // Single closure: one begins the process - the others do not see
                sendToRole($rid, $eco_id)
                UpdateRolesNotifications($eco_id, Int($rid))
            } else {
                // Multiple closure: to each his own notification
                $notify_type = 1
                var participants array
                participants = DBFind("@1roles_participants").Where({ecosystem:$eco_id, "role->id":$rid, deleted:0}).Columns("member->member_id")
                var i int
                while i < Len(participants) {
                    var p map
                    p = participants[i]
                    sendNotify(getMemberInfo(Int(p["member.member_id"]), $eco_id))
                    UpdateNotifications($eco_id, Int(p["member.member_id"]))
                    i = i + 1
                }
            }
        }
    }
}

contract MetricsUpdate {
    data {
        Interval string
        Aggregate string
    }
    
    func mapping(items array) map {
        var mapout map, i int
        while i < Len(items){
            var k v string, item map
            item = items[i]
            k = item["key"]
            v = item["value"]
            mapout[k] = v
            i = i + 1
        }
        return mapout
    }
    
    conditions {
        if $Interval <= 0 {
            $Interval = 1
        }
        var current_year int
        current_year = Int(Substr(BlockTime(), 0, 4))
        if $Interval > (current_year - 1970) * 365 {
            warning LangRes("@1interval_too_big", "en")
        }
        $interval = Sprintf(LangRes("@1x_days", "en"), $Interval)
    }

    action {
        var pages members txs array metrics map
        pages = DBSelectMetrics("ecosystem_pages", $interval, $Aggregate)
        members = DBSelectMetrics("ecosystem_members", $interval, $Aggregate)
        txs = DBSelectMetrics("ecosystem_tx", $interval, $Aggregate)

        var mPages mMembers mTxs map
        mPages = mapping(pages)
        mMembers = mapping(members)
        mTxs = mapping(txs)

        var i lenPages int
        lenPages = Len(pages)
        while i < lenPages{
            var t map, k string
            t = pages[i]
            k = t["key"]
            metrics[k] = Sprintf(LangRes("@1pages", "en") + ": %v, " + LangRes("@1members", "en") + ": %v, " + LangRes("@1tx", "en") + ": %v; [%v, %v]", mPages[k], mMembers[k], mTxs[k], $interval, $Aggregate)
            i = i + 1
        }

        if lenPages > 0{
            @1BufferManager("Action,Key,Val", "set", "metrics", JSONEncode(metrics))
        }
    }
}

contract NodeRemoveByKey {
    data {}
    
    func nodesRemove(keyId int) array {
        var nodesJSON string nodes res array
        nodesJSON = SysParamString("full_nodes")
        if !HasPrefix(nodesJSON, "[") {
            // empty string nodes
            return res
        }
        nodes = JSONDecode(nodesJSON)
        var i int
        while i < Len(nodes){
            var node map
            node = nodes[i]
            if node["key_id"] != keyId {
                res = Append(res, node)
            }
            i = i + 1
        }
        return res
    }
    
    conditions {
        if $original_contract != "ValidatorResign"{
            warning LangRes("@1contract_chain_distorted", "en")
        }
    }

    action {
        var nodes array fullNodes string
        nodes = nodesRemove($key_id)
        fullNodes = JSONEncode(nodes)
        DBUpdateSysParam("full_nodes", fullNodes, "")
    }
}

contract NotificationsBroadcast {
    data {
        RoleId int
        Header string
        Body string
        Popup string "optional"
    }
    
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("@1members").Where({ecosystem:$ecosystem_id, id:kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            if DBFind("@1keys").Columns("id").Where({ecosystem:$ecosystem_id, id:kid}).Row() {
                res["member_id"] = Str(kid)
                res["member_name"] = IdToAddress(kid)
                res["image_id"] = "0"
            }
        }
        return res
    }
    
    conditions {
        if $RoleId !=1 {
            error LangRes("@1admin_only_action", "en")
        }
        if $ecosystem_id != 1 {
            error LangRes("@1first_ecosystem_admin_only_action", "en")
        }
        $ecos = DBFind("@1ecosystems").Columns("id")
        $lenEcos = Len($ecos)
        if $lenEcos == 1 {
            error LangRes("@1other_ecosystems_not_found", "en")
        }
    }
    
    action {
        var i lenIds int eco map ids array bt string sender recipient params note map
        bt = BlockTime()
        sender = getMemberInfo($key_id)
        sender["type"] = 1
        note["header"] = $Header
        note["type"] = 1
        note["icon"] = "icon-envelope"
        note["body"] = $Body
        if $Popup == "true" || Int($Popup) == 1 {
            note["popup"] = 1
        }else{
            note["popup"] = 0
        }
        
        while i < $lenEcos{
            eco = $ecos[i]
            var ecoId int
            ecoId = Int(eco["id"])
            if ecoId != 1 {
                var adminRoleId int
                adminRoleId = Int(DBFind("@1parameters").Where({ecosystem:ecoId, name:"role_admin"}).One("value"))
                ids = DBFind("@1roles_participants").Where({ecosystem:ecoId, "role->id":adminRoleId, "role->type":3}).Columns("member->member_id")
                lenIds = Len(ids)
                var i_ids int
                while i_ids < lenIds{
                    recipient = ids[i_ids]
                    recipient = getMemberInfo(Int(recipient["member.member_id"]))
                    var m map
                    m["recipient"] = recipient
                    m["sender"] = sender
                    m["notification"] = note
                    m["page_name"] = "@1notifications_show"
                    m["page_params"] = params
                    m["date_created"] = bt
                    m["ecosystem"] = ecoId
                    DBInsert("@1notifications", m)
                    i_ids = i_ids + 1
                }
            }
            i = i + 1
        }
    }
}

contract NotificationsClose {
    data {
        notific_id int
    }

    func getPermission() int {
        var array_permissions array result i int prevContract string
        array_permissions = ["@1TokensRefundAccept", "@1TokensRefundReject", "@1VotingDecisionCheck", "@1VotingDelete"]

        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        while i < Len(array_permissions) {
            var contract_name string
            contract_name = array_permissions[i]
            if contract_name==prevContract {
                result = 1
            }
            i = i + 1
        }
        return result
    }


    conditions {
        $notify = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, id:$notific_id}).Columns("id,date_start_processing,processing_info->member_id,closed,notification->type,recipient->member_id,recipient->role_id").Row()
        if !$notify {
            warning LangRes("@1notification_not_found", "en")
        }

        // checking the type of notification
        if $notify["notification.type"] != 1 && $notify["notification.type"] != 2 {
            warning LangRes("@1notification_type_not_found", "en")
        }

        // getting permission
        var flag_permission int
        flag_permission = getPermission()

        // checking single notification
        if $notify["notification.type"] == 1 && flag_permission == 0 {
            if $notify["recipient.member_id"] != $key_id {
                warning LangRes("@1notification_another_member", "en")
            }
        }

        // checking role notification
        if $notify["notification.type"] == 2 && flag_permission == 0 {
            if $notify["date_start_processing"] == "" {
                warning LangRes("@1notification_processing_not_started", "en")
            }

            if $notify["processing_info.member_id"] != $key_id {
                warning LangRes("@1notice_processing_started_another_member", "en")
            }     
        }
    }

    action {
        if $notify["closed"] > 0 {
            return
        }
        if $notify["notification.type"] == 1 {
            @1NotificationsProcess("notific_id", $notific_id)
        }
        var m map
        m["date_closed"] = BlockTime()
        m["closed"] = 1
        DBUpdate("@1notifications", $notific_id, m)

        if $notify["notification.type"] == 1 {
            UpdateNotifications($ecosystem_id, Int($notify["recipient.member_id"]))
        } else {
            UpdateRolesNotifications($ecosystem_id, Int($notify["recipient.role_id"]))
        }
    }
}

contract MembershipDecide {
    data {
        notific_id int
        accept int
    }

    conditions {
        $request = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, id:$notific_id}).Columns("id,closed,sender->member_id,page_params->ecosystem_id,page_params->ecosystem_sender").Row()
        var ecoId int
        ecoId = Int($request["page_params.ecosystem_id"])

        if Int($request["closed"]) == 1 {
            warning LangRes("@1request_processed_already", "en")
        }

        var admin_id int
        admin_id = EcosysParam("role_admin")
        if !RoleAccess(Int(admin_id)) {
            warning LangRes("@1access_denied", "en")
        }
    }

    action {
        var params map
        params["ecosystem_id"] = $request["page_params.ecosystem_id"]
        if $accept == 1 {
            params["status"] = 1
            var pub string mid eco_id int
            mid = Int($request["sender.member_id"])
            eco_id = Int($request["page_params.ecosystem_id"])
            pub = DBFind("@1keys").Where({ecosystem:1, id:mid}).One("pub") 
            DBInsert("@1keys", {id:mid, pub:pub, ecosystem:$ecosystem_id})
        }
        else{
            params["status"] = 0
        }

        var icon header body page ecoName ecoId string
        ecoId = $request["page_params.ecosystem_id"]
        ecoName = DBFind("@1ecosystems").Where({id:ecoId}).One("name")
        icon = "icon icon-user-follow"
        header = LangRes("@1request_approved", "en")
        body = Sprintf(LangRes("@1ecosystem_request_approved", "en"), ecoName, ecoId)
        page = "@1membership_user_view"

        @1NotificationsSend("member_id,sender,icon_name,text_header,text_body,page_name,params_map,eco_id", $request["sender.member_id"], 1, icon, header, body, page, params, Int($request["page_params.ecosystem_sender"]))
        @1NotificationsProcess("notific_id", $notific_id)
        @1NotificationsClose("notific_id", $notific_id)
    }
}

