contract TokensTransfer {
    data {
        Amount money
        SenderId int
        RecipientId int
        Comment string "optional"
        Ecosystem int "optional"
    }

    func getEcosystem() {
        $e_id = Int($Ecosystem)
        if $e_id == 0 {
            $e_id = $ecosystem_id
        } else {
            if !DBFind("@1ecosystems").Where({id:$e_id}).One("id") {
                error Sprintf(LangRes("@1ecosystem_not_found", "en"), $e_id)
            }
        }    
    }

    func getPermission() {
        var array_permissions array result i int prevContract string
        array_permissions = ["@1TokensSend", "@1PoaTokensSend", "@1TokensRefund"]

        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        while i < Len(array_permissions) {
            var contract_name string
            contract_name = array_permissions[i]
            if contract_name==prevContract {
                result = 1
            }
            i = i + 1
        }

        if result == 0 {
            error LangRes("@1tokens_transfer_cannot_be_made", "en")
        }
    }

    func checkSender() {
        var sender_map map
        sender_map = DBFind("@1keys").Where({ecosystem:$e_id, id:$SenderId}).Row()

        if !sender_map {
            error Sprintf(LangRes("@1sender_x_invalid", "en"), $SenderId)
        }
        if Int(sender_map["blocked"]) != 0 {
            error LangRes("@1sender_blocked", "en")
        }
        if Int(sender_map["deleted"]) != 0 {
            error LangRes("@1sender_deleted", "en")
        }
        if Money(sender_map["amount"]) < $Amount {
            error LangRes("@1amount_less_than_needed", "en")
        }
    }

    func checkRecipient() {
        var recipient_map map
        recipient_map = DBFind("@1keys").Where({ecosystem:$e_id, id:$RecipientId}).Row()

        if !recipient_map {
            error Sprintf(LangRes("@1recipient_x_invalid", "en"), $RecipientId)
        }
        if Int(recipient_map["blocked"]) != 0 {
            error LangRes("@1recipient_blocked", "en")
        }
        if Int(recipient_map["deleted"]) != 0 {
            error LangRes("@1recipient_deleted", "en")
        }
    }

    conditions {
        getEcosystem()    //getting an ecosystem number
        getPermission()   //getting permission to run
        checkSender()     //check the sender
        checkRecipient()  //check the recipient
        
        //check amount
        if $Amount == 0 {
            error LangRes("@1amount_zero", "en")
        }
        if $Amount < 0 {
            error LangRes("@1amount_less_zero", "en")
        }
    }

    action {
        var table_name string
        table_name = "@" + Str($e_id) + "keys"
        DBUpdate(table_name, $SenderId, {"-amount": $Amount})
        DBUpdate(table_name, $RecipientId, {"+amount": $Amount})

        var m map
        m["sender_id"] = $SenderId
        m["recipient_id"] = $RecipientId
        m["amount"] = $Amount
        m["comment"] = $Comment
        m["block_id"] = $block
        m["txhash"] = $txhash
        m["ecosystem"] = $e_id
        m["type"] = 3
        m["created_at"] = $block_time
        DBInsert("@1history", m)
    }
}

contract TokensRefund {
    data {
        SenderId int
        RecipientId int
        Amount money
    }

    conditions {
        var prevContract string
        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        if prevContract != "@1TokensRefundAccept"{
            error LangRes("@1contract_start_tokensrefundaccept_only", "en")
        }

        $amount = Money($Amount)
        if $amount <= 0 {
            error LangRes("@1amount_less_equal_zero", "en")
        }
        
        $RecipientId = Int($RecipientId)
        $SenderId = Int($SenderId)
        var sender map
        sender = DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$SenderId}).Row()
        if !sender{
            error LangRes("@1sender_invalid", "en")
        }
        if !DBFind("@1keys").Where({ecosystem:$ecosystem_id, id:$RecipientId}).Row(){
            error LangRes("@1recipient_invalid", "en")
        }
        if sender["blocked"] == 1 {
            error LangRes("@1sender_blocked", "en")
        }
        if sender["amount"] < $amount {
            error Sprintf(LangRes("@1sender_amount_not_enough", "en"), sender["amount"])
        }
    }

    action {
        @1TokensTransfer("Amount,SenderId,RecipientId,Comment", $amount, $SenderId, $RecipientId, "Refund tokens")
    }
}

contract TokensRefundReject {
    data {
        Id string
    }
    func closeNotes() {
        var notes array
        notes = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, page_name:"@1voting_view", "page_params->voting_id":$votingID})
        var i int
        while i < Len(notes){
            var note map
            note = notes[i]
            var noteId int
            noteId = Int(note["id"])
            @1NotificationsClose("notific_id", noteId)
            i = i + 1
        }
    }

    conditions {
        var prevContract string
        prevContract = $stack[0]
        if Len($stack) > 2 {
            prevContract = $stack[Len($stack) - 2]
        }
        if prevContract != "@1VotingDecisionCheck"{
            error LangRes("@1contract_start_votingdecisioncheck_only", "en")
        }
        
        $Id = Int($Id)
        $tokenrefund = DBFind("tokens_refund").Where({"id":$Id}).Row()
        if !$tokenrefund{
            error LangRes("@1tokens_refund_not_found", "en")
        }

        $victimId = Int($tokenrefund["victim_key_id"])
        $attackerId = Int($tokenrefund["attacker_key_id"])
        $amount = Money($tokenrefund["amount"])
        $votingID = Int($tokenrefund["voting_id"])
    }

    action {
        closeNotes()

        // status - 0. Accounts blocked, 1. Discussion, 2. Vote, 3. Closed
        // result - 0. Discussion and voting, 1. Cancelled, 2. Tokens returned
        if $tokenrefund["result"] == 1 || $tokenrefund["result"] == 2 || $tokenrefund["status"] == 3 {
            return
        }
        
        TokensLockoutMember("KeyId,Value", $victimId, 0)
        TokensLockoutMember("KeyId,Value", $attackerId, 0)

        DBUpdate("tokens_refund", $Id, {status:3, result:1})
    }
}

contract TokensRefundAccept {
    data {
        Id string
    }
    func closeNotes() {
        var notes array
        notes = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, page_name:"@1voting_view", "page_params->voting_id":$votingID})
        var i int
