contract VotingStatusUpdate {
    data {}
    
    func notificationSend(voting map) {
        var title string id i lenVoters int voters array
        var buffer_id int
        buffer_id = Int(voting["id"])
        $type_p = DBFind("@1votings").Columns("id,voting->type_participants").Where({ecosystem:$ecosystem_id, id: buffer_id}).Row()
        if($type_p["voting.type_participants"] == 5){
            title = voting["voting.name"]
            id = Int(voting["id"])
            voters = DBFind("@1votings_participants").Columns("member->role_id").Where({ecosystem:$ecosystem_id, voting_id:id, decision:0})
            lenVoters = Len(voters)
            while i < lenVoters {
                var voter roleId params map
                voter = voters[i]
                roleId = Int(voter["member.role_id"])
                params["voting_id"] = id
                @1NotificationsSend("rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type", roleId, 1, "fa-check", LangRes("@1voting", "en"), title, "@1voting_view", params,1)
                i = i + 1
            }

        } else {
            title = voting["voting.name"]
            id = Int(voting["id"])
            voters = DBFind("@1votings_participants").Columns("member->member_id").Where({ecosystem:$ecosystem_id, voting_id:id, decision:0})
            lenVoters = Len(voters)
            while i < lenVoters {
                var voter memberId params map
                voter = voters[i]
                memberId = Int(voter["member.member_id"])
                params["voting_id"] = id
                @1NotificationsSend("member_id,sender,icon_name,text_header,text_body,page_name,params_map", memberId, 1, "fa-check", LangRes("@1voting", "en"), title, "@1voting_view", params)
                i = i + 1
            }
        }
    }
    
    func changeVotings(from, to map) {
        var votings array i lenVotings int v map
        from["deleted"] = 0
        votings = DBFind("@1votings").Columns("id,date_started,date_ended,voting->name").Where(from)

        lenVotings = Len(votings)
        while i < lenVotings{
            v = votings[i]
            if to["status"] == $STARTED{
                notificationSend(v)
                to["flags->notifics"] = 1
            }
            DBUpdate("@1votings", Int(v["id"]), to)
            i = i + 1
        }
    }

    conditions {
        MainCondition()
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
    }
    
    action {
        var bt string
        bt = BlockTime()
        changeVotings({status:$WAITING, "flags->full_data":0}, {status:$INVALID})
        changeVotings({status:$INVALID, "flags->full_data":1, "progress->number_participants":{"$gt":0}}, {status:$VALID})
        changeVotings({status:$WAITING, "flags->full_data":1, "progress->number_participants":{"$gt":0}}, {status:$VALID})
        changeVotings({status:$VALID, date_started:{"$lte":bt}}, {status:$STARTED})
        changeVotings({status:$STARTED, date_ended:{"$lte":bt}}, {status:$FINISHED})
        //changeVotings({status:$STARTED, date_ended:{"$lte":bt}, "flags->decision":1}, {status:$FINISHED})
    }
}

contract VotingSubjectCandidates {
    data {
        votingID int
        memberID int
    }
    
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("@1members").Where({ecosystem:$ecosystem_id, id:kid}).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("@1keys").WhereId(kid).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }

    conditions {
        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id:$votingID}).Columns("id,optional->number_candidates,voting->type,flags->full_data,status").Row()
        if $voting["status"] == $STARTED {
            warning LangRes("@1voting_started_can_not_add_candidates", "en")
        }
        if $voting["status"] == $FINISHED {
            warning LangRes("@1voting_finished_can_not_add_candidates", "en")
        }

        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
            warning LangRes("@1system_voting_can_not_edit", "en")
        }

        if $memberID == 0 {
            $memberID = $key_id
        }

        if DBFind("@1votings_subject").Where({ecosystem:$ecosystem_id, voting_id:$votingID, "subject->member_id":$memberID}).One("id") {
            warning LangRes("@1candidate_added_before", "en")
        }

        $member = getMemberInfo($memberID)
        if !$member {
            warning Sprintf(LangRes("@1key_not_found", "en") + " (%v)", $memberID)
        }
    }

    action {
        DBInsert("@1votings_subject", {ecosystem: $ecosystem_id, voting_id:$votingID, subject:$member})
        var m map
        m["optional->number_candidates"] = Int($voting["optional.number_candidates"]) + 1
        DBUpdate("@1votings", $votingID, m)
    }
}

contract VotingSubjectSettings {
    data {
        votingID int

        roleID int
        vacancies int

        contract_accept string "optional"
        contract_accept_params string "optional"
        contract_reject string "optional"
        contract_reject_params string "optional"
    }
    
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        if $vacancies < 1 {
            warning LangRes("@1vacancies_number_less_one found", "en")
        }

        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id:$votingID, status:[{"$neq":$STARTED},{"$neq":$FINISHED}]}).Columns("optional,voting->type,flags->full_data").Row()

        if !$voting {
            warning LangRes("@1voting_started_can_not_edit", "en")
        }

        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
            warning LangRes("@1system_voting_can_not_edit", "en")
        }
        var role map
        role = DBFind("@1roles").Where({ecosystem:$ecosystem_id, id:$roleID, deleted:0}).Row()
        if !role {
            warning LangRes("@1specified_role_not_found", "en")
        }

        // roles_types: 1. assigned_role, 2. elective_role, 3. system_role, 4. company_role
        if !(role["role_type"] == 2 || role["role_type"] == 3) {
            warning LangRes("@1role_not_elective_or_removed", "en")
        }
    }

    action {
        var optional map
        optional = safeJsonToMap($voting["optional"])

        optional["contract_accept"] = $contract_accept
        optional["contract_accept_params"] = safeJsonToMap($contract_accept_params)
        optional["contract_reject"] = $contract_reject
        optional["contract_reject_params"] = safeJsonToMap($contract_reject_params)
        optional["role_id"] = $roleID
        optional["role_vacancies"] = $vacancies

        DBUpdate("@1votings", $votingID, {optional:optional})

        @1VotingSubjectCheck("votingID", $votingID)
    }
}

contract VotingSubjectContract {
    data {
        votingID int

        contract_accept string
        contract_accept_params string "optional"

        contract_reject string
        contract_reject_params string "optional"
    }
    
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        $STARTED = 3
        $FINISHED = 4
        $voting = DBFind("@1votings").Where({ecosystem:$ecosystem_id, id:$votingID, status:[{"$neq":$STARTED},{"$neq":$FINISHED}]}).Columns("id,date_started,voting->type,flags->full_data").Row()
        if !$voting {
            warning LangRes("@1voting_started_can_not_edit", "en")
        }

        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
            warning LangRes("@1system_voting_can_not_edit", "en")
        }
    }

    action {
        var accept_params, reject_params map
        accept_params = safeJsonToMap($contract_accept_params)
        reject_params = safeJsonToMap($contract_reject_params)

        var subject map
        subject["contract_accept"] = $contract_accept
        subject["contract_accept_params"] = accept_params
        subject["contract_reject"] = $contract_reject
        subject["contract_reject_params"] = reject_params

