contract BufferManager {
    data {
        Action string
        Key string
        Val string "optional"
    }
    func decodeArray(s string) array{
        var ar array
        if HasPrefix($buf["value"], "[") {
            ar = JSONDecode($buf["value"])
        }
        return ar
    }
    conditions {
        $emptyVal = JSONEncode("")
        $buf = DBFind("buffer_data").Where({key:$Key, member_id:$key_id}).Row()
        if !$buf {
            var buf map, id int
            id = DBInsert("buffer_data", {key:$Key, value:$emptyVal, member_id:$key_id})
            buf["id"] = Str(id)
            buf["value"] = ""
            buf["member_id"] = Str($key_id)
            buf["key"] = Str($Key)
            $buf = buf
        }
    }

    action {
        var id int
        id = Int($buf["id"])
        if $Action == "clean" {
            DBUpdate("buffer_data", id, {value:$emptyVal})
        }
        if $Action == "set" {
            DBUpdate("buffer_data", id, {value:$Val})
        }
        if $Action == "array_add_value" {
            var ar array enc string
            ar = decodeArray($buf["value"])
            ar = Append(ar, $Val)
            enc = JSONEncode(ar)
            DBUpdate("buffer_data", id, {value:enc})
        }
        if $Action == "array_remove_value" {
            var ar arNew array i int enc string
            ar = decodeArray($buf["value"])
            while i<Len(ar){
                if ar[i] != $Val{
                    arNew = Append(arNew, ar[i])
                }
                i=i+1
            }
            enc = JSONEncode(arNew)
            DBUpdate("buffer_data", id, {value:enc})
        }
    }
}

contract RolesAccessCheck {
    data {
        rid int
    }
    conditions {
        var role map
        role = DBFind("roles").WhereId($rid).Columns("id,role_name,deleted,roles_access->rids,role_type,creator->member_id,company_id").Row()
        if !role {
            warning Sprintf("%v: Role not found", $this_contract)
        }

        if role["creator.member_id"] == $key_id {
            return
        }

        var rids rawRids array, i int
        rawRids = Split(role["roles_access.rids"], ",")
        while i < Len(rawRids){
            if rawRids[i] > 0 {
                rids = Append(rids, rawRids[i])
            }
            i=i+1
        }
        i=0
        while i<Len(rids){
            if rids[i] == $role_id {
                return
            }
            i=i+1
        }

        if role["role_type"] == 4 {
            // company role
            if role["company_id"] == $key_id {
                return
            }else{
                warning "Sorry, you are not the creator of this company"
            }
        }
        warning Sprintf("Sorry, you don't have permission to change this role (%q)", role["role_name"])
    }
}

contract RolesUnassign {
    data {
        row_id int
    }

    conditions {
        // check record
        var assigned role map
        assigned = DBFind("roles_participants").WhereId($row_id).Columns("id,role->id").Row()
        if !assigned {
            warning "Participant not found"
        }

        role = DBFind("roles").WhereId(Int(assigned["role.id"])).Columns("id,creator->member_id,role_name").Row()
        if !role {
            warning "Role not found in the roles registry"
        }

        var specialRules map prev_contract string
        prev_contract = $stack[0]
        if Len($stack)>2{
            prev_contract = $stack[Len($stack)-2]
        }
        specialRules["@1VotingValidatorAccept"] = "Candidate for validators"
        specialRules["@1DelegateRefresh"] = "Delegate"
        specialRules["@1CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["@1InvestorRefresh"] = "Investor with voting rights"
        specialRules["@1VotingTokensRefundCreate"] = "Investor with voting rights"
        specialRules["@1sysparams_ValidatorResign"] = "Validator"

        if specialRules[prev_contract] != role["role_name"] {
            RolesAccessCheck("rid", Int(role["id"]))
        }
    }

    action {
        // delete the member
        var m map
        m["deleted"] = 1
        m["date_deleted"] = BlockTime()
        DBUpdate("roles_participants", $row_id, m)
    }
}

contract RolesAssign {
    data {
        rid int
        member_id int
    }
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("members").WhereId(kid).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("keys").WhereId(kid).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }
    conditions {
        $role = DBFind("roles").WhereId($rid).Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Row()
        if (Int($role["role_type"]) == 2){
            warning "Elective role can be assigned only during voting"
        }
        if !$role {
            warning "Role not found"
        }

        if !DBFind("keys").WhereId($member_id).Row() {
            warning "Key not found"
        }

        if $role["deleted"] == 1 {
            warning "This role has been deleted. The appointment of members is impossible"
        }

        var specialRules map prev_contract string stack array
        stack = JSONDecode(JSONEncode($stack))
        prev_contract = stack[0]
        if Len(stack)>2{
            prev_contract = stack[Len(stack)-2]
        }
        specialRules["@1VotingValidatorAccept"] = "Validator"
        specialRules["@1DelegateRefresh"] = "Delegate"
        specialRules["@1CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["@1VotingTemplateRun"] = "Investor with voting rights"
        specialRules["@1VotingTokensRefundCreate"] = "Investor with voting rights"
        if specialRules[prev_contract] != $role["role_name"] {
            RolesAccessCheck("rid", $rid)
        }

        $assigned = DBFind("roles_participants").Where({"role->id":$rid, "member->member_id":$member_id, deleted:0}).One("id")
    }

    action {
        if $assigned {
            // do not re-assign and do not warning because this contract may be called for other
            return
        }
        var role member appointer map
        role["id"] = $role["id"]
        role["type"] = $role["role_type"]
        role["name"] = $role["role_name"]
        role["image_id"] = $role["image_id"]

        member = getMemberInfo($member_id)
        appointer = getMemberInfo($key_id)
        var m map
        m["role"] = role
        m["member"] = member
        m["appointed"] = appointer
        m["date_created"] = BlockTime()
        DBInsert("roles_participants", m)
    }
}

contract CandidateValidatorRequest{
    data{
        HostIp string
        // CommissionPub string
        CommissionAddress string
        Description string "optional"
        Comment string "optional"
    }

    func IsNotValidIp(ip string)bool{
        var parts array
        var i, lenParts, part int
        parts = Split(ip, ".")

        lenParts = Len(parts)
        if lenParts != 4{
            return true
        }
        while i< lenParts {
            part = Int(parts[i])
            if part < 0{
                return true
            }
            if part > 255{
                return true
            }
            i = i+1
        }

        return false
    }

    conditions{
        // $CommissionPub = Replace($CommissionPub, " ", "")
        // info Sprintf("%v %v", $CommissionAddress, PubToID($CommissionPub))
        // if $CommissionAddress != PubToID($CommissionPub) {
        // info "Address or Public key are invalid"
        // }

        $CommissionAddress = AddressToId($CommissionAddress)
        if !DBFind("keys").WhereId($CommissionAddress).Row(){
            warning "Address not valid"
        }

        if IsNotValidIp($HostIp){
            warning "Please enter a valid ip address"
        }
        var roleName string, roleId int, role map
        roleName = "Candidate for validators"
        roleId = 4
        role = DBFind("roles").Where({role_name:roleName, id:roleId, deleted:0}).Row()
        if !role{
            warning Sprintf("Role %q not found", roleName)
        }
        RolesAssign("rid,member_id", roleId, $key_id)
    }

    action{
        var m map
        m["host_ip"] = $HostIp
        m["commission_pub"] = ""
        m["commission_address"] = $CommissionAddress
        m["description"] = $Description
        m["comment"] = $Comment
        m["candidate_key_id"] = $key_id
        m["date_created"]  = BlockTime()
        m["deleted"] = 0
        DBInsert("sysparams_candidate", m)
    }
}

