contract VotingCreate {
    data {
        voting_name string
        voting_type int
        type_participants int
        type_decision int

        volume int
        quorum int "optional"

        count_type_voters string "optional"
        description string "optional"
        interval string "optional"
        start_date string "optional"
        start_time string "optional"
        end_date string "optional"
        end_time string "optional"
    }
    func trimZeroTime(s string) string{
        if Contains(s, "T00:00:00Z"){
            s = s Replace(s, "T00:00:00Z", "")
        }
        return s
    }
    func dateAddTime(d, t string) string {
        var dt string
        if Size(t) == 5{
            dt = Sprintf("%v %v:00", trimZeroTime(d), t)
        }
        return dt
    }
    func isCalledFromPage() bool{
        return $stack[0] == Sprintf("@%v%v", $ecosystem_id, $this_contract)
    }
    func fixDatetimes(){
        $date_started = dateAddTime($start_date, $start_time)
        $date_ended = dateAddTime($end_date, $end_time)

        if UnixDateTime($date_started) == 0 || UnixDateTime($date_ended) == 0 { // invalid datetimes
            if isCalledFromPage(){
                var errs array
                if Size($start_date) < 10 {
                    errs = Append(errs, "starting date")
                }
                if Size($start_time) < 5 {
                    errs = Append(errs, "starting time")
                }
                if Size($end_date) < 10 {
                    errs = Append(errs, "ending date")
                }
                if Size($end_time) < 5 {
                    errs = Append(errs, "ending time")
                }
                error Sprintf("Error create voting: %v unspecified", Join(errs, ", "))
            }else{
                $date_started = $block_time
                $interval = Int($interval)
                if $interval == 0 {
                    $interval = 3
                }
                $date_ended = $date_started + 3600 * 24 * $interval
                $date_started = DateTime($date_started)
                $date_ended = DateTime($date_ended)
            }
        }
        if UnixDateTime($date_started) < $block_time{
            error "Invalid date starting"
        }
        if UnixDateTime($date_started) >= UnixDateTime($date_ended){
            error "Invalid date ending"
        }
    }

    conditions {
        if (($type_decision == 1 || $type_decision == 2) && $count_type_voters == 1){
            info("The selected type of voting is not suitable for this decision")
        }
        if $count_type_voters == 0 {
            if ($quorum < 5) || ($quorum > 100) {
                warning Sprintf("Quorum [%v] should be in the range from 5 to 100", $quorum)
            }   
            if ($volume < 50) || ($volume > 100) {
                warning Sprintf("Volume [%v] should be in the range from 50 to 100", $volume)
            }
        }
        if $count_type_voters == 1 {
            if $volume <= 0 {
                info("Volume can not be less than 1")
            }
            $quorum = 0
        }
        if $voting_type == 2 {
            var specRules map
            specRules["VotingTokensRefundCreate"] = 1
            specRules["VotingTemplateRun"] = 1
            specRules["VotingNodeAdd"] = 1
            if specRules[$original_contract] != 1 {
                warning Sprintf("The voting type 'system' is not allowed to launch from %v", $original_contract)
            }
        }


        $key = DBFind("keys").WhereId($key_id).Row()
        if !$key {
            warning "You are not a member of the ecosystem"
        }

        fixDatetimes()
    }

    action {
        var voting progress flags creator map votingId int
        voting["name"] = $voting_name
        voting["type"] = $voting_type
        voting["description"] = $description
        voting["type_participants"] = $type_participants
        voting["type_decision"] = $type_decision
        voting["volume"] = $volume
        voting["quorum"] = $quorum
        voting["count_type_voters"] = $count_type_voters

        progress["number_participants"] = "0"
        progress["number_voters"] = "0"
        progress["percent_success"] = "0"
        progress["percent_voters"] = "0"

        flags["success"] = "0"
        flags["decision"] = "0"
        flags["full_data"] = "0"
        flags["notifics"] = "0"

        creator["member_id"] = $key["id"]
        creator["member_name"] = IdToAddress($key_id)
        var m map
        m["voting"] = voting
        m["progress"] = progress
        m["flags"] = flags
        m["creator"] = creator
        m["status"] = 1
        m["date_started"] = $date_started
        m["date_ended"] = $date_ended

        votingId = DBInsert("votings", m)

        // if all members are voting participants
        if $type_participants == 1 {
            VotingInvite("votingID,var_id", votingId, 0)
        }
        $result = votingId
    }
}

contract VotingSubjectCheck {
    data {
        votingID int
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        $voting_map = DBFind("votings").WhereId($votingID).Columns("id,optional,voting->type,voting->type_decision,flags").Row()
        if !$voting_map {
            warning "Voting is not found"
        }

        $type_decision = $voting_map["voting.type_decision"]
        if $type_decision < 1 || $type_decision > 5 {
            warning "invalid type of decision"
        }

        $subject_map = DBFind("votings_subject").Where({voting_id:$votingID}).Row()
        if !$subject_map {
            warning "Subject of voting is not found"
        }
    }

    action {
        var flags, subject map
        flags = safeJsonToMap($voting_map["flags"])
        if $type_decision > 2 && $type_decision <= 5 {
            subject = safeJsonToMap($subject_map["subject"])
        }

        var fullData string

        if $type_decision == 1 || $type_decision == 2 {
            var optional map
            optional = safeJsonToMap($voting_map["optional"])

            if optional["number_candidates"] > 0 && optional["role_vacancies"] > 0 && optional["role_id"] > 0 {
                fullData = 1
            }
        }

        if $type_decision == 3 {
            if subject["text"] != "" && subject["hash"] != "" {
                fullData = 1
            }
        }

        if $type_decision == 4 {
            if $voting_map["voting.type"] == 1{ // personal
                if subject["table"] != "" && subject["table_id"] != "" && subject["column"] != "" && subject["column_value"] != "" {
                    fullData = 1
                }
            }
            if $voting_map["voting.type"] == 2 { // system
                if subject["column"] != "" && subject["column_value"] != "" {
                    fullData = 1
                }
            }
        }

        if $type_decision == 5 {
            if subject["contract_accept"] != "" && subject["contract_reject"] != "" {
                fullData = 1
            }
        }

        if fullData == 1 {
            DBUpdate("votings", $votingID, {"flags->full_data":fullData})
        }
    }
}

contract VotingNotificationSend {
    data {
        votingID int
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 {
            res = JSONToMap(m)
        }
        return res
    }
    conditions {
        var bt string
        bt = BlockTime()
        $voting = DBFind("votings").Columns("id,date_ended").Where({id:$votingID, date_ended:[{"$gt":bt}]}).One("id")
        if !$voting {
            warning "Voting has expired. Notifications can not be sent"
        }

        $voting = DBFind("votings").Columns("voting->name,creator->member_id,flags->notifics,flags,date_started").Where({id:$votingID, date_started:[{"$gt":bt}]}).Row()
        if !$voting {
            warning "Voting has not yet begun. Try again later, please"
        }
        if $voting["creator.member_id"] != $key_id {
            warning "You are not the creator of the voting"
        }
        if $voting["flags.notifics"] == 1 {
            warning "Notifications have already been sent"
        }

        $voting_name = $voting["voting.name"]
    }

    action {
        var participants array
        participants = DBFind("votings_participants").Where({voting_id:$votingID, decision:0})
        var i int
        while i < Len(participants) {
            var participant member params map
            participant = participants[i]
            member = safeJsonToMap(participant["member"])
            params["voting_id"] = $votingID
            NotificationsSend("member_id,sender,icon_name,text_header,text_body,page_name,params_map,rid,closure_type", Int(member["member_id"]), 1, "fa-check", "Voting", $voting_name, "voting_view", JSONEncode(params), 0, 0)

            i = i + 1
        }

        DBUpdate("votings", $votingID, {"flags->notifics":1})
    }
}

contract VotingStatusUpdate {
    data {}

    func notificationSend(voting map){
        var title string id i lenVoters int voters array
        $type_p = DBFind("votings").Columns("id,voting->type_participants").WhereId(Int(voting["id"])).Row()
        if($type_p["voting.type_participants"] == 5){
            title = voting["voting.name"]
            id = Int(voting["id"])
            voters = DBFind("votings_participants").Columns("member->role_id").Where({voting_id:id, decision:0})
            lenVoters = Len(voters)
            while i < lenVoters {
                var voter roleId params map
                voter = voters[i]
                roleId = Int(voter["member.role_id"])
                params["voting_id"] = id
                NotificationsSend("rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type", roleId, 1, "fa-check", "Voting", title, "voting_view", params,1)
                i = i + 1
            }
            //notifications_Send("icon_name,rid,closure_type,sender,text_header,text_body,page_name,params_map","fa-check",$role_id_send, 1, 1,"Voting",$voting_name,"voting_view",JSONEncode(params))
        } else{
            title = voting["voting.name"]
            id = Int(voting["id"])
            voters = DBFind("votings_participants").Columns("member->member_id").Where({voting_id:id, decision:0})
            lenVoters = Len(voters)
            while i < lenVoters {
                var voter memberId params map
                voter = voters[i]
                memberId = Int(voter["member.member_id"])
                params["voting_id"] = id
                NotificationsSend("member_id,sender,icon_name,text_header,text_body,page_name,params_map", memberId, 1, "fa-check", "Voting", title, "voting_view", params)
                i = i + 1
            }
        }
    }
    func changeVotings(from, to map){
        var votings array i lenVotings int v map
        from["deleted"] = 0
        votings = DBFind("votings").Columns("id,date_started,date_ended,voting->name").Where(from)

        lenVotings = Len(votings)
        while i<lenVotings{
            v = votings[i]
            if to["status"] == $STARTED{
                notificationSend(v)
                to["flags->notifics"] = 1
            }
            DBUpdate("votings", Int(v["id"]), to)
            i=i+1
        }
    }

    conditions {
        MainCondition()
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
    }
    action {
        var bt string
        bt = BlockTime()
        changeVotings({status:$WAITING, "flags->full_data":0}, {status:$INVALID})
        changeVotings({status:$INVALID, "flags->full_data":1, "progress->number_participants":{"$gt":0}}, {status:$VALID})
        changeVotings({status:$WAITING, "flags->full_data":1, "progress->number_participants":{"$gt":0}}, {status:$VALID})
        changeVotings({status:$VALID, date_started:{"$lte":bt}}, {status:$STARTED})

        changeVotings({status:$STARTED, date_ended:{"$lte":bt}}, {status:$FINISHED})

        // changeVotings({status:$STARTED, date_ended:{"$lte":bt}, "flags->decision":1}, {status:$FINISHED})
    }
}

contract VotingSubjectCandidates {
    data {
        votingID int
        memberID int
    }
    func getMemberInfo(kid int) map {
        var res member map
        member = DBFind("members").WhereId(kid).Row()
        if member {
            res["member_id"] = member["id"]
            res["member_name"] = member["member_name"]
            res["image_id"] = member["image_id"]
        }else{
            member = DBFind("keys").WhereId(kid).Row()
            if member {
                res["member_id"] = member["id"]
                res["member_name"] = IdToAddress(Int(member["id"]))
                res["image_id"] = 0
            }
        }
        return res
    }

    conditions {
        $STARTED = 3
        $FINISHED = 4        
        $voting = DBFind("votings").Where({id:$votingID}).Columns("id,optional->number_candidates,voting->type,flags->full_data,status").Row()
        if $voting["status"] == $STARTED {
            warning "Voting already started. Can not add candidates"
        }
        if $voting["status"] == $FINISHED {
            warning "Voting already finished. Can not add candidates"
        }

        if $voting["flags.full_data"] == 1 && $voting["voting.type"] == 2 {
            warning "This vote is a system. Change settings not allowed"
        }

        if $memberID == 0 {
            $memberID = $key_id
        }

        if DBFind("votings_subject").Where({voting_id:$votingID, "subject->member_id":$memberID}).One("id") {
            warning "This candidature for this role has already been added before"
        }

        $member = getMemberInfo($memberID)
        if !$member {
            warning Sprintf("Key not found (%v)", $memberID)
        }
    }

    action {
        DBInsert("votings_subject", {voting_id:$votingID, subject:$member})
        var m map
        m["optional->number_candidates"] = Int($voting["optional.number_candidates"]) + 1
        DBUpdate("votings", $votingID, m)
    }
}

