        Vacancies int "optional"
        ContractAccept string "optional"
        ParamsAccept string "optional"
        ContractReject string "optional"
        ParamsReject string "optional"
    }

    conditions{
        if $TypeParticipants == 3 {
            if Size($Voters) == 0 || $Voters == "NULL" || $Voters == 0 {
                warning "Voters role not found"
            }
        }
        if Size($Title) <3 {
            warning "Please enter title"
        }
        if $Volume <0 || $Volume > 100 {
            warning "Wrong volume range"
        }
        if $Quorum <0 || $Quorum > 100 {
            warning "Wrong quorum range"
        }

        if $TypeDecision == 1 || $TypeDecision == 2 {
            if $Vacancies < 1 {
                warning "Wrong number vacancies"
            }
            if $Candidates == "NULL" || $Candidates == 0 || Size($Candidates) == 0 {
                warning "Wrong candidate role"
            }
        }

        if Size($InitContract) > 0{
            if GetContractByName($InitContract) == 0 {
                warning "Init Contract not found"
            }
        }
        if Size($ContractAccept) > 0{
            if GetContractByName($ContractAccept) == 0 {
                warning "Contract for accepted decision not found"
            }
        }
        if Size($ContractReject) > 0{
            if GetContractByName($ContractReject) == 0{
                warning "Contract for rejected decision not found"
            }
        }
    }

    action{
        var m map
        m["title"] = $Title
        m["subject"] = $Subject
        m["voters"] = $Voters
        m["candidates"] = $Candidates
        m["init_contract"] = $InitContract
        m["volume"] = $Volume
        m["quorum"] = $Quorum
        m["type_voting"] = $TypeVoting
        m["type_participants"] = $TypeParticipants
        m["type_decision"] = $TypeDecision
        m["vacancies"] = $Vacancies
        m["contract_accept"] = $ContractAccept
        m["accept_params"] = $ParamsAccept
        m["contract_reject"] = $ContractReject
        m["reject_params"] = $ParamsReject
        $result = DBInsert("voting_templates", m)
    }
}

contract VotingNodeAdd{
    data{
        TcpAddress string
        ApiAddress string
        KeyId int
        PubKey string
        Duration int
    }
    func warn(name, detail string){
        warning Sprintf("%v invalid (%v)", name, detail)
    }
    func checkApiAddress(addr string){
        var name string
        name = "API Address"
        if Size(addr) == 0 {
            warn(name, "size")
        }
        if !(HasPrefix(addr, "http://") || HasPrefix(addr, "https://")) {
            warn(name, "protocol")
        }
        if Contains(addr, " ") {
            warn(name, "space")
        }
        addr = Replace(addr, "http://", "")
        addr = Replace(addr, "https://", "")
        var parts array
        if Contains(addr, ":") {
            parts = Split(addr, ":")
            if Len(parts) > 2 {
                warn(name, ":")
            }
            var port string
            port = parts[1]
            if port <= 0 {
                warn(name, "port")
            }
            addr = parts[0]
        }
    }
    func checkTcpAddress(addr string){
        var name string
        name = "TCP Address"
        if Size(addr) == 0 {
            warn(name, "size")
        }
        if Contains(addr, " ") {
            warn(name, "space")
        }
        var parts array
        if Contains(addr, ":") {
            parts = Split(addr, ":")
            if Len(parts) > 2 {
                warn(name, ":")
            }
            var port string
            port = parts[1]
            if port <= 0 {
                warn(name, "port")
            }
        }
    }
    func checkPub(pub string){
        if PubToID(pub) == 0 {
            warning "Public key invalid"
        }
    }
    func checkKeyId(keyid int){
        var existed map
        existed = DBFind("keys").WhereId(keyid).Row()
        if !existed {
            warning "Key id not found"
        }
    }
    func getNodesJSON() string{
        var nodes string
        nodes = SysParamString("full_nodes")
        if Size(nodes) < 100 {
            nodes = "[]"
        }
        return nodes
    }
    func appendFirstNode(nodes array) array{
        var first string firstNode map
        first = AppParam($appId,"first_node", $ecosystem_id)
        if !HasPrefix(first, "{") || Size(first) < 100 {
            warning "Application parameter first_node not valid"
        }
        firstNode = JSONDecode(first)
        checkTcpAddress(firstNode["tcp_address"])
        checkApiAddress(firstNode["api_address"])
        checkKeyId(Int(firstNode["key_id"]))
        checkPub(firstNode["public_key"])

        nodes = Append(nodes, firstNode)
        return nodes
    }
    conditions{
        $appId = Int(DBFind("applications").Where({name:"Basic application"}).Columns("name,id").One("id"))
        $templateId = Int(AppParam($appId,"voting_sysparams_template_id", $ecosystem_id))
        if $templateId == 0 {
            warning "Template id not found"
        }
        checkKeyId($KeyId)
        checkPub($PubKey)
        checkApiAddress($ApiAddress)
        checkTcpAddress($TcpAddress)
    }

    action{
        var nodes array node map
        node["tcp_address"] = $TcpAddress
        node["api_address"] = $ApiAddress
        node["key_id"] = Str($KeyId)
        node["public_key"] = $PubKey
        nodes = JSONDecode(getNodesJSON())
        if Len(nodes) == 0 {
            nodes = appendFirstNode(nodes)
        }
        nodes = Append(nodes, node)
        VotingTemplateRun("TemplateId,Duration,KeyId,TableWritten,RowId,ColumnName,NewValue,TextDocument", $templateId, $Duration, 0, "", "", "full_nodes", JSONEncode(nodes), "")
    }
}

contract VotingTokensRefundCreate{
    data{
        VictimAccount string
        SuspectAccount string
        Amount int
        Note string
    }
    func blockAccounts(victimId, attackerId int){
        if victimId==0 {
            error "unblockAccounts. invalid victim key"
        }
        if attackerId==0{
            error "unblockAccounts. invalid attacker key"
        }
        if !DBFind("keys").Where({blocked:1, id:victimId}).One("id"){
            DBUpdate("keys", victimId, {blocked:1})
        }
        if !DBFind("keys").Where({blocked:1, id:attackerId}).One("id"){
            DBUpdate("keys", attackerId, {blocked:1})
        }
    }
    func getKeyId(test string) int{
        if Len(Split(test, "-")) > 1 {
            // probably it is address
            return AddressToId(test)
        }
        if "invalid" != IdToAddress(Int(test)){
            // probably it is key_id
            return Int(test)
        }
        return 0
    }

    conditions{
        if $Amount <= 0{
            error "Amount not valid"
        }
        if Size($Note) == 0{
            error "Please add description"
        }
        var victim attacker int
        victim = getKeyId($VictimAccount)
        attacker = getKeyId($SuspectAccount)
        if victim == 0{
            error "Victim address not valid"
        }
        if attacker == 0{
            error "Suspect address not valid"
        }
        $victimId = victim
        $attackerId = attacker

        var app_id template_id int
        app_id = Int(DBFind("applications").Where({name:"Basic application"}).Columns("name,id").One("id"))
        template_id = Int(AppParam(app_id,"voting_tokenrefund_template_id", $ecosystem_id))
        if template_id <= 0 {
            warning "Template id not found"
        }

        $template = DBFind("voting_templates").WhereId(template_id).Row()
        $votersRoleId = Int($template["voters"])
        $votingName = $template["title"]
        $typeParticipants = Int($template["type_participants"])
        $typeDecision = Int($template["type_decision"])
        $typeVoting = Int($template["type_voting"])
        $volume = Int($template["volume"])
        $quorum = Int($template["quorum"])
        $contractAccept = $template["contract_accept"]
        $contractReject = $template["contract_reject"]
        var initContract string
        initContract = $template["init_contract"]
        if Size(initContract) > 4 { // greater then "NULL"
            var m map
            CallContract(initContract, m)
        }
        $desc = Sprintf("%v (victim: %v, attacker: %v, amount: %v)", $votingName, $victimId, $attackerId, $Amount)

        if !DBFind("roles_participants").Where({"role->id":$votersRoleId, deleted:0}).One("id") {
            warning Sprintf("Role (id: %v) does not contain members", $votersRoleId)
        }
    }

    action{
        blockAccounts($victimId,$attackerId)
        var votingId int params m map paramsJSON interval_days string
        interval_days = "3"
        votingId = VotingCreate("voting_name,voting_type,description,type_participants,type_decision,volume,quorum,interval", $votingName, $typeVoting, $desc, $typeParticipants, $typeDecision, $volume, $quorum, interval_days)

        m["victim_key_id"] = $victimId
        m["attacker_key_id"] = $attackerId
        m["amount"] = $Amount
        m["note"] = $Note
        m["validator_key_id"] = $key_id
        m["blocked_at"] = BlockTime()
        m["voting_id"] = votingId
        m["status"] = 1 // 0.Accounts blocked 1.Discussion 2.Vote 3.Closed
        m["result"] = 0 // 0. discussion and voting 1. cancelled 2. the tokens returned
        params["Id"] = DBInsert("tokenrefund", m)

        paramsJSON = JSONEncode(params)
        VotingSubjectContract("votingID,contract_reject,contract_accept,contract_reject_params,contract_accept_params", votingId, $contractReject, $contractAccept, paramsJSON, paramsJSON)

        VotingInvite("votingID,var_id", votingId, $votersRoleId)
    }
}

contract VotingDecisionReject {
    data {
        votingID int
    }
    func closeNotification(){
        var role_id_search int
        role_id_search = Int(DBFind("notifications").Where({"recipient->role_id":$role_id, page_name:"voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
        if role_id_search > 0 {
            NotificationsClose("notific_id", role_id_search)
        } else {
            var id int
            id = Int(DBFind("notifications").Where({"recipient->member_id":$key_id, page_name:"voting_view", "page_params->voting_id":$votingID, closed:0}).One("id"))
            if id > 0 {
                NotificationsClose("notific_id", id)
            }
        }
    }
    conditions {
        $WAITING = 1
        $VALID = 2
        $STARTED = 3
        $FINISHED = 4
        $INVALID = 5
        $voting = DBFind("votings").WhereId($votingID).Columns("voting->type_participants,voting->role_id,flags->decision,flags->notifics,status").Row()
        if !$voting {
            warning "Voting is not found"
        }
        if $voting["voting.type_participants"] == 3{
            if $voting["voting.role_id"] != $role_id{
                warning "Incorrect role for voting"
            }
        }
        if $voting["status"] == $FINISHED {
            warning "Voting has expired. Voting is now not possible"
        }
        if $voting["status"] != $STARTED {
            warning "Voting has not yet begun. Try again later, please"
        }
        $participant_role_id = DBFind("votings_participants").Where({voting_id:$votingID, "member->role_id":$role_id,"member->member_id":0}).One("id")

        $check_type = DBFind("votings").Columns("id,voting,voting->type_participants").WhereId($votingID).Row()
        if (Int($check_type["voting.type_participants"]) < 5){
            $participant_id = DBFind("votings_participants").Where({voting_id:$votingID, "member->member_id":$key_id,decision:0}).One("id")
            if !$participant_id {
                warning "You are not a participant in this voting"
            }
        }

        if $participant_role_id {
            $rid = DBFind("votings_participants").Columns("id,voting_id,member->role_id").Where({voting_id:$votingID, "member->role_id":$role_id}).Row()
            if $rid {
                var mem_role int
                mem_role = Int($rid["member.role_id"])
                $participant_id = DBFind("roles_participants").Columns("id,role,role->id,member,member->member_id").Where({"member->member_id":$key_id,"role->id":mem_role,"deleted":0}).One("member.member_id")
                if !$participant_id{
                    warning "You are not a participant in this voting"
                } else {
                    var solo_role_vot map
                    $membership_role_name = DBFind("members").WhereId($key_id).Row()
                    solo_role_vot["role_id"] = Int($rid["member.role_id"])
                    solo_role_vot["member_id"] = $key_id
                    solo_role_vot["member_name"] = $membership_role_name["member_name"]
                    solo_role_vot["image_id"] = $membership_role_name["image_id"]
                    var json string
                    json = JSONEncode(solo_role_vot)
                    DBUpdate("votings_participants",Int($rid["id"]),{"member":json})
                    $participant_id = Int($rid["id"])
                }
            } else {
                warning "You are not a participant in this voting"
            }
        }
    }

    action {
        if $voting["flags.notifics"] == 1 {
            closeNotification()
        }
        if $voting["flags.decision"] != 1 {
            var bt string
            bt = BlockTime()

            DBUpdate("votings_participants", Int($participant_id), {decision_date:bt, decision:"-1"})
            VotingUpdate("votingID", $votingID)
        }
    }
}

contract VotingValidatorAccept{
    data{
        VotingTemplateId string
        KeyId string
        VotingId string
    }
    func getAppRoleId(id int, name string, warn bool) int{
        var rid int
        rid = Int(AppParam(id, name, $ecosystem_id))
        if rid == 0 && warn{
            warning Sprintf("Role not setted in application parameter '%v'", name)
        }
        return rid
    }
    func conditions{
        var prev string
        prev = $stack[0]
        if Len($stack) > 2{
            prev = $stack[Len($stack) - 2]
        }
        if prev != "@1VotingDecisionCheck"{
            warning "This contract allow starts only from @1VotingDecisionCheck"
        }

        var appId templateId int params map
        appId = Int(DBFind("applications").Where({name:"Basic application"}).Columns("name,id").One("id"))
        templateId = Int(AppParam(appId,"voting_validators_template_id", $ecosystem_id))
        if !DBFind("voting_templates").WhereId(templateId).One("id"){
            warning "Voting template not found"
        }
        if $VotingTemplateId != templateId{
            warning "Incorrect template"
        }

        var candidateId oldRoleId roleId int roleName string
        oldRoleId = getAppRoleId(appId, "role_candidate_for_validators", false)
        roleId = getAppRoleId(appId, "role_validator", true)

        candidateId = DBFind("sysparams_candidate").Where({candidate_key_id:$KeyId}).One("candidate_key_id")
        if !candidateId {
            warning "Candidate key id not found"
        }
        $oldRoleRowId = Int(DBFind("roles_participants").Where({deleted:0, "role->id":oldRoleId, "member->member_id":candidateId}).One("id"))

        $roleId = Int(DBFind("roles").Where({id:roleId, deleted:0}).One("id"))
        if $roleId == 0 {
            warning "Validator role not found"
        }
    }

    func action{
        if $oldRoleRowId > 0 {
            RolesUnassign("row_id", $oldRoleRowId)
        }
        RolesAssign("rid,member_id", $roleId, Int($KeyId))
    }
}

