contract Roles_Assign {
    data {
        role_id int
        member_id int
    }

    conditions {

        $role_map = DBFind("roles_list").Where("id = $", $role_id).Row()
        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        $appointer_map = DBFind("members").Where("id = $", $key_id).Row()

        if !$role_map {
            warning "Role not found"
        }
        if !$member_map {
            warning "Member not found"
        }
        if !$appointer_map {
            warning "Appointer not found"
        }

        if(Int($role_map["delete"]) == 1) {
            warning "This role has been deleted. The appointment of members is impossible"
        }
        if((Int($role_map["role_type"]) != 1) &&(Int($role_map["role_type"]) != 4)) {
            warning "This role is not assignable. The appointment of members is impossible"
        }
        if(Int($role_map["creator_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role. The appointment of members is impossible"
        }

        $assign_id = DBFind("roles_assign").Where("role_id=$ and member_id=$ and delete=0", $role_id, $member_id).One("id")
        if($assign_id != nil) {
            warning "This member has already been added"
        }
    }

    action {
        DBInsert("roles_assign", "role_id,role_type,role_name,member_id,member_name,member_avatar,appointed_by_id,appointed_by_name,timestamp date_start,delete",
        $role_id, $role_map["role_type"], $role_map["role_name"], $member_id, $member_map["member_name"], $member_map["avatar"], $key_id, $appointer_map["member_name"], $block_time, 0)
    }
}

contract Companies_Del {
    data {
        company_id int
    }

    conditions {
        // check creator of the company
        if ($company_id != $key_id) {
            warning "Sorry, you are not the creator of this company"
        }
    }

    action {
        // get the list of roles
        $ret_roles = DBFind("roles_list").Columns("id").Where("company_id = $ and delete = $", $company_id, 0).Order("id")

        // delete all roles
        $i1 = 0
        while ($i1 < Len($ret_roles)) {
            $vals_roles = $ret_roles[$i1]
            $i1 = $i1 + 1
        }

        // get the list of roles
        $ret_participants = DBFind("companies_participants").Columns("id").Where("company_id = $ and delete = $", $company_id, 0).Order("id")

        // delete all roles
        $i2 = 0
        while ($i2 < Len($ret_participants)) {
            $vals_participants = $ret_participants[$i2]
            Companies_Participants_Del("participant_id", Int($vals_participants["id"]))
            $i2 = $i2+ 1
        }

        // delete company
        DBUpdate("companies", $company_id, "delete,timestamp date_end", 1, $block_time)
    }
}

contract Notifications_Roles_Finishing{
    data {
	    notific_id int
    }

    conditions {
		$notifications_map = DBFind("notifications").Where("id=$", $notific_id).Row()
		if ($notifications_map["id"] == nil){
			warning "Notification not found"
		}

        if ( Int($notifications_map["started_processing_id"]) == 0 ){
            warning "Sorry, processing of this notification has not yet begun"  
        }
        if ( Int($notifications_map["started_processing_id"]) != $key_id ){
            warning "Sorry, processing of this notice began another member"   
        }
        if ( (Int($notifications_map["finished_processing_id"]) != 0) || (Int($notifications_map["closed"]) > 0) ){
            warning "Sorry, this notification has already been closed before"  
        }

		$assign_id = DBFind("roles_assign").Where("role_id=$ and member_id=$ and delete=$", Int($notifications_map["role_id"]), $key_id, 0).One("id")
        if ($assign_id == 0){
            warning "Sorry, you are not part of the role for which this notification is intended"  
        }
    }

    action {
        DBUpdate("notifications", $notific_id, "timestamp finished_processing_time,finished_processing_id,closed", $block_time, $key_id, 1) 
    }
}

