contract Roles_Assign {
    data {
        rid int
        member_id int
    }

    conditions {
        $role_map = DBFind("roles").Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Where("id = $", $rid).Row()
        if !$role_map {
            warning "Role not found"
        }

        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        if !$member_map {
            warning "Member not found"
        }

        $appointer_map = DBFind("members").Where("id = $", $key_id).Row()
        if !$appointer_map {
            warning "Appointer not found"
        }

        if $role_map["deleted"] == 1 {
            warning "This role has been deleted. The appointment of members is impossible"
        }

        var specialRules map
        specialRules["sysparams_AcceptValidator"] = "Validator"
        specialRules["sysparams_DelegateRefresh"] = "Delegate"
        specialRules["sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["voting_TemplateRun"] = "Investor with voting rights"
        specialRules["tokenrefund_Start"] = "Investor with voting rights"

        if !(specialRules[$original_contract] == $role_map["role_name"]) {
            if $role_map["role_type"] != 1 && $role_map["role_type"] != 4 {
                warning "This role is not assignable. The appointment of members is impossible"
            }
            // check creator of the role
            if $role_map["creator.member_id"] != $key_id {
                warning "Sorry, you are not the creator of this role. The appointment of members is impossible"
            }
        }

        $assign_id = DBFind("roles_participants").Where("role->id=$ and member->member_id=$ and deleted=0", $rid, $member_id).One("id")
        if $assign_id {
            warning "This member has already been added"
        }

    }

    action {
        var role map
        role["id"] = $role_map["id"]
        role["type"] = $role_map["role_type"]
        role["name"] = $role_map["role_name"]
        role["image_id"] = $role_map["image_id"]

        var member map
        member["member_id"] = $member_map["id"]
        member["member_name"] = $member_map["member_name"]
        member["image_id"] = $member_map["image_id"]

        var appointed map
        appointed["member_id"] = $appointer_map["id"]
        appointed["member_name"] = $appointer_map["member_name"]
        appointed["image_id"] = $appointer_map["image_id"]

        DBInsert("roles_participants", "role,member,appointed,timestamp date_created", role, member, appointed, $block_time)
    }
}

contract Roles_Create {
    data {
        role_name string
        role_type int
        icon string "image,optional"
        company_id int "optional"
    }

    conditions {
        // system role
        if $role_type == 3 {
            warning "System role can not be created"
        }

        // company role
        if $role_type == 4 {
            // check param
            if $company_id == 0 {
                warning "On this page you can not create a role for the company. Go to the company's view page to create a role"
            }

            // check creator of the company
            if $company_id != $key_id {
                warning "Sorry, you are not the creator of this company"
            }
        }

        $member_map = DBFind("members").Where("id = $", $key_id).Row()
        if !$member_map {
            warning "You are not a member of the ecosystem"
        }
    }

    action {
        var creator map
        creator["member_id"] = $member_map["id"]
        creator["member_name"] = $member_map["member_name"]
        creator["image_id"] = $member_map["image_id"]

        // insert role
        if $role_type == 4 {
            $rid = DBInsert("roles", "role_name,role_type,creator,timestamp date_created,company_id", $role_name, $role_type, creator, $block_time, $company_id)
        } else {
            $rid = DBInsert("roles", "role_name,role_type,creator,timestamp date_created,company_id", $role_name, $role_type, creator, $block_time, 0)
        }

        // insert image
        var imageId int
        if Size($icon) > 0 {
            var app_name string
            app_name = "Basic application"

            var image_name string
            image_name = "role-" + Str($rid) + " " + MD5($icon)

            $app_id = DBFind("applications").Where("name = $ AND deleted = 0", app_name).One("id")
            imageId = UploadBinary("Name,Data,AppID,MemberID", image_name, $icon, Int($app_id), $key_id)
        }

        // update image
        DBUpdate("roles", $rid,  "image_id", imageId)

        // result
        $result = $rid
    }
}

contract Roles_Unassign {
    data {
        row_id int
    }

    conditions {
        // check record
        var assigned, role map
        assigned = DBFind("roles_participants").Columns("id,role->id").Where("id=$", $row_id).Row()
        if !assigned {
            warning "Participant not found"
        }

        role = DBFind("roles").Columns("id,creator->member_id,role_name").Where("id=?", assigned["role.id"]).Row()
        if !role {
            warning "Role not found in the roles registry"
        }

        var specialRules map
        specialRules["sysparams_AcceptValidator"] = "Validator"
        specialRules["sysparams_DelegateRefresh"] = "Delegate"
        specialRules["sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["sysparams_InvestorRefresh"] = "Investor with voting rights"
        specialRules["tokenrefund_Start"] = "Investor with voting rights"
        specialRules["sysparams_ValidatorResign"] = "Validator"

        if !(specialRules[$original_contract] == role["role_name"]) {
            // check creator of the role
            if role["creator.member_id"] != $key_id {
                warning "Sorry, you are not the creator of this role"
            }
        }
    }

    action {
        // delete the member
        DBUpdate("roles_participants", $row_id, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract Roles_Restore {
    data {
        rid int
    }

    conditions {        
        var role map
        role = DBFind("roles").Where("deleted=1 and id=?", $rid).Row()
        if !role {
            warning "Role not found"
        }
    }

    action {
        DBUpdate("roles", $rid, "deleted", 0)
    }
}

contract Roles_Del {
    data {
        rid int
    }

    conditions {        
        var role map
        role = DBFind("roles").Columns("id,role_type,creator->member_id").Where("id=?", $rid).Row()
        if !role {
            warning "Role not found"
        }

        if role["role_type"] == 3 {
            warning "System role can not be removed"
        }

        if role["creator.member_id"] != $key_id {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        var participants array
        participants = DBFind("roles_participants").Columns("id").Where("role->id=? and deleted=0", $rid)

        // delete all participants
        var i int
        while i < Len(participants) {
            var p map
            p = participants[i]
            Roles_Unassign("row_id", Int(p["id"]))
            i = i + 1
        }

        DBUpdate("roles", $rid, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract TokenTransfer {
    data {
        Amount money
        Sender_AccountId int
        Recipient_AccountId int
        Comment string "optional"
        Signature string "optional hidden"
    }

    conditions {
        //check amount
        if $Amount == 0 {
            error "Amount is zero"
        }
        if $Amount < 0 {
            error "Amount is negative"
        }

        //check the sender
        var sender string
        sender = DBFind("keys").Where("id=$", $Sender_AccountId).One("id")
        if !sender {
            error Sprintf("Sender %v is invalid", $Sender_AccountId)
        }

        //check balance
        $sender_balance = Money(DBFind("keys").Where("id=$", $Sender_AccountId).One("amount"))
        if $Amount > $sender_balance {
            error Sprintf("Money is not enough %v < %v", $sender_balance, $Amount)
        }

        //check the recipient
        var recipient string
        recipient = DBFind("keys").Where("id=$", $Recipient_AccountId).One("id")
        if !recipient {
            error Sprintf("Recipient %v is invalid", $Recipient_AccountId)
        }
    }

    action {
        DBUpdate("keys", $Sender_AccountId, "-amount", $Amount)
        DBUpdate("keys", $Recipient_AccountId, "+amount", $Amount)
        DBInsert("history", "sender_id,recipient_id,amount,comment,block_id,txhash", $Sender_AccountId, $Recipient_AccountId, $Amount, $Comment, $block, $txhash)
    }
}

contract SendTokens {
    data {
        Amount money
        Recipient_Account string
        Signature string "signature:TokenTransfer"
    }

    conditions {
        $recipient = AddressToId($Recipient_Account)
        if $recipient == 0 {
            error Sprintf("Recipient %v is invalid", $Recipient_Account)
        }
    }

    action {
        TokenTransfer("Amount,Sender_AccountId,Recipient_AccountId,Signature", $Amount, $key_id, $recipient, $Signature)
    }
}

contract catalog_ecosystems_Add {
    data {
        Ecosystem_name string
        Ecosystem_number int
        Logo string "image"
        Ecosystem_type int
        Description string
        Conditions string
        Flag_free int
        Vde_form_address string "optional"
        Vde_page string "optional"
        Web_form_address string "optional"
        Web_page string "optional"
        Email string "optional"
        Phone string "optional"
    }
    conditions {
        var FOUNDER_ACCOUNT, DELEGATE_ACCOUNT string
        FOUNDER_ACCOUNT = "founder_account"
        DELEGATE_ACCOUNT = "delegate_account"

        if Size($Email) > 0 {
            var emailParts int
            emailParts = Split($Email, "@")
            if Len(emailParts) != 2 {
                warning "Invalid email"
            }
        }

        if Size($Logo) == 0 {
            warning "Required logo"
        }
        var ecoExists map
        ecoExists = DBFind("catalog_ecosystems").Where("ecosystem_number=? and deleted=0", $Ecosystem_number).Row()
        if ecoExists {
            info "The ecosystem with same number already added"
        }

        var isFounder, isDelegate bool
        var founder, delegate map
        founder = DBFind("parameters").Where("name=?", FOUNDER_ACCOUNT).Ecosystem($Ecosystem_number).Row()
        isFounder = founder["value"] == $key_id

        delegate = DBFind("parameters").Where("name=?", DELEGATE_ACCOUNT).Ecosystem($Ecosystem_number).Row()
        if delegate {
            isDelegate = delegate["value"] == $key_id
        }
        if !(isFounder || isDelegate) {
            warning "Sorry, you can not add this ecosystem"
        }
    }

    action {
        var ecosystem_info map
        ecosystem_info["description"] = $Description
        ecosystem_info["conditions"] = $Conditions
        ecosystem_info["email"] = $Email
        ecosystem_info["phone"] = $Phone

        var url_address map
        url_address["web_page"] = $Web_page
        url_address["web_form_address"] = $Web_form_address
        url_address["vde_page"] = $Vde_page
        url_address["vde_form_address"] = $Vde_form_address

        var image_id int
        image_id = DBInsert("binaries", "data", $Logo)

        DBInsert("catalog_ecosystems", "ecosystem_name,ecosystem_number,ecosystem_type,image_id,ecosystem_info,url_address,flag_free",
        $Ecosystem_name, $Ecosystem_number, $Ecosystem_type, image_id, ecosystem_info, url_address, $Flag_free)
    }
}

contract catalog_ecosystems_Request {
    data {
