contract voting_SendNotifics {
    data {
        votingID int
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }
    conditions {
        $voting_map = DBFind("votings").Columns("id,date_ended").Where("id=$ and date_ended > now()", $votingID).One("id")
        if !$voting_map {
            warning "Voting has expired. Notifications can not be sent"
        }

        $voting_map = DBFind("votings").Columns("voting->name,creator->member_id,flags->notifics,flags,date_started").Where("id=$ and date_started < now()", $votingID).Row()
        if !$voting_map {
            warning "Voting has not yet begun. Try again later, please"
        }
        if $voting_map["creator.member_id"] != $key_id {
            warning "You are not the creator of the voting"
        }
        if $voting_map["flags.notifics"] == 1 {
            warning "Notifications have already been sent"
        }

        $voting_name = $voting_map["voting.name"]
    }

    action {
        var participants array
        participants = DBFind("votings_participants").Where("voting_id=$ and decision=$", $votingID, 0)
        var i int
        while i < Len(participants) {
            var participant, member, params map
            participant = participants[i]
            member = safeJsonToMap(participant["member"])
            params["voting_id"] = $votingID
            notifications_Send("member_id,sender,icon_name,text_header,text_body,page_name,params_map,rid,closure_type", Int(member["member_id"]), 1, "fa-check", "Voting", $voting_name, "voting_view", JSONEncode(params), 0, 0)

            i = i + 1
        }

        var flags map
        flags = safeJsonToMap($voting_map["flags"])
        flags["notifics"] = 1
        DBUpdate("votings", $votingID, "flags", flags)
    }
}

contract voting_SubjectCandidates {
    data {
        votingID int
        memberID int
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        $voting_map = DBFind("votings").Columns("id,date_started,optional,voting->type,flags->full_data").Where("id=? and date_started > now()", $votingID).Row()
        if !$voting_map {
            warning "Voting already started. Can not edit document"
        }

        if $voting_map["flags.full_data"] == 1 && $voting_map["voting.type"] == 2 {
            warning "This vote is a system. Change settings not allowed"
        }

        if $memberID == 0 {
            $memberID = $key_id
        }

        $subject_id = DBFind("votings_subject").Where("voting_id=$ and subject->member_id=$", $votingID, $memberID).One("id")
        if $subject_id {
            warning "This candidature for this role has already been added before"
        }

        $member_map = DBFind("members").Where("id = $", $memberID).Row()
        if !$member_map {
            warning "Member not found"
        }
    }

    action {

        var subject map
        subject["member_id"] = $member_map["id"]
        subject["member_name"] = $member_map["member_name"]
        subject["image_id"] = $member_map["image_id"]

        DBInsert("votings_subject", "voting_id,subject", $votingID, subject)

        var optional map
        optional = safeJsonToMap($voting_map["optional"])

        var number_candidates int
        if optional["number_candidates"]{
            number_candidates = Int(optional["number_candidates"])
            number_candidates = number_candidates + 1
        } else {
            number_candidates = 1
        }

        optional["number_candidates"] = number_candidates
        DBUpdate("votings", $votingID, "optional", optional)

        //voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_SubjectCheckFulldata {
    data {
        votingID int
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        $voting_map = DBFind("votings").Columns("id,optional,voting->type,voting->type_decision,flags").Where("id=?", $votingID).Row()
        if !$voting_map {
            warning "Voting is not found"
        }

        $type_decision = $voting_map["voting.type_decision"]
        if $type_decision < 1 || $type_decision > 5 {
            warning "invalid type of decision"
        }

        $subject_map = DBFind("votings_subject").Where("voting_id=?", $votingID).Row()
        if !$subject_map {
            warning "Subject of voting is not found"
        }
    }

    action {
        var flags, subject map
        flags = safeJsonToMap($voting_map["flags"])
        if $type_decision > 2 && $type_decision <= 5 {
            subject = safeJsonToMap($subject_map["subject"])
        }

        var fullData string

        if $type_decision == 1 || $type_decision == 2 {
            var optional map
            optional = safeJsonToMap($voting_map["optional"])

            if optional["number_candidates"] > 0 && optional["role_vacancies"] > 0 && optional["role_id"] > 0 {
                fullData = 1
            }
        }

        if $type_decision == 3 {
            if subject["text"] != "" && subject["hash"] != "" {
                fullData = 1
            }
        }

        if $type_decision == 4 {
            if $voting_map["voting.type"] == 1{ // personal
                if subject["table"] != "" && subject["table_id"] != "" && subject["column"] != "" && subject["column_value"] != "" {
                    fullData = 1
                }
            }
            if $voting_map["voting.type"] == 2 { // system
                if subject["column"] != "" && subject["column_value"] != "" {
                    fullData = 1
                }
            }
        }

        if $type_decision == 5 {
            if subject["contract_accept"] != "" && subject["contract_reject"] != "" {
                fullData = 1
            }
        }

        if fullData == 1 {
            flags["full_data"] = 1
            DBUpdate("votings", $votingID, "flags", flags)
        }
    }
}

