contract Roles_Assign {
    data {
        role_id int
        member_id int
    }

    conditions {

        $role_map = DBFind("roles_list").Where("id = $", $role_id).Row()
        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        $appointer_map = DBFind("members").Where("id = $", $key_id).Row()

        if !$role_map {
            warning "Role not found"
        }
        if !$member_map {
            warning "Member not found"
        }
        if !$appointer_map {
            warning "Appointer not found"
        }

        if(Int($role_map["delete"]) == 1) {
            warning "This role has been deleted. The appointment of members is impossible"
        }
        if((Int($role_map["role_type"]) != 1) &&(Int($role_map["role_type"]) != 4)) {
            warning "This role is not assignable. The appointment of members is impossible"
        }
        if(Int($role_map["creator_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role. The appointment of members is impossible"
        }

        $assign_id = DBFind("roles_assign").Where("role_id=$ and member_id=$ and delete=0", $role_id, $member_id).One("id")
        if($assign_id != nil) {
            warning "This member has already been added"
        }
    }

    action {
        DBInsert("roles_assign", "role_id,role_type,role_name,member_id,member_name,member_avatar,appointed_by_id,appointed_by_name,timestamp date_start,delete",
        $role_id, $role_map["role_type"], $role_map["role_name"], $member_id, $member_map["member_name"], $member_map["avatar"], $key_id, $appointer_map["member_name"], $block_time, 0)
    }
}

contract Roles_Create {
    data {
        role_name string
        role_type int
        company_id int "optional"
    }

    conditions {
        // system role
        if ($role_type == 3) {
            warning "System role can not be created"
        }

        // company role
        if ($role_type == 4) {

            // check param
            if ($company_id == 0) {
                warning "On this page you can not create a role for the company. Go to the company's view page to create a role"
            }

            // check creator of the company
            if ($company_id != $key_id) {
                warning "Sorry, you are not the creator of this company"
            }
        }

        // check member
        $member_map = DBFind("members").Where("id = $", $key_id).Row()
        if ($member_map["id"] == nil) {
            warning "You are not a member of the ecosystem"
        }
    }

    action {
        if ($role_type == 4) {
            $result = DBInsert("roles_list", "role_name,role_type,creator_id,creator_name,creator_avatar,company_id,timestamp date_create,delete",
            $role_name, $role_type, $key_id, $member_map["member_name"], $member_map["avatar"], $company_id, $block_time, 0)
        } else {
            $result = DBInsert("roles_list", "role_name,role_type,creator_id,creator_name,creator_avatar,company_id,timestamp date_create,delete",
            $role_name, $role_type, $key_id, $member_map["member_name"], $member_map["avatar"], 0, $block_time, 0)
        }
    }
}

contract Roles_Unassign {
    data {
        row_id int
    }

    conditions {
        // check record
        $assign_map = DBFind("roles_assign").Where("id=$", $row_id).Row()
        if ($assign_map["id"] == nil) {
            warning "Role not found for the specified member"
        }

        // get role
        $role_map = DBFind("roles_list").Where("id=$", Int($assign_map["role_id"])).Row()
        if ($role_map["id"] == nil) {
            warning "Role not found in the roles registry"
        }

        // check creator of the role
        if (Int($role_map["creator_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        // delete the member
        DBUpdate("roles_assign", $row_id, "delete,timestamp date_end", 1, $block_time)
    }
}

contract Roles_Del {
    data {
        role_id int
    }

    conditions {
        // check role
        $role_map = DBFind("roles_list").Where("id=$", $role_id).Row()
        if ($role_map["id"] == nil) {
            warning "Role not found"
        }

        // system role can not be deleted
        if (Int($role_map["role_type"]) == 3) {
            warning "System role can not be removed"
        }

        // check creator of the role
        if (Int($role_map["creator_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        // get the list of participants
        $ret_assign = DBFind("roles_assign").Columns("id").Where("role_id = $ and delete = $", $role_id, 0).Order("id")

        // delete all participants
        $len_assign = Len($ret_assign)
        $i_assign = 0
        while ($i_assign < $len_assign) {
            $vals_assign = $ret_assign[$i_assign]
            Roles_Unassign("row_id", Int($vals_assign["id"]))
            $i_assign = $i_assign + 1
        }

        // delete the role
        DBUpdate("roles_list", $role_id, "delete,timestamp date_delete", 1, $block_time)
    }
}

contract voting_CheckDecision {
    func stringToMap(s string) map{
        var arr, par array
        var i int
        var res map
        arr = Split(s, ";")
        while i < Len(arr){
            par = Split(arr[i], "->")
            if Len(par) == 2{
                res[par[0]] = par[1]
            }
            i=i+1
        }
        return res
    }
    data {
        votingID int
    }

    conditions {
        $voting_map = DBFind("voting_instances").Where("id=?", $votingID).Where("enddate < now() or percent_voters >= quorum").Row()
        if !$voting_map{
            warning "Voting has not expired. Try again later, please"
        }
        // if Int($voting_map["creator_id"]) != $key_id {
        // warning "Only creator the voting can check decision"
        // }

        $type_voting = Int($voting_map["type_voting"])	//system or custom
        $number_voters = Int($voting_map["number_voters"]) // number of participants who have already voted
        $optional_role_vacancies = Int($voting_map["optional_role_vacancies"]) // number of vacancies for the role
        $voting_name = $voting_map["name"]
        $quorum = Int($voting_map["quorum"])
        $optional_role_id = Int($voting_map["optional_role_id"])
        $contract_accept = $voting_map["optional_contract_accept"]
        $contract_reject = $voting_map["optional_contract_reject"]
        $param_accept = stringToMap($voting_map["contract_accept_params"])
        $param_reject = stringToMap($voting_map["contract_reject_params"])
        $flag_success = Int($voting_map["flag_success"])
        $typedecision = Int($voting_map["typedecision"])

        $subjectId = DBFind("voting_subject").Where("voting_id=$", $votingID).One("id")
        if $subjectId == nil {
            warning "Subject of voting is not found"
        }
    }

    action {
        // if notifications have been sent, then close it
        var params string
        params = "notific_param=" + Str($votingID)
        $ret_notifics = DBFind("notifications").Where("page_name=$ and page_params=$ and closed=0", "voting_view", params)
        var i int
        while(i < Len($ret_notifics)){
            $vals_notifics = $ret_notifics[i]
            Notifications_Single_Close("notific_id", Int($vals_notifics["id"]))
            i = i + 1
        }

        //if the flag of success = true(enough participants to make a decision)
        if($flag_success == 1){
            if(($typedecision == 1) ||($typedecision == 2) ){
                $number_inserts = 0 // number of candidates who have already been added(for comparison with the number of vacancies)
                $flag_decision = -1 // none of the candidates has been added yet

                $ret_subject = DBFind("voting_subject").Where("voting_id=$", $votingID).Order("number_accept DESC")
                i = 0
                while i < Len($ret_subject){
                    $vals_subject = $ret_subject[i]

                    // if there are still vacancies
                    if $number_inserts < $optional_role_vacancies {
                        // calculate the percentage to compare it with the quorum
                        $v_percent = Int($vals_subject["number_accept"]) * 100 / $number_voters
                        // if percentage >= quorum values, then this candidate must be added to the role
                        if $v_percent >= $quorum {
                            // get role name
                            $role_name = DBFind("roles_list").Where("id=$", $optional_role_id).One("role_name")
                            if $role_name != nil {
                                // add a candidate to the role
                                DBInsert("roles_assign", "role_id,role_name,member_id,member_name,member_avatar,timestamp date_start,appointed_by_id,appointed_by_name",$optional_role_id, string($role_name), Int($vals_subject["member_id"]), string($vals_subject["member_name"]), string($vals_subject["member_avatar"]), $block_time, $votingID, $voting_name)

                                // increment of the counter of candidates
                                $number_inserts = $number_inserts + 1
                                // if at least one of the candidates was added to the role, then the voting decision is considered successful
                                $flag_decision = 1
                            }
                        }
                    }
                    i = i + 1
                }

                DBUpdate("voting_instances", $votingID, "flag_decision", $flag_decision)

                // starting optional contract
                if($flag_decision == 1){
                    if($contract_accept != ""){
                        CallContract($contract_accept, $param_accept)
                    }
                }
                else{
                    if($contract_reject != ""){
                        CallContract($contract_reject, $param_reject)
                    }
                }
            }

            if($typedecision == 3){
                $number_accept = DBFind("voting_subject").Where("voting_id=$", $votingID).One("number_accept")
                if($number_accept != nil){
                    // calculate the percentage to compare it with the quorum
                    $v_percent = Int($number_accept) * 100 / $number_voters

                    // if percentage >= quorum values, then this document will be approved
                    if($v_percent >= $quorum){
                        $flag_decision = 1
                    }
                    // otherwise the document will be rejected
                    else{
                        $flag_decision = -1
                    }

                    DBUpdate("voting_instances", $votingID, "flag_decision", $flag_decision)

                    // starting optional contract
                    if($flag_decision == 1){
                        if($contract_accept != ""){
                            CallContract($contract_accept, $param_accept)
                        }
                    }
                    else{
                        if($contract_reject != ""){
                            CallContract($contract_reject, $param_reject)
                        }
                    }
                }
            }

            if($typedecision == 4){
                $subject_map = DBFind("voting_subject").Where("voting_id=$", $votingID).Row()
                if($subject_map["id"] != nil){
                    $number_accept = Int($subject_map["number_accept"])

                    // calculate the percentage to compare it with the quorum
                    $v_percent = $number_accept * 100 / $number_voters

                    // if percentage >= quorum values, then this document will be approved
                    if($v_percent >= $quorum){
                        $flag_decision = 1

                        $decisionTable = $subject_map["formal_decision_table"]
                        $decisionId = Int($subject_map["formal_decision_tableid"])
                        $decisionColumn =$subject_map["formal_decision_column"]
                        $decisionValue = $subject_map["formal_decision_colvalue"]

                        // type of voting - custom
                        if($type_voting == 1){
                            // ALWAYS CHECK THE PERMISSION
                            DBUpdate($decisionTable, $decisionId, $decisionColumn, $decisionValue)
                        }

                        // type of voting - system
                        if($type_voting == 2){
                            DBUpdateSysParam($decisionColumn, $decisionValue, "")
                        }
                    }
                    // otherwise the document will be rejected
                    else{
                        $flag_decision = -1
                    }

                    DBUpdate("voting_instances", $votingID, "flag_decision", $flag_decision)

                    // starting optional contract
                    if($flag_decision == 1){
                        if($contract_accept != ""){
                            CallContract($contract_accept, $param_accept)
                        }
                    }
                    else{
                        if($contract_reject != ""){
                            CallContract($contract_reject, $param_reject)
                        }
                    }
                }
            }

            if($typedecision == 5){
                $subject_map = DBFind("voting_subject").Where("voting_id=$", $votingID).Row()
                if($subject_map["id"] != nil){
                    $number_accept = Int($subject_map["number_accept"])

                    // calculate the percentage to compare it with the quorum
                    $v_percent = $number_accept * 100 / $number_voters

                    $con_accept = $subject_map["contract_accept"]
                    $con_accept = $subject_map["contract_accept"]
                    $param_accept = stringToMap($subject_map["contract_accept_params"])
                    $param_reject = stringToMap($subject_map["contract_reject_params"])

                    // if percentage >= quorum values, then this document will be approved
                    if($v_percent >= $quorum){
                        $flag_decision = 1

                        CallContract($con_accept, $param_accept)
                    }
                    // otherwise the document will be rejected
                    else{
                        $flag_decision = -1

                        CallContract($con_reject, $param_reject)
                    }
                }

                DBUpdate("voting_instances", $votingID, "flag_decision", $flag_decision)
            }
        }
        // decision can not be made(not enough participants to make a decision)
        else {
            DBUpdate("voting_instances", $votingID, "flag_decision", -2)
        }
    }
}

contract voting_UpdateDataForGraphs {
    data {
        votingID int
    }

    conditions {
        $voting_map = DBFind("voting_instances").Where("id=$", $votingID).Row()
        if ($voting_map["id"] == nil){
            warning "Voting is not found"
        }

        $number_participants = Int($voting_map["number_participants"]) // number of participants
        $number_voters = Int($voting_map["number_voters"]) // number of voters
        $percent_voters = Int($voting_map["percent_voters"]) 
        $volume = Int($voting_map["volume"]) // percentage of votes for this vote
        $quorum = Int($voting_map["quorum"]) 
    }

    action {
        // increment count of voters
        $number_voters = $number_voters + 1

        // calculate the percentage of the ratio of the number of voters to the number of participants (for the graph)
        $percent_voters = ($number_voters * 100) / $number_participants
        if ($percent_voters > 100) {$percent_voters = 100}

        // update progress
        $percent_success = ($percent_voters * 100) / $volume
        if ($percent_success > 100) {$percent_success = 100}

        // update the flag of success
        $flag_success = 0
        if ($percent_success == 100) {$flag_success = 1}

        DBUpdate("voting_instances", $votingID, "number_voters,percent_voters,percent_success,flag_success",
        $number_voters, $percent_voters, $percent_success, $flag_success)

		if $percent_voters >= $quorum{
			voting_CheckDecision("votingID", $votingID)
		}
    }
}

contract voting_AcceptCandidates {
    data {
        votingID int
        candidateID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=$", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting is not found"
		}
		$subject_map = DBFind("voting_subject").Where("voting_id=$ and member_id=$", $votingID, $candidateID).Row()
		if ($subject_map["id"] == nil){
			warning "Subject of voting is not found"
		}

		// check the participant of the voting
		$participant_id = DBFind("voting_participants").Where("voting_id=$ and member_id=$", $votingID, $key_id).One("id")
		if ($participant_id == nil){
			warning "You are not a participant in this voting"
		}
    }

    action {
		// if notifications have been sent, then close it
		if ( Int($voting_map["flag_notifics"]) == 1 ){
			var params string
			params = "notific_param=" + Str($votingID)		
			$notific_id = DBFind("notifications").Where("recipient_id=$ and page_name=$ and page_params=$ and closed=$", $key_id, "voting_view", params, 0).One("id")
			if ($notific_id != nil){
				Notifications_Single_Close("notific_id", Int($notific_id))
			}
		}

		// check the decision
		if ( Int($voting_map["flag_decision"]) == 1 ){
			warning "Decision was taken. Voting is now not possible"
		} 

		// check start-end of voting time
		$voting_start = DBFind("voting_instances").Where("id=$ and startdate < now()", $votingID).One("id")
		$voting_end   = DBFind("voting_instances").Where("id=$ and enddate   > now()", $votingID).One("id")
		if ($voting_start == nil){
			warning "Voting has not yet begun. Try again later, please"
		}
		if ($voting_end == nil){
			warning "Voting has expired. Voting is now not possible"
		}

		//-----------------------------------------------------------------------------------------------

		// write the result of the voting
		DBUpdate("voting_participants", Int($participant_id), "timestamp decision_date, decision", $block_time, $candidateID)

		// increment count of accept
		$subject_id     = Int($subject_map["id"])
		$number_accept  = Int($subject_map["number_accept"])
		$number_accept   = $number_accept + 1
		DBUpdate("voting_subject", $subject_id, "number_accept", $number_accept)

		voting_UpdateDataForGraphs("votingID", $votingID)
    }
}

contract voting_Invite {
    data {
        votingID int
        var_id int      //0, member_id, role_id
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=$ and enddate > now()", $votingID).Row()	
		if ($voting_map["id"] == nil){
			warning "Voting has expired. Can not add new participants"
		}

		$type_participants = Int($voting_map["typeparticipants"])
		$number_participants = Int($voting_map["number_participants"])

		if (($number_participants > 1) && (Int($voting_map["type_voting"]) == 2)){
			warning "This vote is a system. Add new participants are not possible"
		}

		if ($type_participants == 2){       //list of candidates
			$participant_id = DBFind("voting_participants").Where("voting_id=$ and member_id=$", $votingID, $var_id).One("id")
			if ($participant_id != nil){		
				warning "Member has already been added before"
			}
		}

		if ($type_participants == 3){       //list of candidates
			$participant_id = DBFind("voting_participants").Where("voting_id=$", $votingID).One("id")
			if ($participant_id != nil){		
				warning "Voting participants have already been added before"
			}
		}
    }

    action {
		if ($type_participants == 1){
			$ret_member = DBFind("members").Where("id != $", 0).Order("id")
			$i = 0
			while($i < Len($ret_member)){
				$vals_member = $ret_member[$i]

				DBInsert("voting_participants", "voting_id, member_id, member_name, member_avatar, decision", $votingID, $vals_member["id"], $vals_member["member_name"], $vals_member["avatar"], 0)  
				$number_participants = $number_participants + 1
				$i = $i + 1
			} 
		}

		if ($type_participants == 2){
			$member_map = DBFind("members").Where("id = $", $var_id).Row()
			if ($member_map["id"] != nil){
				DBInsert("voting_participants", "voting_id, member_id, member_name, member_avatar, decision", $votingID, $var_id, $member_map["member_name"], $member_map["avatar"], 0)  
				$number_participants = $number_participants + 1
			}
		}

		if ($type_participants == 3){
			$ret_member = DBFind("roles_assign").Where("role_id=$ and delete=0", $var_id).Order("id")
			$i = 0
			while($i < Len($ret_member)){
				$vals_member = $ret_member[$i]

				DBInsert("voting_participants", "voting_id, member_id, member_name, member_avatar, decision", $votingID, $vals_member["member_id"], $vals_member["member_name"], $vals_member["member_avatar"], 0)  
				$number_participants = $number_participants + 1
				$i = $i + 1
			} 
		}

		DBUpdate("voting_instances", $votingID, "number_participants", $number_participants)
    }
}

contract voting_Delete {
    data {
        votingID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=? and startdate > now()", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting already started. Delete voting impossible"
		}	

		if (Int($voting_map["creator_id"]) != $key_id){
			warning "You are not the creator of the voting"
		}
    }

    action {
		DBUpdate("voting_instances", $votingID, "delete", 1)
    }
}

contract voting_CreateNew {
    data {
        voting_name string
        type_voting int
        description string
        type_participants int
        type_decision int
        now_date string "date"
        start_time string "date"
        start_date string "date"
        end_date string "date"
        end_time string "date"
        volume int
        quorum int
    }

    func conditions {
        $start_date = $start_date + " "+ $start_time
        $end_date = $end_date + " "+ $end_time
        var m string

        if ($now_date >= $start_date){
            m = Sprintf("Voting start date [%v] is less than the current date [%v]", $start_date, $now_date)
            warning m
        }

        if ($start_date >= $end_date){
            m = Sprintf("Voting end date [%v] is less than the start date [%v]",$end_date, $start_date)
            warning m
        }

        if (($volume < 50) || ($volume > 100) ){
            m = Sprintf("Volume [%v] should be in the range from 50 to 100", $volume)
            warning m
        }

        if (($quorum < 5) || ($quorum > 100) ){
            m = Sprintf("Quorum [%v] should be in the range from 5 to 100", $quorum)
            warning m
        }

        $member_map = DBFind("members").Where("id=$", $key_id).Row()
        if !$member_map {
            warning "You are not a member of the ecosystem"
        }
    }

    func action {
        $voting_id = DBInsert("voting_instances", "name,type_voting,description,typeparticipants,typedecision,startdate,enddate,volume,quorum,creator_id,creator_name,creator_avatar,flag_success,percent_success,number_participants,number_voters,flag_decision,flag_notifics,delete",
        $voting_name, $type_voting, $description, $type_participants, $type_decision, $start_date, $end_date, $volume, $quorum, $key_id, $member_map["member_name"], $member_map["avatar"], 0, 0, 0, 0, 0, 0, 0)

        // if all members are voting participants
        if ($type_participants==1){
            voting_Invite("votingID,var_id", $voting_id, 0)
        }
    }
}

contract voting_AcceptDecision {
    data {
        votingID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=$", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting is not found"
		}
		$subject_map = DBFind("voting_subject").Where("voting_id=$", $votingID).Row()
		if ($subject_map["id"] == nil){
			warning "Subject of voting is not found"
		}

		// check the participant of the voting
		$participant_id = DBFind("voting_participants").Where("voting_id=$ and member_id=$", $votingID, $key_id).One("id")
		if ($participant_id == nil){
			warning "You are not a participant in this voting"
		}
    }

    action {
		// if notifications have been sent, then close it
		if ( Int($voting_map["flag_notifics"]) == 1 ){
			var params string
			params = "notific_param=" + Str($votingID)	
			$notific_id = DBFind("notifications").Where("recipient_id=$ and page_name=$ and page_params=$ and closed=$", $key_id, "voting_view", params, 0).One("id")
			if ($notific_id != nil){
				Notifications_Single_Close("notific_id", Int($notific_id))
			}
		}

		// check the decision
		if ( Int($voting_map["flag_decision"]) == 1 ){
			warning "Decision was taken. Voting is now not possible"
		} 

		// check start-end of voting time
		$voting_start = DBFind("voting_instances").Where("id=$ and startdate < now()", $votingID).One("id")
		$voting_end   = DBFind("voting_instances").Where("id=$ and enddate   > now()", $votingID).One("id")
		if ($voting_start == nil){
			warning "Voting has not yet begun. Try again later, please"
		}
		if ($voting_end == nil){
			warning "Voting has expired. Voting is now not possible"
		}

		//-----------------------------------------------------------------------------------------------

		// write the result of the voting
		DBUpdate("voting_participants", Int($participant_id), "timestamp decision_date, decision", $block_time, 1)

		// increment count of accept
		$subject_id     = Int($subject_map["id"])
		$number_accept  = Int($subject_map["number_accept"])
		$number_accept   = $number_accept + 1
		DBUpdate("voting_subject", $subject_id, "number_accept", $number_accept)

		voting_UpdateDataForGraphs("votingID", $votingID)
    }
}

contract voting_SendNotifics {
    data {
        votingID int
    }

    conditions {
		$voting_id = DBFind("voting_instances").Where("id=$ and enddate > now()", $votingID).One("id")
        if ($voting_id == nil){
			warning "Voting has expired. Notifications can not be sent"
		}

		$voting_map = DBFind("voting_instances").Where("id=$ and startdate < now()", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting has not yet begun. Try again later, please"
		}	

		if( Int($voting_map["creator_id"]) != $key_id ){
			warning "You are not the creator of the voting"
		}
		if( Int($voting_map["flag_notifics"]) == 1 ){
			warning "Notifications have already been sent"
		}

		$voting_name = string($voting_map["name"])
    }

    action {
		$ret_participants = DBFind("voting_participants").Where("voting_id=$ and decision=$", $votingID, 0)
		$i = 0
		while($i < Len($ret_participants)){
			$vals_participants = $ret_participants[$i]
			$recipient = Int($vals_participants["member_id"])

			var params string
			params = "notific_param=" + Str($votingID)

			Notifications_Single_Send("member_id,icon_name,text_header,text_body,page_name,params_val", 
								$recipient, "fa-check", "Voting", $voting_name, "voting_view", params)
			$i = $i + 1
		}

		DBUpdate("voting_instances", $votingID, "flag_notifics", 1)
    }
}

contract voting_SubjectCheckFulldata {
    data {
        votingID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=?", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting is not found"
		}	

		$subject_map = DBFind("voting_subject").Where("voting_id=?", $votingID).Row()
		if ($subject_map["id"] == nil){
			warning "Subject of voting is not found"
		}

		$typedecision = Int($voting_map["typedecision"])
    }

    action {
		if ( ($typedecision == 1) || ($typedecision == 2) ){
			if ( (Int($voting_map["optional_number_cands"]) > 0) && (Int($voting_map["optional_role_vacancies"]) > 0) && (Int($voting_map["optional_role_id"])  > 0) ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}

		if ($typedecision == 3){
			if ( (string($subject_map["text_document"]) != "") && (string($subject_map["text_doc_hash"]) != "") ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}

		if ($typedecision == 4){
			if ( (string($subject_map["formal_decision_description"]) != "") && (string($subject_map["formal_decision_table"])  != "") && 
				 (string($subject_map["formal_decision_tableid"])     != "") && (string($subject_map["formal_decision_column"]) != "") && 
				 (string($subject_map["formal_decision_colvalue"])    != "") ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}

		if ($typedecision == 5){
			if ( (string($subject_map["contract_accept"]) != "") && (string($subject_map["contract_reject"]) != "") ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}
    }
}

contract voting_SubjectCandidates {
    data {
        votingID int
        memberID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=$ and startdate > now()", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting already started. New candidate can not be added"
		}		

		if ( (Int($voting_map["flag_fulldata"]) == 1) && (Int($voting_map["type_voting"]) == 2) ){
			warning "This vote is a system. Change settings not allowed"
		}

        if($memberID == 0){
            $memberID = $key_id
        }

		$subject_id = DBFind("voting_subject").Where("voting_id=$ and member_id=$", $votingID, $memberID).One("id")
		if ($subject_id != nil){
			warning "This candidature for this role has already been added before"
		}

		$member_map = DBFind("members").Where("id = $", $memberID).Row()
		if ($member_map["id"] == nil){
			warning "Member not found"
		}

		$optional_number_cands = Int($voting_map["optional_number_cands"])
    }

    action {
		DBInsert("voting_subject", "member_id,member_name,member_avatar,voting_id", $memberID, $member_map["member_name"], $member_map["avatar"], $votingID)

		$optional_number_cands = $optional_number_cands + 1
		DBUpdate("voting_instances", $votingID, "optional_number_cands", $optional_number_cands)

		voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_SubjectContract {
  data {
    votingID int

    contract_reject string
    contract_accept string
    contract_reject_params string "optional"
    contract_accept_params string "optional"
  }

  conditions {
    $voting_map = DBFind("voting_instances").Where("id=? and startdate > now()", $votingID).Row()
    if ($voting_map["id"] == nil){
      warning "Voting already started. Can not edit document"
    }
    if ((Int($voting_map["flag_fulldata"]) == 1) && (Int($voting_map["type_voting"]) == 2) ){
      warning "This vote is a system. Change settings not allowed"
    }
  }

  action {
    $subject_id = DBFind("voting_subject").Where("voting_id = $", $votingID).One("id")
    if ($subject_id != nil){
      DBUpdate(`voting_subject`, Int($subject_id), `contract_accept,contract_reject,contract_accept_params,contract_reject_params`, $contract_accept, $contract_reject, $contract_accept_params, $contract_reject_params)
    } else {
      DBInsert(`voting_subject`, `voting_id,contract_accept,contract_reject,contract_accept_params,contract_reject_params`, $votingID, $contract_accept, $contract_reject, $contract_accept_params, $contract_reject_params)
    }

    voting_SubjectCheckFulldata("votingID", $votingID)
  }
}

contract voting_SubjectDocument {
    data {
        votingID int

        text_document string

        contract_reject string "optional"
        contract_accept string "optional"
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=? and startdate > now()", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting already started. Can not edit document"
		}		
		if ( (Int($voting_map["flag_fulldata"]) == 1) && (Int($voting_map["type_voting"]) == 2) ){
			warning "This vote is a system. Change settings not allowed"
		}	

		$hash = Sha256($text_document)
    }

    action {
		$subject_id = DBFind("voting_subject").Where("voting_id = $", $votingID).One("id")
		if ($subject_id != nil){
			DBUpdate("voting_subject", Int($subject_id), "text_document, text_doc_hash", $text_document, $hash)
		} else {
			DBInsert("voting_subject", "text_document, text_doc_hash, voting_id", $text_document, $hash, $votingID)
		}

		DBUpdate("voting_instances", $votingID, "optional_contract_accept,optional_contract_reject", $contract_accept, $contract_reject)
		voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_SubjectFormal {
    data {
        votingID int

        description string
        table_to_written string
        row_id string
        column_name string
        written_value string

        contract_reject string "optional"
        contract_accept string "optional"
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=? and startdate > now()", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting already started. Can not edit document"
		}		
		if ( (Int($voting_map["flag_fulldata"]) == 1) && (Int($voting_map["type_voting"]) == 2) ){
			warning "This vote is a system. Change settings not allowed"
		}
    }

    action {
		$subject_id = DBFind("voting_subject").Where("voting_id = $", $votingID).One("id")
		if ($subject_id != nil){
			DBUpdate("voting_subject", Int($subject_id), "formal_decision_description,formal_decision_table,formal_decision_tableid,formal_decision_column,formal_decision_colvalue", 
				$description, $table_to_written, $row_id, $column_name, $written_value)
		} else {
			DBInsert("voting_subject", "voting_id,formal_decision_description,formal_decision_table,formal_decision_tableid,formal_decision_column,formal_decision_colvalue", 
				$votingID, $description, $table_to_written, $row_id, $column_name, $written_value)
		}

		DBUpdate("voting_instances", $votingID, "optional_contract_accept,optional_contract_reject", $contract_accept, $contract_reject)
		voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_SubjectSettings {
    data {
        votingID int

        roleID int
        vacancies int

        contract_reject string "optional"
        contract_accept string "optional"
        accept_params string "optional"
        reject_params string "optional"
    }

    conditions {
        if($vacancies < 1){
            warning "Number of vacancies can not be less than 1"
        }

        $voting_map = DBFind("voting_instances").Where("id=? and startdate > now()", $votingID).Row()
        if($voting_map["id"] == nil){
            warning "Voting already started. Change settings not allowed"
        }
        if((Int($voting_map["flag_fulldata"]) == 1) &&(Int($voting_map["type_voting"]) == 2) ){
            warning "This vote is a system. Change settings not allowed"
        }

        $role_map = DBFind("roles_list").Where("id=? and delete=0", $roleID).Row()
        if($role_map["id"] == nil){
            warning "Specified role not found"
        }
        // not elective or not "Candidate for validators"
        // roles_types: 1. assigned_role, 2. elective_role, 3. system_role, 4. company_role
        if !($role_map["role_type"] == 2 || $role_map["role_type"] == 3) {
            warning "The chosen role is not elective or has been removed"
        }
    }

    action {
        DBUpdate("voting_instances", $votingID, "optional_role_id,optional_role_vacancies,optional_contract_accept,optional_contract_reject,contract_accept_params,contract_reject_params",
        $roleID, $vacancies, $contract_accept, $contract_reject, $accept_params, $reject_params)

        voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_RejectDecision {
    data {
