contract Roles_Assign {
    data {
        role_id int
        member_id int
    }

    conditions {
        $role_map = DBFind("roles").Where("id = $", $role_id).Row()
        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        $appointer_map = DBFind("members").Where("id = $", $key_id).Row()

        if !$role_map {
            warning "Role not found"
        }
        if !$member_map {
            warning "Member not found"
        }
        if !$appointer_map {
            warning "Appointer not found"
        }

        if (Int($role_map["deleted"]) == 1) {
            warning "This role has been deleted. The appointment of members is impossible"
        }

        if ((Int($role_map["role_type"]) != 1) && (Int($role_map["role_type"]) != 4)) {
            warning "This role is not assignable. The appointment of members is impossible"
        }

        // get JSON
        var creator_json string
        creator_json = Str($role_map["creator"])

        // get MAP
        var creator map
        creator = JSONToMap(creator_json)

        // check creator of the role
        if (Int(creator["member_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role. The appointment of members is impossible"
        }

        $assign_id = DBFind("roles_participants").Where("role->id=$ and member->member_id=$ and deleted=0", $role_id, $member_id).One("id")
        if $assign_id {
            warning "This member has already been added"
        }
    }

    action {
        var role map
        role["id"] = Str($role_map["id"])
        role["type"] = Str($role_map["role_type"])
        role["name"] = Str($role_map["role_name"])
        role["image_id"] = Str($role_map["image_id"])

        var member map
        member["member_id"] = Str($member_map["id"])
        member["member_name"] = Str($member_map["member_name"])
        member["image_id"] = Str($member_map["image_id"])

        var appointed map
        appointed["member_id"] = Str($appointer_map["id"])
        appointed["member_name"] = Str($appointer_map["member_name"])
        appointed["image_id"] = Str($appointer_map["image_id"])

        DBInsert("roles_participants", "role,member,appointed,timestamp date_created", role, member, appointed, $block_time)
    }
}

contract Roles_Create {
    data {
        role_name string
        role_type int
        icon string "image"
        company_id int "optional"
    }

    conditions {
        // check icon
        if (Size($icon) == 0) {
            warning "Icon was not received"
        }

        // system role
        if ($role_type == 3) {
            warning "System role can not be created"
        }

        // company role
        if ($role_type == 4) {

            // check param
            if ($company_id == 0) {
                warning "On this page you can not create a role for the company. Go to the company's view page to create a role"
            }

            // check creator of the company
            if ($company_id != $key_id) {
                warning "Sorry, you are not the creator of this company"
            }
        }

        // check member
        $member_map = DBFind("members").Where("id = $", $key_id).Row()
        if !$member_map {
            warning "You are not a member of the ecosystem"
        }
    }

    action {
        // new JSON
        var creator map
        creator["member_id"] = Str($member_map["id"])
        creator["member_name"] = Str($member_map["member_name"])
        creator["image_id"] = Str($member_map["image_id"])

        // new icon
        var image_id int
        image_id = DBInsert("binary_data", "binary_data", $icon)

        // insert role
        if ($role_type == 4) {
            $result = DBInsert("roles", "role_name,role_type,image_id,creator,timestamp date_created,company_id",
                $role_name, $role_type, image_id, creator, $block_time, $company_id)
        } else {
            $result = DBInsert("roles", "role_name,role_type,image_id,creator,timestamp date_created,company_id",
                $role_name, $role_type, image_id, creator, $block_time, 0)
        }
    }
}

contract Roles_Unassign {
    data {
        row_id int
    }

    conditions {
        // check record
        $assign_map = DBFind("roles_participants").Where("id=$", $row_id).Row()
        if !$assign_map {
            warning "Participant not found"
        }

        // check role
        var role_json string
        role_json = Str($assign_map["role"])
        var role map
        role = JSONToMap(role_json)

        $role_map = DBFind("roles").Where("id=$", Int(role["id"])).Row()
        if !$role_map["id"] {
            warning "Role not found in the roles registry"
        }

        // check creator of the role
        var creator_json string
        creator_json = Str($role_map["creator"])
        var creator map
        creator = JSONToMap(creator_json)

        if (Int(creator["member_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        // delete the member
        DBUpdate("roles_participants", $row_id, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract Roles_Del {
    data {
        role_id int
    }

    conditions {

        // check role
        $role_map = DBFind("roles").Where("id=$", $role_id).Row()
        if !$role_map {
            warning "Role not found"
        }

        // system role can not be deleted
        if (Int($role_map["role_type"]) == 3) {
            warning "System role can not be removed"
        }

        // get JSON
        var creator_json string
        creator_json = Str($role_map["creator"])

        // get MAP
        var creator map
        creator = JSONToMap(creator_json)

        // check creator of the role
        if (Int(creator["member_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        // get the list of participants
        $ret_assign = DBFind("roles_participants").Columns("id").Where("role->id = $ and deleted = $", $role_id, 0).Order("id")

        // delete all participants
        $len_assign = Len($ret_assign)
        $i_assign = 0
        while ($i_assign < $len_assign) {
            $vals_assign = $ret_assign[$i_assign]
            Roles_Unassign("row_id", Int($vals_assign["id"]))
            $i_assign = $i_assign + 1
        }

        // delete the role
        DBUpdate("roles", $role_id, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract TokenTransfer {
    data {
        Amount money
        Sender_AccountId int
        Recipient_AccountId int
		Comment string "optional"
        Signature string "optional hidden"
    }

    conditions {
        //check amount
        if ($Amount == 0){
            error "Amount is zero"
        }		
	
        //check the sender
        $sender = DBFind("keys").Where("id=$", $Sender_AccountId).One("id")
        if ($sender == nil){
            error Sprintf("Sender %v is invalid", $Sender_AccountId)
        }
		
        //check balance
		$sender_balance = Money(DBFind("keys").Where("id=$", $Sender_AccountId).One("amount"))
        if ($Amount > $sender_balance){
            error Sprintf("Money is not enough %v < %v", $sender_balance, $Amount)
        }
		
        //check the recipient
		$recipient = DBFind("keys").Where("id=$", $Recipient_AccountId).One("id")
		if ($recipient == nil){
            error Sprintf("Recipient %v is invalid", $Recipient_AccountId)
        }
    }

    action {
        DBUpdate("keys", $Sender_AccountId, "-amount", $Amount)
        DBUpdate("keys", $Recipient_AccountId, "+amount", $Amount)
		DBInsert("history", "sender_id,recipient_id,amount,comment,block_id,txhash", $Sender_AccountId, $Recipient_AccountId, $Amount, $Comment, $block, $txhash)
    }
}

contract SendTokens {
    data {
        Amount money
        Recipient_Account string
        Signature string "signature:TokenTransfer"
    }

    conditions {
        $recipient = AddressToId($Recipient_Account)
        if $recipient == 0 {
            error Sprintf("Recipient %v is invalid", $Recipient_Account)
        }
    }

    action {
        TokenTransfer("Amount,Sender_AccountId,Recipient_AccountId,Signature", $Amount, $key_id, $recipient, $Signature)
    }
}

contract catalog_ecosystems_Add {
    data {
        Ecosystem_name string
        Ecosystem_number int
        Logo string "image"
        Ecosystem_type int
        Description string
        Conditions string
        Flag_free int
        Vde_form_address string "optional"
        Vde_page string "optional"
        Web_form_address string "optional"
        Web_page string "optional"
        Email string "optional"
        Phone string "optional"
    }
    conditions {

        $founderParam = "founder_account"
        $delegateParam = "delegate_account"
        $catalogTbl = "catalog_ecosystems"
        $meFounder = false
        $meDelegate = false

        if Size($Email) > 0 {
            if !Contains($Email, "@") {
                warning "Invalid email"
            }
        }

        if Size($Logo) == 0 {
            warning "Required logo"
        }

        $ecoExists = DBFind($catalogTbl).Where("ecosystem_number=? and deleted=0", $Ecosystem_number).Row()
        if $ecoExists {
            info "The ecosystem with same number already added"
        }

        $founder = DBFind("parameters").Where("name=?", $founderParam).Ecosystem($Ecosystem_number).Row()
        $meFounder = $founder["value"] == $key_id

        $delegate = DBFind("parameters").Where("name=?", $delegateParam).Ecosystem($Ecosystem_number).Row()
        if $delegate {
            $meDelegate = $delegate["value"] == $key_id
        }
        if !($meFounder || $meDelegate) {
            warning "Sorry, you can not add this ecosystem"
        }
    }

    action {
        var ecosystem_info map
        ecosystem_info["description"] = Str($Description)
        ecosystem_info["conditions"] = Str($Conditions)
        ecosystem_info["email"] = Str($Email)
        ecosystem_info["phone"] = Str($Phone)

        var url_address map
        url_address["web_page"] = Str($Web_page)
        url_address["web_form_address"] = Str($Web_form_address)
        url_address["vde_page"] = Str($Vde_page)
        url_address["vde_form_address"] = Str($Vde_form_address)

        var image_id int
        image_id = DBInsert("binary_data", "binary_data", $Logo)

        DBInsert($catalogTbl, "ecosystem_name,ecosystem_number,ecosystem_type,image_id,ecosystem_info,url_address,flag_free",
            $Ecosystem_name, $Ecosystem_number, $Ecosystem_type, image_id, ecosystem_info, url_address, $Flag_free)
    }
}

contract catalog_ecosystems_Request {
    data {
        ecosystem_num int
        pub_key string
        comment string "optional" 
    }

    conditions {
        if($ecosystem_num <= 1){
            warning "Ecosystem number was sent earlier"
        }
        $table_name = Sprintf("%v_membership_requests", $ecosystem_num)

        $request_id = DBFind($table_name).Where("member_id = $", $key_id).One("id")
        if ($request_id != nil) {
            warning "Request was sent earlier"
        }

        if(Size($pub_key) == 0){
            warning "Public key was not received"
        }
    }

    action {
        DBInsert($table_name, "member_id,pub_key,comment", $key_id, $pub_key, $comment)
    }
}

contract voting_UpdateDataForGraphs {
    data {
        votingID int
    }

    conditions {
        $voting_map = DBFind("votings").Where("id=?", $votingID).Row()
        if !$voting_map {
            warning "Voting is not found"
        }
    }

    action {
        // get JSON
        var progress_json string
        progress_json = Str($voting_map["progress"])

        // get MAP
        var progress map
        progress = JSONToMap(progress_json)

        $number_participants = Int(progress["number_participants"]) // number of participants
        $number_voters = Int(progress["number_voters"]) // number of voters
        $percent_voters = Int(progress["percent_voters"])

        // get JSON
        var voting_json string
        voting_json = Str($voting_map["voting"])

        // get MAP
        var voting map
        voting = JSONToMap(voting_json)

        $volume = Int(voting["volume"])
        $quorum = Int(voting["quorum"])

        // get JSON
        var flags_json string
        flags_json = Str($voting_map["flags"])

        // get MAP
        var flags map
        flags = JSONToMap(flags_json)

        //-----------------------------------------------------------------------------------

        // increment count of voters
        $number_voters = $number_voters + 1

        // calculate the percentage of the ratio of the number of voters to the number of participants (for the graph)
        $percent_voters = ($number_voters * 100) / $number_participants
        if ($percent_voters > 100) {
            $percent_voters = 100
        }

        // update progress
        $percent_success = ($percent_voters * 100) / $volume
        if ($percent_success > 100) {
            $percent_success = 100
        }

        // update the flag of success
        $flag_success = 0
        if ($percent_success == 100) {
            $flag_success = 1
        }

        progress["percent_success"] = Str($percent_success)
        progress["number_voters"] = Str($number_voters)
        progress["percent_voters"] = Str($percent_voters)

        flags["success"] = Str($flag_success)

        DBUpdate("votings", $votingID, "progress,flags", progress, flags)

        //if ($percent_voters >= $quorum) {
        //    voting_CheckDecision("votingID", $votingID)
        //}
    }
}

contract voting_AcceptCandidates {
    data {
        votingID int
        candidateID int
    }

    conditions {
        $voting_map = DBFind("votings").Where("id=$", $votingID).Row()
        if !$voting_map {
            warning "Voting is not found"
        }

        $subject_map = DBFind("votings_subject").Where("voting_id=$ and subject->member_id=$", $votingID, $candidateID).Row()
        if !$subject_map {
            warning "Subject of voting is not found"
        }

        $participant_id = DBFind("votings_participants").Where("voting_id=$ and member->member_id=$", $votingID, $key_id).One("id")
        if !$participant_id {
            warning "You are not a participant in this voting"
        }
    }

    action {
        // get JSON
        var flags_json string
        flags_json = Str($voting_map["flags"])

        // get MAP
        var flags map
        flags = JSONToMap(flags_json)

        // if notifications have been sent, then close it
        if (Int(flags["notifics"]) == 1) {

            $notific_id = DBFind("notifications").Where("recipient->member_id=$ and page_name=$ and page_params->voting_id=$ and closed=$",
                $key_id, "voting_view", $votingID, 0).One("id")

            if $notific_id {
                Notifications_Single_Close("notific_id", Int($notific_id))
            }
        }

        // check the decision
        if (Int(flags["decision"]) == 1) {
            warning "Decision was taken. Voting is now not possible"
        }

        // check start-end of voting time
        $voting_start = DBFind("votings").Where("id=$ and date_started < now()", $votingID).One("id")
        $voting_end = DBFind("votings").Where("id=$ and date_ended > now()", $votingID).One("id")
        if !$voting_start {
            warning "Voting has not yet begun. Try again later, please"
        }
        if !$voting_end {
            warning "Voting has expired. Voting is now not possible"
        }

        //-----------------------------------------------------------------------------------------------

        // write the result of the voting
        DBUpdate("votings_participants", Int($participant_id), "timestamp decision_date,decision", $block_time, $candidateID)

        // increment count of accept
        $number_accept = Int($subject_map["number_accept"])
        $number_accept = $number_accept + 1
        DBUpdate("votings_subject", Int($subject_map["id"]), "number_accept", $number_accept)

        voting_UpdateDataForGraphs("votingID", $votingID)
    }
}

contract voting_CheckDecision {
    data {
        votingID int
    }

    conditions {
        $voting_map = DBFind("votings").Columns("voting->name,voting->type,voting->quorum,voting->type_decision,flags->success,progress->number_voters,optional->role_id,optional->role_vacancies,optional->contract_accept,optional->contract_reject,optional->contract_accept_params,optional->contract_reject_params").Where("id=? and (date_ended < now() or progress->percent_voters >= voting->quorum)", $votingID).Row()
        if !$voting_map {
            warning "Voting has not expired. Try again later, please"
        }

        $subject_id = DBFind("votings_subject").Where("voting_id=$", $votingID).One("id")
        if !$subject_id {
            warning "Subject of voting is not found"
        }

        $voting_name = Str($voting_map["voting.name"])
        $voting_type = Int($voting_map["voting.type"]) //system or custom
        $quorum = Int($voting_map["voting.quorum"])
        $type_decision = Int($voting_map["voting.type_decision"])
        $optional_role_id = Int($voting_map["optional.role_id"])
        $optional_role_vacancies = Int($voting_map["optional.role_vacancies"]) // number of vacancies for the role
        $number_voters = Int($voting_map["progress.number_voters"]) // number of participants who have already voted
        $contract_accept = Str($voting_map["optional.contract_accept"])
        $contract_reject = Str($voting_map["optional.contract_reject"])
        $param_accept = Str($voting_map["optional.contract_accept_params"])
        $param_reject = Str($voting_map["optional.contract_reject_params"])
        $flag_success = Int($voting_map["flags.success"])
    }

    action {
        // if notifications have been sent, then close it
        $ret_notifics = DBFind("notifications").Where("page_name=$ and page_params->voting_id=$ and closed=$", "voting_view", $votingID, 0)
        var i int
        while (i < Len($ret_notifics)) {
            $vals_notifics = $ret_notifics[i]
            Notifications_Single_Close("notific_id", Int($vals_notifics["id"]))
            i = i + 1
        }

        //if the flag of success = true(enough participants to make a decision)
        if ($flag_success == 1) {
            if (($type_decision == 1) || ($type_decision == 2)) {
    
                $number_inserts = 0 // number of candidates who have already been added(for comparison with the number of vacancies)
                $flag_decision = -1 // none of the candidates has been added yet

                $ret_subject = DBFind("votings_subject").Columns("number_accept,subject->member_id,subject->member_name,subject->image_id").Where("voting_id=$", $votingID).Order("number_accept DESC")
                i = 0
                while i < Len($ret_subject) {
                    $vals_subject = $ret_subject[i]

                    // if there are still vacancies
                    if $number_inserts < $optional_role_vacancies {
                        // calculate the percentage to compare it with the quorum
                        $v_percent = Int($vals_subject["number_accept"]) * 100 / $number_voters
                        // if percentage >= quorum values, then this candidate must be added to the role

                        if $v_percent >= $quorum {
                            // get role name
                            $role_map = DBFind("roles").Columns("id,role_type,role_name,image_id").Where("id=$", $optional_role_id).Row()
                            if $role_map {

                                var role map
                                role["id"] = Str($role_map["id"])
                                role["type"] = Str($role_map["role_type"])
                                role["name"] = Str($role_map["role_name"])
                                role["image_id"] = Str($role_map["image_id"])

                                var member map
                                member["member_id"] = Str($vals_subject["subject.member_id"])
                                member["member_name"] = Str($vals_subject["subject.member_name"])
                                member["image_id"] = Str($vals_subject["subject.image_id"])

                                var appointed map
                                appointed["voting_id"] = Str($votingID)
                                appointed["voting_name"] = Str($voting_name)

                                // add a candidate to the role
                                DBInsert("roles_participants", "role,member,appointed,timestamp date_created", role, member, appointed, $block_time)

                                // increment of the counter of candidates
                                $number_inserts = $number_inserts + 1
                                // if at least one of the candidates was added to the role, then the voting decision is considered successful
                                $flag_decision = 1
                            }
                        }
                    }
                    i = i + 1
                }

                DBUpdate("votings", $votingID, "flags->decision", $flag_decision)

                // starting optional contract
                if ($flag_decision == 1) {
                    if (Size($contract_accept) > 0) {
                        var params map
                        if (Size($param_accept) > 0) {
                            params = JSONToMap($param_accept)
                        }
                        CallContract($contract_accept, params)
                    }
                } else {
                    if (Size($contract_reject) > 0) {
                        var params map
                        if (Size($param_reject) > 0) {
                            params = JSONToMap($param_reject)
                        }
                        CallContract($contract_reject, params)
                    }
                }

            }

            if ($type_decision == 3) {
                $subject_map = DBFind("votings_subject").Columns("id,number_accept").Where("voting_id=$", $votingID).Row()
                if $subject_map {
                    $number_accept = Int($subject_map["number_accept"])

                    // calculate the percentage to compare it with the quorum
                    $v_percent = $number_accept * 100 / $number_voters

                    // if percentage >= quorum values, then this document will be approved
                    if ($v_percent >= $quorum) {
                        $flag_decision = 1
                    }
                    // otherwise the document will be rejected
                    else {
                        $flag_decision = -1
                    }
                    
                    DBUpdate("votings", $votingID, "flags->decision", $flag_decision)

                    // starting optional contract
                    if ($flag_decision == 1) {
                        if (Size($contract_accept) > 0) {
                            var params map
                            if (Size($param_accept) > 0) {
                                params = JSONToMap($param_accept)
                            }
                            CallContract($contract_accept, params)
                        }
                    } else {
                        if (Size($contract_reject) > 0) {
                            var params map
                            if (Size($param_reject) > 0) {
                                params = JSONToMap($param_reject)
                            }
                            CallContract($contract_reject, params)
                        }
                    }

                }
            }

            if ($type_decision == 4) {
                $subject_map = DBFind("votings_subject").Columns("id,number_accept,subject->table,subject->table_id,subject->column,subject->column_value").Where("voting_id=$", $votingID).Row()
                if $subject_map {
                    $number_accept = Int($subject_map["number_accept"])

                    // calculate the percentage to compare it with the quorum
                    $v_percent = $number_accept * 100 / $number_voters

                    // if percentage >= quorum values, then this document will be approved
                    if ($v_percent >= $quorum) {
                        $flag_decision = 1

                        $decisionTable = Str($subject_map["subject.table"])
                        $decisionId = Int($subject_map["subject.table_id"])
                        $decisionColumn = Str($subject_map["subject.column"])
                        $decisionValue = Str($subject_map["subject.column_value"])

                        // type of voting - custom
                        if ($voting_type == 1) {
                            // ALWAYS CHECK THE PERMISSION
                            DBUpdate($decisionTable, $decisionId, $decisionColumn, $decisionValue)
                        }

                        // type of voting - system
                        if ($voting_type == 2) {
                            DBUpdateSysParam($decisionColumn, $decisionValue, "")
                        }
                    }
                    // otherwise the document will be rejected
                    else {
                        $flag_decision = -1
                    }

                    DBUpdate("votings", $votingID, "flags->decision", $flag_decision)

                    // starting optional contract
                    if ($flag_decision == 1) {
                        if (Size($contract_accept) > 0) {
                            var params map
                            if (Size($param_accept) > 0) {
                                params = JSONToMap($param_accept)
                            }
                            CallContract($contract_accept, params)
                        }
                    } else {
                        if (Size($contract_reject) > 0) {
                            var params map
                            if (Size($param_reject) > 0) {
                                params = JSONToMap($param_reject)
                            }
                            CallContract($contract_reject, params)
                        }
                    }

                }
            }

            if ($type_decision == 5) {
                $subject_map = DBFind("votings_subject").Columns("id,number_accept,subject->contract_accept,subject->contract_reject,subject->contract_accept_params,subject->contract_reject_params").Where("voting_id=$", $votingID).Row()
                if $subject_map {
                    $number_accept = Int($subject_map["number_accept"])

                    // calculate the percentage to compare it with the quorum
                    $v_percent = $number_accept * 100 / $number_voters

                    $con_accept = Str($subject_map["subject.contract_accept"])
                    $con_reject = Str($subject_map["subject.contract_reject"])
                    $param_accept = Str($subject_map["subject.contract_accept_params"])
                    $param_reject = Str($subject_map["subject.contract_reject_params"])

                    // if percentage >= quorum values, then this document will be approved
                    if ($v_percent >= $quorum) {
                        $flag_decision = 1

                        var params map
                        if (Size($param_accept) > 0) {
                            params = JSONToMap($param_accept)
                        }
                        CallContract($con_accept, params)
                    }
                    // otherwise the document will be rejected
                    else {
                        $flag_decision = -1

                        var params map
                        if (Size($param_reject) > 0) {
                            params = JSONToMap($param_reject)
                        }
                        CallContract($con_reject, params)
                    }
                }

                DBUpdate("votings", $votingID, "flags->decision", $flag_decision)
            }
        }
        // decision can not be made(not enough participants to make a decision)
        else {
            DBUpdate("votings", $votingID, "flags->decision", -2)
        }
    }
}

contract voting_Invite {
    data {
        votingID int
        var_id int //0, member_id, role_id
    }

    conditions {
        $voting_map = DBFind("votings").Where("id=$ and date_ended > now()", $votingID).Row()
        if !$voting_map {
            warning "Voting has expired. Can not add new participants"
        }

        // get JSON
        var voting_json string
        voting_json = Str($voting_map["voting"])

        // get MAP
        var voting map
        voting = JSONToMap(voting_json)

        $type_participants = Int(voting["type_participants"])
        $type_voting = Int(voting["type"])

        // get JSON
        var progress_json string
        progress_json = Str($voting_map["progress"])

        // get MAP
        var progress map
        progress = JSONToMap(progress_json)

        $number_participants = Int(progress["number_participants"])

        if (($number_participants > 1) && ($type_voting == 2)) {
            warning "This vote is a system. Add new participants are not possible"
        }

        if ($type_participants == 2) { //list of candidates
            $participant_id = DBFind("votings_participants").Where("voting_id=$ and member->member_id=$", $votingID, $var_id).One("id")
            if $participant_id {
                warning "Member has already been added before"
            }
        }

        if ($type_participants == 3) { //list of candidates
            $participant_id = DBFind("votings_participants").Where("voting_id=$", $votingID).One("id")
            if $participant_id {
                warning "Voting participants have already been added before"
            }
        }
    }

    action {
        if ($type_participants == 1) {
            $ret_member = DBFind("members").Where("id != $", 0).Order("id")
            $i = 0
            while ($i < Len($ret_member)) {
                $vals_member = $ret_member[$i]

                var member map
                member["member_id"] = Str($vals_member["id"])
                member["member_name"] = Str($vals_member["member_name"])
                member["image_id"] = Str($vals_member["image_id"])

                DBInsert("votings_participants", "voting_id,member", $votingID, member)
                $number_participants = $number_participants + 1
                $i = $i + 1
            }
        }

        if ($type_participants == 2) {
            $member_map = DBFind("members").Where("id = $", $var_id).Row()
            if $member_map {

                var member map
                member["member_id"] = Str($member_map["id"])
                member["member_name"] = Str($member_map["member_name"])
                member["image_id"] = Str($member_map["image_id"])

                DBInsert("votings_participants", "voting_id,member", $votingID, member)
                $number_participants = $number_participants + 1
            }
        }

        if ($type_participants == 3) {
            $ret_role = DBFind("roles_participants").Where("role->id=$ and deleted=0", $var_id).Order("id")
            $i = 0
            while ($i < Len($ret_role)) {
                $vals_role = $ret_role[$i]

                DBInsert("votings_participants", "voting_id,member", $votingID, Str($vals_role["member"]))
                $number_participants = $number_participants + 1
                $i = $i + 1
            }
        }

        // get JSON
        var progress_json string
        progress_json = Str($voting_map["progress"])

        // get MAP
        var progress map
        progress = JSONToMap(progress_json)

		progress["number_participants"] = Str($number_participants)
        DBUpdate("votings", $votingID, "progress", progress)
    }
}

