        $subject_id = DBFind("votings_subject").Where("voting_id = $", $votingID).One("id")
        if !$subject_id {
            DBInsert("votings_subject", "voting_id,subject", $votingID, subject)
        } else {
            DBUpdate("votings_subject", Int($subject_id), "subject", subject)
        }

        var accept_params, reject_params map
        accept_params = safeJsonToMap($contract_accept_params)
        reject_params = safeJsonToMap($contract_reject_params)

        var optional map
        optional = safeJsonToMap($voting_map["optional"])
        optional["contract_accept"] = $contract_accept
        optional["contract_accept_params"] = accept_params
        optional["contract_reject"] = $contract_reject
        optional["contract_reject_params"] = reject_params

        DBUpdate("votings", $votingID, "optional", optional)

        voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_SubjectSettings {
    data {
        votingID int

        roleID int
        vacancies int

        contract_accept string "optional"
        contract_accept_params string "optional"
        contract_reject string "optional"
        contract_reject_params string "optional"
    }
    func safeJsonToMap(m string) map {
        var res map
        if Size(m) > 0 { res = JSONToMap(m) }
        return res
    }

    conditions {
        if $vacancies < 1 {
            warning "Number of vacancies can not be less than 1"
        }

        $voting_map = DBFind("votings").Columns("optional,voting->type,flags->full_data").Where("id=? and date_started > now()", $votingID).Row()

        if !$voting_map {
            warning "Voting already started. Can not edit document"
        }

        if $voting_map["flags.full_data"] == 1 && $voting_map["voting.type"] == 2 {
            warning "This vote is a system. Change settings not allowed"
        }
        var role map
        role = DBFind("roles").Where("id=? and deleted=0", $roleID).Row()
        if !role {
            warning "Specified role not found"
        }

        // roles_types: 1. assigned_role, 2. elective_role, 3. system_role, 4. company_role
        if !(role["role_type"] == 2 || role["role_type"] == 3) {
            warning "The chosen role is not elective or has been removed"
        }
    }

    action {
        var optional map
        optional = safeJsonToMap($voting_map["optional"])

        optional["contract_accept"] = $contract_accept
        optional["contract_accept_params"] = safeJsonToMap($contract_accept_params)
        optional["contract_reject"] = $contract_reject
        optional["contract_reject_params"] = safeJsonToMap($contract_reject_params)
        optional["role_id"] = $roleID
        optional["role_vacancies"] = $vacancies

        DBUpdate("votings", $votingID, "optional", optional)

        voting_SubjectCheckFulldata("votingID", $votingID)
    }
}

contract voting_RejectDecision {
    data {
        votingID int
    }

    conditions {
        $voting_map = DBFind("votings").Columns("flags->decision,flags->notifics").Where("id=$", $votingID).Row()
        if !$voting_map {
            warning "Voting is not found"
        }

        $participant_id = DBFind("votings_participants").Where("voting_id=$ and member->member_id=$", $votingID, $key_id).One("id")
        if !$participant_id {
            warning "You are not a participant in this voting"
        }
    }

    action {
        if $voting_map["flags.notifics"] == 1 {
            // close sent notifications
            var notific_id string
            notific_id = DBFind("notifications").Where("recipient->member_id=$ and page_name='voting_view' and page_params->voting_id=$ and closed=0", $key_id, $votingID).One("id")
            if notific_id {
                Notifications_Single_Close("notific_id", Int(notific_id))
            }
        }

        if $voting_map["flags.decision"] == 1 {
            warning "Decision was taken. Voting is now not possible"
        }
        var voting_start, voting_end string
        voting_start = DBFind("votings").Where("id=$ and date_started < now()", $votingID).One("id")
        voting_end = DBFind("votings").Where("id=$ and date_ended > now()", $votingID).One("id")
        if !voting_start {
            warning "Voting has not yet begun. Try again later, please"
        }
        if !voting_end {
            warning "Voting has expired. Voting is now not possible"
        }


        DBUpdate("votings_participants", Int($participant_id), "timestamp decision_date,decision", $block_time, -1)

        voting_UpdateDataForGraphs("votingID", $votingID)
    }
}

