SetTitle($blockcron_list$)
AddToolButton(Title: $create$, Icon:"icon icon-clock", Page: blockcron_create)

If(GetVar(query)!=""){
    SetVar(cron_where, {"key_id":"#key_id#", "contract":{"$like":"#query#"}, "deleted":"false"})
}.Else{
    SetVar(cron_where, {"key_id":"#key_id#", "deleted":"false"})
    SetVar(query,)
}

Div(content-wrapper){
    Div(panel panel-primary){
        Div(panel panel-body){
            Div(row){
                Div(col-md-12){
                    Form(input-group){
                        Div(Class: input-group-addon){
                            $contract$
                        }
                        Input(Name: query_input, Value: #query#)
                        Div(input-group-btn){
                            Button(Class: btn btn-default fa fa-search, Page: blockcron_list, PageParams: "query=Val(query_input)")
                        }
                    }
                }
            }
            DBFind(delayed_contracts,src_cron).Columns(["contract", "block_id", "every_block", "counter", "limit", "deleted::int", "id"]).Where(#cron_where#).Order({"id":-1}).Custom(_actions){
                Div(text-right){
                    Div(input-group-btn){
                        Button(Contract: BlockCronEdit, Class: btn btn-danger icon-trash, Params:"id=#id#,deleted=1", Page:blockcron_list).Alert(Text: $want_delete_block_timer$,ConfirmButton:$yes$, CancelButton: $cancel$)
                        Button(Class:btn btn-primary fa fa-edit, Page:blockcron_edit, PageParams: "id=#id#")
                    }
                }
            }.Custom(_next_block){
                If(Or(#counter# < #limit#, #limit# == 0)){
                    Span(Class: text-warning, Body: #block_id#)
                }.Else{
                    Span(Class: text-success, Body: $done$)
                }
            }
            Table(src_cron, "$contract$=contract,$interval$=every_block,$next_block$=_next_block,$count$=counter,$limit$=limit,=_actions")
        }
    }
}

contract BlockCronCreate {
    data {
        contract_name string
        limit int
        start_block int
        every_block int
    }
    conditions {
        if $start_block < $block {
            $start_block = $block + 1
        }
    }
    action {
        NewDelayedContract("Contract,EveryBlock,Conditions,BlockID,Limit", $contract_name, $every_block, `true`, $start_block, $limit)
    }
}

contract BlockCronEdit {
    data {
        id int
        contract_name string "optional"
        every_block int "optional"
        limit int "optional"
        start_block int "optional"
        deleted int "optional"
    }
    conditions {
        var original_cron map
        original_cron = DBFind("delayed_contracts").Where({"id": $id, "key_id": $key_id}).Row()

        if !original_cron {
            warning Sprintf("Delayed contract #%v not found", $id)
        }

        if !$contract_name {
            $contract_name = original_cron["contract"]
        }
        if !$every_block {
            $every_block = Int(original_cron["every_block"])
        }
        if !$limit {
            $limit = Int(original_cron["limit"])
        }
        if !$start_block {
            $start_block = Int(original_cron["start_block"])
        }
        if !$deleted {
            $deleted = 0
        }
        $conditions = Str(original_cron["conditions"])
        if $start_block < $block {
            $start_block = $block + 1
        }
    }
    action {
        EditDelayedContract("Id,Contract,EveryBlock,Conditions,BlockID,Limit,Deleted", $id, $contract_name, $every_block, $conditions, $start_block, $limit, $deleted)
    }
}
