contract land_registry_AccountCreate{
    data{
        account_type int
        member_id string
    }

    func conditions{

        MainCondition()

        $account_id = 0;
        $memberId = AddressToId($member_id)

        var account map
        if $account_type == 1 || $account_type == 2 {
            account = DBFind("land_registry_accounts").Where("type=? and onhold=0", $account_type).Row()
        } else {
            account = DBFind("land_registry_accounts").Where("type=? and member_id=? and onhold=0", $account_type,$memberId).Row()
        }

        if account {
            warning "Account with this type has already been created"
        }
    }

    func action {
        DBInsert("land_registry_accounts", "member_id,type", $memberId, $account_type)
    }
}

contract land_registry_Add{
    data{
        OwnerId int
        Coords string // {"coords":[{"lat":float,"lng":float},],"area": float,"address": string}
        LandUse int
        BuildingsUseClass int
        RegistrationDate string
        LandNature int
        LandLocalBody int
    }

    func conditions{
        var member map
        member = DBFind("members").Where("id = ?", $OwnerId).Row()
        if !member {
            warning "Member account for Owner not found"
        }

        $depart2_RoleId = DBFind("roles_list").Where("role_name='land_depart2' and delete=0").One("id")
        $depart3_RoleId = DBFind("roles_list").Where("role_name='land_depart3' and delete=0").One("id")
        if $depart2_RoleId == nil || $depart3_RoleId == nil{
            info "Department roles not found"
        }

    }
    func action{
        var icon, header, body, page, params, roleParams, coordsAddress, area string
        var closureType, landId int
        var coords map
        coords = JSONToMap($Coords)
        coordsAddress = coords["address"]
        area = Sprintf("%.0f", coords["area"])

