Div(list-group-item mt-lg mb-lg ml-lg mr-lg){
    Div(Class: h3 text-bold m0 text-center, Body: To Apla Users who have passed)
    Div(Class: h3 text-bold m0 text-center, Body: the KYC screening at SnapSwap International S.A.)

    Div(Class: h4 mt-xl, Body: Em(Date: 1st of November 2018))
    Div(Class: h4, Body: Em(Re: Invitation to submit purchase orders to buy APLA Tokens))

    Div(Class: h4 mt-xl, Body: Dear Apla User,)
    Div(Class: h4, Body: We are very delighted that you have chosen the Apla blockchain platform to build decentralised applications, create ecosystems and accommodate your other business needs.)
    Div(Class: h4, Body: According to Article 8.1 of the Apla General Terms and Conditions (Span(Class: text-primary, Body: www.apla.io)), EGAAS S.A. will be charging a license fee for each transaction signed by Apla users on the platform. The license fee is payable in the internal virtual currency of the Apla platform ecosystem named APLA tokens. Hence, in order to use the platform, you will need to have APLA tokens on your APLA Wallet prior to signing each transaction.)
    Div(Class: h4){
        Span(EGAAS S.A. will be conducting an initial sale of APLA Tokens from November 2018 until July 2019. The commercial terms of the sale are summarised below:)
        Button(Class: btn-xs btn-link, Page: ts_commercial_terms){
            Span(Class: h4, Body: The commercial terms)
        }.Popup(70, The commercial terms)
    }

    Div(Class: h4, Body: Please submit your purchase order to buy APLA tokens after you have reviewed and agreed with the legal terms of sale.)
    Div(Class: h4, Body: After we have received your purchase order, we will send you an invoice for making the payment of the purchase price.)
    Div(Class: h4, Body: Please reach out to us via the email Span(Class: text-primary, Body: luxembourg@apla.io) if you have any questions or require any clarification of the above terms. We’ll also give you an access to the online data room with all information and legal documents concerning the Apla project, if you require so.)

    Div(Class: h4 mt-xl, Body: Kind regards,)
    Div(row){
        Div(col-sm-6){
            Div(Class: h4 m0, Body: Oleg Strelenko)
        }
        Div(col-sm-6){
            Div(Class: h4 m0, Body: Vitaly Bondar)
        }
    }
    Div(row){
        Div(col-sm-6){
            Div(Class: h4 m0, Body: Director)
        }
        Div(col-sm-6){
            Div(Class: h4 m0, Body: Director)
        }
    }

    Div(Class: h4 mt-xl, Body: Annexes:)
    Div(Class: h4 m0, Body: Annex 1. Purchase Order)
    Div(Class: h4 m0, Body: Annex 2. Standard Terms & Conditions of Sale)

    Div(row mt-xl pull-right){
        Div(col-sm-12){
            Button(Class: btn btn-primary h4, Page: ts_buyer_contract, Body: Standard terms and conditions of sale)
        }
    }
}.Style(
    padding-left: 60px!important; 
    padding-right: 60px!important;
)

If(#ecosystem_id# == 1){
    MenuItem(Title:$@1ts_settings$, Page:ts_settings, Icon:"icon-wrench")
}

If(#ecosystem_id# == 1){
    SetVar(is_allowed, 0).(k_id, 0).(unreaded_wallet_id,0).(exist_wallet_id,0)
    DBFind(@1keys).Where({id:#key_id#}).Vars(k)
    DBFind(@1ts_wallets).Where({key_id_buyer:#key_id#}).Vars(exist_wallet)
    DBFind(@1ts_wallets).Where({key_id_buyer:#key_id#, rejected_at:0, signed_at:0}).Vars(unreaded_wallet)
    If(#k_id#!=0){
        If(#exist_wallet_id#>0){
            If(#unreaded_wallet_id#==0){
                SetVar(is_allowed, 1)
            }
        }.Else{
            SetVar(is_allowed, 1)
        }
    }
    If(#is_allowed#==1){
        SetVar(snapswap_role_id,0).(admin_role_id,0).(manager_role_id,0).(seller_role_id,0)
        SetVar(snapswap_role_id, EcosysParam(Name:role_snapswap))
        SetVar(admin_role_id, EcosysParam(Name:role_sale_admin))
        SetVar(manager_role_id, EcosysParam(Name:role_sale_manager))
        SetVar(seller_role_id, EcosysParam(Name:role_sale_seller))

        Span("Set here the menu of the first ecosystem")
        If(And(#role_id#>0,Or(#role_id#==#admin_role_id#,#role_id#==#manager_role_id#,#role_id#==#seller_role_id#,#role_id#==#snapswap_role_id#))){
            MenuItem(Title:$@1ts_free_wallets$, Page:@1ts_free_wallets, Icon:"icon-credit-card")
        }
        If(And(#role_id#>0,Or(#role_id#==#admin_role_id#,#role_id#==#manager_role_id#,#role_id#==#seller_role_id#,#role_id#==#snapswap_role_id#))){
            MenuItem(Title:$@1ts_wallets$, Page:@1ts_wallets, Icon:"icon-credit-card")
        }
        If(And(#role_id#>0,Or(#role_id#==#admin_role_id#,#role_id#==#manager_role_id#,#role_id#==#seller_role_id#,#role_id#==#snapswap_role_id#))){
            MenuItem(Title:$@1ts_sales$, Page:@1ts_sales, Icon:"icon-credit-card")
        }
    }
}

If(#ecosystem_id# == 1){
    DBFind(@1ts_wallets).Where({key_id_buyer:#key_id#, rejected_at:0, signed_at:0}).Vars(unconfirmed_wallet)
    If(#unconfirmed_wallet_id#>0){
        Include(@1ts_contract_investors)
    }.Else{
        Include(@1profile)
    }
}
If(#ecosystem_id# > 1){
    DBFind(@1roles).Where({"ecosystem": #ecosystem_id#}).Count(roles_count)
    If(#roles_count# > 0){
        Include(@1profile)
    }.Else{
        Include(@1welcome)
    }
}

DBFind(@1ts_wallets).Where({"key_id_buyer":#key_id#}).Vars(w)
If(#w_id# > 0){
    Div(list-group-item mt-lg mb-lg ml-lg mr-lg){
        Div(Class: h3 text-bold text-center pb-xl, Body: Purchase Order)
        Div(Class: h4 pb-lg, Body: In response to your invitation to submit a purchase order as of 1s tof November 2018, we would like to submit the below purchase order to buy APLA Tokens: )
            Div(){
                Div(row){
                    Div(col-md-12){
                        Div(table tablez){
                            Div(table__row){
                                Div(table__cell table__cell_10){APLA Wallet Number}
                                Div(table__cell table__cell_10){Address(#key_id#)}
                            }
                            Div(table__row){
                                Div(table__cell table__cell_10){Number of APLA Tokens to buy}
                                Div(table__cell table__cell_10){}
                            }
                            Div(table__row){
                                Div(table__cell table__cell_10 tborder){Payment means}
                                Div(table__cell table__cell_10 tborder){
                                    Div(Class: text-normal, Body: BTC: #w_wallet_btc#)
                                    Div(Class: text-normal, Body: ETH: #w_wallet_eth#)
                                }
                            }
                        }
                    }
                }
            }.Style(
                .row-flex {
                    display: flex;
                    flex-flow: row wrap;
                }
                .table {
                    display: table;
                    border: solid 1px #000000;
                }
                .tablez {
                    border: solid 1px #000000;
                }
                .table .table__head {
                    display: table-header-group;
                    background: blue;
                    color: #fff;
                }
                .table .table__head .table__cell {
                    font-size: 15px;
                    color: #000000;
                    background: #B7B7B7;
                }
                .table > div:not(.table__body) {
                    display: table-row-group;
                }
                .table .table__body:last-child .table__row:last-child .table__cell {
                    border-bottom: none;
                }
                .table .table__row {
                    display: table-row;
                    flex-direction: row;
                    flex-wrap: no-wrap;
                }
                .table .table__row:nth-child(2n) {
                    display: table-row;
                    flex-direction: row;
                    flex-wrap: no-wrap;
                    border-left: solid 1px red;
                }
                .table .table__row .table__cell {
                    border-bottom: solid 1px #000000;
                }
                .table .table__cell {
                    display: table-cell;
                    padding: 8px 10px;
                    font-size: 14px;
                    color: #000000;
                    border-right: solid 1px #000000;
                }
                .tborder {
                    border-bottom: 0!important;
                }
                .table .table__cell:last-child {
                    border-right: none;
                }
                .table .table__cell.table__cell_collapse {
                    width: 1px;
                }
                .table .table__cell.table__cell_10 {
                    width: 10%;
                }
                .table .table__cell.table__cell_20 {
                    width: 20%;
                }
                .table .table__cell.table__cell_25 {
                    width: 25%;
                }
                .table .table__cell.table__cell_30 {
                    width: 30%;
                }
                .table .table__cell.table__cell_40 {
                    width: 40%;
                }
                .table .table__cell.table__cell_50 {
                    width: 50%;
                }
                .table .table__cell.table__cell_60 {
                    width: 60%;
                }
                .table .table__cell.table__cell_70 {
                    width: 70%;
                }
                .table .table__cell.table__cell_80 {
                    width: 80%;
                }
                .table .table__cell.table__cell_18 {
                    width: 18%;
                }
                .table .table__cell.table__cell_90 {
                    width: 90%;
                }
                .table .table__cell.table__cell_stretch {
                    width: 100%;
                }
            )
        Div(Class: h4, Body: We hereby acknowledge the receipt of and agree with the Standard Terms & Conditions of Sale. )
        Div(){
            DBFind(@1notifications).Where({ecosystem:#ecosystem_id#, id:#notific_id#}).Columns("closed").Vars(note)
            If(#note_closed# == 0){
                Button(Body: $@1readed$, Class: btn btn-primary pull-right, Page: default_page, Contract: NotificationsClose, Params: "notific_id=#notific_id#")
            }
        }
    }
}.Else{
    Div(list-group-item text-center h4 ml mr mt-lg){
        $@1wallet$ $@1not_found$
    }
}

Div(ml mr){
    Div(Class: h5 text-bold, Body: 1. Price, sale rounds and extra terms:)
    Div(row){
        Div(col-md-12){
            Div(table tablez){
                Div(table__head){
                    Div(table__row){
                        Div(table__cell table__cell_20){Round}
                        Div(table__cell table__cell_10){Volume of APLA Tokens}
                        Div(table__cell table__cell_10){Price, EUR, excl. VAT}
                        Div(table__cell table__cell_10){Discount}
                        Div(table__cell){Extra terms}
                    }
                }
                Div(table__row){
                    Div(table__cell){Round 1 (01.11.18-20.12.18)}
                    Div(table__cell){15 100 000}
                    Div(table__cell){5}
                    Div(table__cell){20%}
                    Div(table__cell){25% of unsold APLA tokens during each Round 2 to 7 will be proportionally distributed for free among the buyers purchased the tokens during Round 1. 
                    This option becomes available only if the conditions for free distribution of APLA Tokens to the buyers participating to the Rounds 2-7 have been fulfilled.
                    If you acquire APLA Tokens for the amount of at least 125,000 EUR, you will be entitled to additional benefits.  Please reach out to us via email luxembourg@apla.io to get more details. 
                    }
                }
                Div(table__row){
                    Div(table__cell){Round 2(20.12.18-01.02.19)}
                    Div(table__cell){1 000 000}
                    Div(table__cell){5}
                    Div(table__cell){0%}
                    Div(table__cell tborder){75% of unsold APLA tokens during each Round 2 to 7 will be proportionally distributed for free among the buyers participating to the respective round. This option becomes available only if at least 40% of APLA tokens have been sold out during this round. In all other cases, the unsold tokens remain the property of the current owner.
                    }
                }
                Div(table__row){
                    Div(table__cell){Round 4 (01.03.19-01.04.19)}
                    Div(table__cell){5 000 000}
                    Div(table__cell){5}
                    Div(table__cell){0%}
                    Div(table__cell tborder){}
                }
                Div(table__row){
                    Div(table__cell){Round 5 (01.04.19-01.05.19)}
                    Div(table__cell){7 000 000}
                    Div(table__cell){5}
                    Div(table__cell){0%}
                    Div(table__cell tborder){}
                }
                Div(table__row){
                    Div(table__cell){Round 6 (01.05.19-01.06.19)}
                    Div(table__cell){12 000 000}
                    Div(table__cell){5}
                    Div(table__cell){0%}
                    Div(table__cell tborder){}
                }
                Div(table__row){
                    Div(table__cell tborder){Round 7 (01.06.19-01.07.19)}
                    Div(table__cell tborder){16 000 000}
                    Div(table__cell tborder){5}
                    Div(table__cell tborder){0%}
                    Div(table__cell tborder){}
                }
            }
        }
    }
    Div(Class: h5 m0, Body: Each round starts at 11:01 am and ends at 11 am of the designated date)

    Div(row mt-xl){
        Div(col-sm-3){
            Div(Class: h5 text-bold, Body: 2. Delivery of APLA Tokens)
        }
        Div(col-sm-9){
            Div(Class: h5, Body: The number of APLA tokens you have paid for, will be delivered to your APLA Wallet within 2 business days after EGAAS S.A. have received the payment of the purchase price.)
        }
    }
    Div(row){
        Div(col-sm-3){
            Div(Class: h5 text-bold, Body: 3. Payment currency)
        }
        Div(col-sm-9){
            Div(Class: h5, Body: EUR/BTC/ETH)
        }
    }
    Div(row){
        Div(col-sm-3){
            Div(Class: h5 text-bold, Body: 4. Payment terms )
        }
        Div(col-sm-9){
            Div(Class: h5, Body: If you make a payment in EUR, you will need to make a transfer of the purchase price from your EUR account with SnapSwap to the EUR account of EGAAS S.A. with the same financial institution. Please indicate in the payment order your APLA Wallet number for the delivery of APLA Tokens.)
            Div(Class: h5, Body: If you make a payment in BTC or ETH, the Bitstamp exchange rate of BTC/EUR or ETH/EUR shall be used to calculate the EUR equivalent. The Bitstamp rate shall be defined on the day EGAAS S.A. have received BTC or ETH into its wallet. )
            Div(Class: h5, Body: The details of the BTC or ETH wallets of EGAAS S.A. will be communicated to you for each transaction individually after we have received your purchase order. )
        }
    }
    Div(row){
        Div(col-sm-3){
            Div(Class: h5 text-bold, Body: 5. Legal terms)
        }
        Div(col-sm-9){
            Div(Class: h5, Body: Standard Terms & Conditions of Sale enclosed to these commercial terms. )
        }
    }

    Div(row mt-xl pull-right){
        Div(col-sm-12){
            Button(Class: btn btn-default, Page: default_page, Body: Back)
        }
    }
