contract TsBuyerAction {
    data {
        Action string
    }
    conditions{
        var basicAppId int
        basicAppId = Int(DBFind("@1applications").Where({ecosystem:1, name:"Basic"}).One("id"))
        if basicAppId == 0{
            warning LangRes("@1app_not_found", "en")
        }
        $roleConsensus = Int(EcosysParam("role_apla_consensus_asbl"))
        if $roleConsensus == 0{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), "role_apla_consensus_asbl")
        }
        $wallet = DBFind("ts_wallets").Where({key_id_buyer:$key_id, rejected_at:0}).Row()
        if !$wallet{
            warning "Buyer wallet not found"
        }
    }

    action {
        if $Action == "accept"{
            DBUpdate("ts_wallets", Int($wallet["id"]), {signed_at:$time})
            // role Apla Consensus notification
            var params map title string
            title = Sprintf("Buyer %v signed contract", IdToAddress($key_id))
            params["wallet_id"] = $wallet["id"]
            @1NotificationsSend("rid,sender,text_header,page_name,params_map,closure_type", $roleConsensus, 1, title, "ts_consensus_contract", params, 1)

        }elif $Action == "reject"{
            DBUpdate("ts_wallets", Int($wallet["id"]), {rejected_at:$time})
        }

        var notes array i int note map
        notes = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, "recipient->member_id":$key_id, page_name:"@1ts_buyer_contract", closed:0}).Columns("id")
        while i < Len(notes){
            note = notes[i]
            @1NotificationsClose("notific_id",Int(note["id"]))
            i = i + 1
        }
    }
}

contract TsConsensusAction {
    data {
        Action string
        WalletId int
        NotificId int
    }
    conditions{
        var basicAppId int
        basicAppId = Int(DBFind("@1applications").Where({ecosystem:1, name:"Basic"}).One("id"))
        if basicAppId == 0{
            warning LangRes("@1app_not_found", "en")
        }
        $roleConsensus = Int(EcosysParam("role_apla_consensus_asbl"))
        if $roleConsensus == 0{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), "role_apla_consensus_asbl")
        }
        if !RoleAccess($roleConsensus){
            warning "this action allowed only for role id #" + $roleConsensus
        }
        $wallet = DBFind("ts_wallets").Where({id:$WalletId, rejected_at:0}).Row()
        if !$wallet{
            warning "Buyer wallet not found"
        }
        if Int($wallet["signed_at"]) == 0{
            warning "Buyer wallet not signed"
        }
        if $wallet["key_id_signer"] > 0{
            if $wallet["key_id_signer"] != $key_id{
                warning "This wallet was signed by asbl early"
            }
        }
    }

    action {
        if $Action == "accept"{
            DBUpdate("ts_wallets", Int($wallet["id"]), {key_id_signer:$key_id})
            // buyer notification
            var params map
            params["wallet_id"] = $wallet["id"]
            @1NotificationsSend("member_id,sender,text_header,page_name,params_map,closure_type", Int($wallet["key_id_buyer"]), 1, "Your wallet approved", "ts_buyer_congratulation", params, 1)

        }elif $Action == "reject"{
            DBUpdate("ts_wallets", Int($wallet["id"]), {rejected_at:$time})
        }
        @1NotificationsClose("notific_id", $NotificId)
    }
}

