contract TeBurn {
    data {
        Amount money
    }

    func checkSender() {
        var sender_map map
        sender_map = DBFind("@1keys").Where({"id": $key_id, "ecosystem": $ecosystem_id}).Row()

        if !sender_map {
            warning Sprintf(LangRes("@1sender_x_invalid", "en"), $key_id)
        }
        if Int(sender_map["blocked"]) != 0 {
            warning LangRes("@1sender_blocked", "en")
        }
        if Int(sender_map["deleted"]) != 0 {
            warning LangRes("@1sender_deleted", "en")
        }
        if Money(sender_map["amount"]) < $Amount {
            warning LangRes("@1amount_less_than_needed", "en")
        }
    }

    conditions {
        if $ecosystem_id == 1 {
            warning LangRes("@1burning_blocked", "en")
        }

        checkSender() //check the sender

        //check amount
        if $Amount == 0 {
            warning LangRes("@1amount_zero", "en")
        }
        if $Amount < 0 {
            warning LangRes("@1amount_less_zero", "en")
        }

        $item = DBFind("@1ecosystems").WhereId($ecosystem_id).Row()
        if $item["type_withdraw"] == 1 {
            warning LangRes("@1burning_blocked", "en")
        }
    }

    action {
        var mb map
        var burn map arr array
        burn["type"] = "burn"
        burn["time"] = Str($block_time)
        burn["val"] = Money($Amount)
        arr = JSONDecode($item["emission_amount"])
        arr = Append(arr, burn)
        mb["emission_amount"] = JSONEncode(arr)
        DBUpdate("@1ecosystems", $ecosystem_id, mb)

        var m map
        m["sender_id"] = $key_id
        m["recipient_id"] = $guest_key
        m["amount"] = $Amount
        m["comment"] = "New burn"
        m["block_id"] = $block
        m["txhash"] = $txhash
        m["ecosystem"] = $ecosystem_id
        m["type"] = 4
        m["created_at"] = $block_time
        DBInsert("@1history", m)

        DBUpdateExt("@1keys", {"id": $key_id, "ecosystem": $ecosystem_id}, {"-amount": $Amount})
        DBUpdateExt("@1keys", {"id": $guest_key, "ecosystem": $ecosystem_id}, {"+amount": $Amount})
    }
}

contract TeChange {
    data {
        TypeChange string
        Value int
    }

    conditions {
        $app = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Token emission"}).One("id")
        var admin_ids string
        admin_ids = AppParam(Int($app), "te_admin_role", $ecosystem_id)
        var rids array
        rids = JSONDecode("["+admin_ids+"]")
        if !DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "role->id": {"$in": rids}, "member->member_id": $key_id, "deleted": 0}).Row() {
            warning LangRes("@1access_denied", "en")
        }

        $item = DBFind("@1ecosystems").WhereId($ecosystem_id).Row()
        if !$item {
            warning LangRes("@1emission_not_found", "en")
        }

        if $Value < 1 || $Value > 2  {
            warning LangRes("@1value_between_1_and_2", "en")
        }
    }

    action {
        var m map
        if $TypeChange == "emission" {
            m["type_emission"] = $Value
        }
        if $TypeChange == "withdraw" {
            m["type_withdraw"] = $Value
        }

        DBUpdate("@1ecosystems", $ecosystem_id, m)
    }
}

contract TeCreate {
    data {
        Title string "optional"
        Amount money
        TypeWithdraw int "optional"
        TypeEmission int "optional"
    }

    func getEmissionConditionContract() string {
        var param map contractName string contractId int
        param = DBFind("@1parameters").Where({"ecosystem": $ecosystem_id, "name": "emission_condition_contract"}).Row()
        if param {
            contractName = param["value"]
            if Size(contractName) > 0 {
                contractId = GetContractByName(contractName)
                if contractId == 0 {
                    warning LangRes("@1emission_conditions_contract_not_found", "en")
                }
            }
        }
        return contractName
    }

    func createEmissionConditionParam() {
        if !DBFind("@1parameters").Where({"ecosystem": $ecosystem_id, "name": "emission_condition_contract"}).Row(){
            @1NewParameter("Name,Value,Conditions", "emission_condition_contract", "AdminCondition", "ContractConditions(\"AdminCondition\")")
        }
    }

    conditions {
        if $ecosystem_id == 1 {
            warning LangRes("@1emission_blocked", "en")
        }

        $founder = Int(EcosysParam("founder_account"))

        $item = DBFind("@1ecosystems").WhereId($ecosystem_id).Row()
        $SECONDARY_EMISSION = $item["type_emission"] == 1 || $item["type_emission"] == 2

        if $SECONDARY_EMISSION {
            if $item["type_emission"] == 1 {
                warning LangRes("@1emission_blocked", "en")
            }
        } else {
            if Size($Title) == 0 {
                warning Sprintf(LangRes("@1template_creation_error_x_unspecified", "en"), "token_title")
            }
            if $TypeWithdraw < 1 || $TypeWithdraw > 2 {
                warning Sprintf(LangRes("@1template_creation_error_x_unspecified", "en"), "type_withdraw")
            }
            if $TypeEmission < 1 || $TypeEmission > 2 {
                warning Sprintf(LangRes("@1template_creation_error_x_unspecified", "en"), "type_emission")
            }
        }
    }

    action {
        createEmissionConditionParam()

        var contract_name string
        contract_name = getEmissionConditionContract()
        ContractConditions(contract_name)

        var m emission map arr array
        emission["type"] = "emission"
        emission["time"] = Str($block_time)
        emission["val"] = Money($Amount)
        if $SECONDARY_EMISSION {
            if $item["emission_amount"]{
                arr = JSONDecode($item["emission_amount"])
            }
        } else {
            m["type_emission"] = $TypeEmission
            m["token_title"] = $Title
            m["type_withdraw"] = $TypeWithdraw
        }
        arr = Append(arr, emission)
        m["emission_amount"] = JSONEncode(arr)
        DBUpdate("@1ecosystems", $ecosystem_id, m)

        var h map
        h["sender_id"] = 0
        h["recipient_id"] = $founder
        h["amount"] = Money($Amount)
        h["comment"] = "New emission"
        h["block_id"] = $block
        h["txhash"] = $txhash
        h["ecosystem"] = $ecosystem_id
        h["type"] = 2
        h["created_at"] = $block_time
        DBInsert("@1history", h)

        DBUpdateExt("@1keys", {"ecosystem": $ecosystem_id, "id": $founder}, {"+amount": $Amount})
    }
}

contract TeSettings {
    data {
        RoleAdmin int
    }

    conditions {
        AdminCondition()
    }

    func updateParam(name string, value int) {
        if value > 0 {
            var param paramsNew map
            if $app_id > 0 {
                param = DBFind("@1app_params").Where({"ecosystem": $ecosystem_id, "app_id": $app_id, "name": name}).Row()
                if param {
                    paramsNew["Id"] = Int(param["id"])
                    paramsNew["Value"] = Str(value)
                    paramsNew["Conditions"] = param["conditions"]
                    CallContract("@1EditAppParam", paramsNew)
                } else {
                    paramsNew["ApplicationId"] = $app_id
                    paramsNew["Name"] = name
                    paramsNew["Value"] = Str(value)
                    paramsNew["Conditions"] = "ContractConditions(\"AdminCondition\")"
                    CallContract("@1NewAppParam", paramsNew)
                }
            } else {
                warning LangRes("@1app_not_found", "en")
            }
        }
    }

    action {
        $app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Token emission"}).One("id")
        updateParam("te_admin_role", $RoleAdmin)
    }
}
