contract Roles_Unassign {
    data {
        row_id int
    }
    
    conditions {
        // check record
        var assigned, role map
        assigned = DBFind("roles_participants").Columns("id,role->id").Where("id=$", $row_id).Row()
        if !assigned {
            warning "Participant not found"
        }
        
        role = DBFind("roles").Columns("id,creator->member_id,role_name").Where("id=?", assigned["role.id"]).Row()
        if !role {
            warning "Role not found in the roles registry"
        }
        
        var specialRules map
        specialRules["sysparams_AcceptValidator"] = "Validator"
        specialRules["sysparams_DelegateRefresh"] = "Delegate"
        specialRules["sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["sysparams_InvestorRefresh"] = "Investor with voting rights"
        specialRules["tokenrefund_Start"] = "Investor with voting rights"
        specialRules["sysparams_ValidatorResign"] = "Validator"
        
        if !(specialRules[$original_contract] == role["role_name"]) {
            // check creator of the role
            if role["creator.member_id"] != $key_id {
                warning "Sorry, you are not the creator of this role"
            }
        }
    }
    
    action {
        // delete the member
        DBUpdate("roles_participants", $row_id, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract Roles_Del {
    data {
        rid int
    }

    conditions {        
        var role map
        role = DBFind("roles").Columns("id,role_type,creator->member_id").Where("id=?", $rid).Row()
        if !role {
            warning "Role not found"
        }

        if role["role_type"] == 3 {
            warning "System role can not be removed"
        }

        if role["creator.member_id"] != $key_id {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        var participants array
        participants = DBFind("roles_participants").Columns("id").Where("role->id=? and deleted=0", $rid)

        // delete all participants
        var i int
        while i < Len(participants) {
            var p map
            p = participants[i]
            Roles_Unassign("row_id", Int(p["id"]))
            i = i + 1
        }

        DBUpdate("roles", $rid, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract TokenTransfer {
    data {
        Amount money
        Sender_AccountId int
        Recipient_AccountId int
        Comment string "optional"
        Signature string "optional hidden"
    }
    
    conditions {
        //check amount
        if $Amount == 0 {
            error "Amount is zero"
        }
        if $Amount < 0 {
            error "Amount is negative"
        }
        
        //check the sender
        var sender string
        sender = DBFind("keys").Where("id=$", $Sender_AccountId).One("id")
        if !sender {
            error Sprintf("Sender %v is invalid", $Sender_AccountId)
        }
        
        //check balance
        $sender_balance = Money(DBFind("keys").Where("id=$", $Sender_AccountId).One("amount"))
        if $Amount > $sender_balance {
            error Sprintf("Money is not enough %v < %v", $sender_balance, $Amount)
        }
        
        //check the recipient
        var recipient string
        recipient = DBFind("keys").Where("id=$", $Recipient_AccountId).One("id")
        if !recipient {
            error Sprintf("Recipient %v is invalid", $Recipient_AccountId)
        }
    }
    
    action {
        DBUpdate("keys", $Sender_AccountId, "-amount", $Amount)
        DBUpdate("keys", $Recipient_AccountId, "+amount", $Amount)
        //DBInsert("history", "sender_id,recipient_id,amount,comment,block_id,txhash", $Sender_AccountId, $Recipient_AccountId, $Amount, $Comment, $block, $txhash)
    }
}

contract SendTokens {
    data {
        Amount money
        Recipient_Account string
        Signature string "signature:TokenTransfer"
    }

    conditions {
        $recipient = AddressToId($Recipient_Account)
