contract sysparams_DelegateRefresh{
    func NotInArray(val string, arr array) bool{
        var i int
        // info arr
        while i< Len(arr){
            if val == arr[i]{
                return false
            }
            i=i+1
        }
        return true
    }

    conditions{
        MainCondition()

        var ROLE_NAME string
        var ROLE_ID int
        ROLE_NAME = "Delegate"
        ROLE_ID = 5

        // get role Id and Type
        $role = DBFind("roles").Where("role_name=? and id=? and delete=0", ROLE_NAME, ROLE_ID).Row()
        if !$role{
            warning Sprintf("Role %q not found", ROLE_NAME)
        }
    }

    action{
        var DELEGATE_ACCOUNT string
        DELEGATE_ACCOUNT = "delegate_account"
        // get delegates ids
        var delegateIds currentDelegates ecosystems array
        ecosystems = DBFind("ecosystems").Columns("id").Ecosystem(1)
        var i ecoId int
        while i < Len(ecosystems) {
            var eco delegateParam map
            eco = ecosystems[i]
            ecoId = Int(eco["id"])
            delegateParam = DBFind("parameters").Where("name=?", DELEGATE_ACCOUNT).Ecosystem(ecoId).Row()
            if !delegateParam{
                delegateParam = DBFind("parameters").Where("name='founder_account'").Ecosystem(ecoId).Row()
            }

            if NotInArray(delegateParam["value"], delegateIds){
                delegateIds[Len(delegateIds)] = delegateParam["value"]
            }
            i = i + 1
        }

        // remove old role assigns
        currentDelegates = DBFind("roles_participants").Where("role->id=? and deleted=0", $role["id"])
        i = 0
        while i < Len(currentDelegates){
            var delegate map
            delegate = currentDelegates[i]
            if NotInArray(delegate["member_id"], delegateIds){
                var id int
                id = Int(delegate["id"])
                Roles_Unassign("row_id", id)
            }
            i = i + 1
        }

        // assign new roles
        i = 0
        while i < Len(delegateIds) {
            var delegateId int
            delegateId = Int(delegateIds[i])
            var member map
            member = DBFind("members").Where("id=?",delegateId).Row()
            if member{
                // check role dup
                var assigned map
                assigned = DBFind("roles_participants").Where("member->member_id=? and role->id=? and deleted=0", delegateId, $role["id"]).Row()
                if !assigned{
                    Roles_Assign("rid,member_id", Int($role["id"]), Int(member["id"]))
                }
            }
            i=i+1
        }
    }
}

contract sysparams_InvestorRefresh {
    func NotInArray(val string, arr array) bool{
        var i int
        while i< Len(arr){
            if val == Str(arr[i]){
                return false
            }
            i=i+1
        }
        return true
    }
    conditions{
         if $role_id != 3 {
            warning "You not Validator. You can not to run this"
        }

        // roles:id == 4 Investor with voting rights
        $roleId = Int(DBFind("roles").Where("id=4 and deleted=0").One("id"))
        if !$roleId{
            warning "Investor role not found or deleted"
        }
        var appId int
        appId = Int(DBFind("applications").Columns("name,id").Where("name='Basic application'").One("id"))
        $limitInvestors = Int(AppParam(appId,"investors_number"))
        if $limitInvestors == 0 {
            warning "Investors number not set in application params"
        }
    }

    action{
        // get investors ids
        var keysInvestors assignedInvestors investorsIds assignedIds array
        keysInvestors = DBFind("keys").Order("amount desc, id").Limit($limitInvestors)
        assignedInvestors = DBFind("roles_participants").Columns("member->member_id").Where("role->id=? and deleted=0", $roleId)

        // get ids
        var i int
        while i < Len(keysInvestors) {
            var inv map
            inv = keysInvestors[i]
            investorsIds[i] = inv["id"]
            i = i + 1
        }
        i=0
        while i < Len(assignedInvestors) {
            var inv map
            inv = assignedInvestors[i]
            assignedIds[i] = inv["member.member_id"]
            i = i + 1
        }

        // remove old investors
        i=0
        while i < Len(assignedIds) {
            if NotInArray(assignedIds[i], investorsIds){
                var assigned map
                assigned = DBFind("roles_participants").Where("member->member_id=? and role->id=?", assignedIds[i], $roleId).Row()
                if assigned {
                    Roles_Unassign("row_id", Int(assigned["id"]))
                }
            }
            i = i+1
        }
        // add new investors
        i=0
        while i < Len(investorsIds) {
            if NotInArray(investorsIds[i], assignedIds){
                Roles_Assign("rid,member_id", $roleId, Int(investorsIds[i]))
            }
            i = i+1
        }
    }
}

contract sysparams_RemoveNode{
    data{}
    func jsonToArray(json string) array {
        // KOSTYL
        var res, parts array
        if Size(json) < 60 {
            // fastway empty|incorrect detect
            return res
        }
        if HasPrefix(json, "[") {
            json = Substr(json, 1, Size(json)-2)
            if Contains(json, "},"){
                // json have many objects
                parts = Split(json, "},")
                var i int
                while i < Len(parts){
                    var p, lastSym string
                    p = parts[i]
                    lastSym = Substr(p, Size(p)-1, 1)
                    if lastSym != "}"{
                        p = Sprintf("%v}", p)
                    }
                    res[i] = JSONToMap(p)
                    i=i+1
                }
            }else{
                if HasPrefix(json, "{"){
                    res[0] = JSONToMap(json)
                }
            }
        }
        return res
    }
    func nodesRemove(keyId int) array{
        var json string
        var nodes, res array
        json = SysParamString("full_nodes")
        if !HasPrefix(json, "[") {
            // empty string nodes
            return res
        }
        nodes = jsonToArray(json)
        var i, i_res int
        while i < Len(nodes){
            var node map
            node = nodes[i]
            if node["key_id"] != keyId {
                res[i_res] = node
                i_res = i_res+1
            }
            i=i+1
        }
        return res
    }
    func nodesArrayToJson(nodes array) string{
        var res string
        var json array
        if Len(nodes) == 0 {
            res = "[]"
            return res
        }
        var i int
        while i < Len(nodes){
            var node map
            node = nodes[i]
            json[i] = Sprintf(`{"tcp_address":"%v", "api_address":"%v", "key_id":"%v", "public_key":"%v"}`, node["tcp_address"], node["api_address"], node["key_id"], node["public_key"])
            i=i+1
        }
        res = Join(json, ",")
        res = Sprintf("[%v]", res)
        return res
    }
    conditions{
        if $original_contract != "sysparams_ValidatorResign"{
            warning "Unknown contract"
        }
    }

    action{
        var nodes array
        nodes = nodesRemove($key_id)
        var fullNodes string
        fullNodes = nodesArrayToJson(nodes)
        DBUpdateSysParam("full_nodes", fullNodes, "")
    }
}

contract voting_TemplateRun{
    data{
        TemplateId int
        Start string "date"
        End string "date"

        EndTime string "date,optional"
        StartTime string "date,optional"
        KeyId int "optional"
        TableWritten string "optional"
        RowId string "optional"
        ColumnName string "optional"
        NewValue string "optional"
        TextDocument string "optional"
    }
    func checkInput(value, name string){
        if Size(value) == 0 || value == "NULL" {
            warning Sprintf("%q not found", name)
        }
    }
    func emptyOrValue(value string) string{
        if value == "NULL" {
            value = ""
        }
        return value
    }

    conditions{
        var template map
        template = DBFind("voting_templates").Where("id=?",$TemplateId).Row()
        if !template{
            warning "Voting template not found"
        }
        if $KeyId != 0{
            $keyFind = DBFind("keys").Where("id=?", $KeyId).Row()
            if !$keyFind{
                warning "Key id not found"
            }
        }
        if $StartTime == ""{
            $StartTime = "00:00"
        }
        if $EndTime == ""{
            $EndTime = "23:59"
        }

        var initContract string
        initContract = template["init_contract"]
        if initContract != "" && initContract != "NULL"{
            var initParam map
            initParam["KeyId"] = $KeyId
            CallContract(initContract, initParam)
        }
        $voters = template["voters"]
        $candidates = template["candidates"]
        $votingName = template["title"]
        $volume = Int(template["volume"])
        $quorum = Int(template["quorum"])
        $type_voting = Int(template["type_voting"])
        $participants = Int(template["type_participants"])
        $decision = Int(template["type_decision"])
        $vacancies = Int(template["vacancies"])

        $desc = Sprintf("%v template #%v", $votingName, $TemplateId)
        if $type_voting < 1 || $type_voting > 2 {
            warning "Invalid type of voting"
        }
        $contractAccept = emptyOrValue(template["contract_accept"])
        $acceptParams = emptyOrValue(template["accept_params"])
        $contractReject = emptyOrValue(template["contract_reject"])
        $rejectParams = emptyOrValue(template["reject_params"])

        if $decision == 1 || $decision == 2 {
            // role-candidates
            checkInput($candidates, "Candidate role")
            var candidate map
            candidate = DBFind("roles").Where("id=? and deleted=0", $candidates).Row()
            if !candidate {
                warning Sprintf("Candidates role (id: %v) not found", $candidates)
            }
            $candidateId = Int(candidate["id"])
        }

        if $decision == 3 {
            // document approval
            checkInput($TextDocument, "TextDocument")
        }

        if $decision == 4 {
            // formal decision
            // in the system vote need only a field and a value
            checkInput($ColumnName, "ColumnName")
            checkInput($NewValue, "NewValue")
            if $type_voting == 2{
                $desc = Sprintf("%v [template #%v] %v", $votingName, $TemplateId, $ColumnName)
            }

            if $type_voting == 1{
                checkInput($TableWritten, "TableWritten")
                checkInput($RowId, "RowId")
                $desc = Sprintf("%v [template #%v] %v.%v.%v", $votingName, $TemplateId, $TableWritten, $ColumnName, $RowId)
            }
            if $RowId == "" || $RowId == "NULL"{
                $RowId = 0
            }

        }
        // check duplicated
        // var isCreated map
        // isCreated = DBFind("votings").Where("voting->name=? and voting->description=? and deleted=0", $votingName, $desc).Row()
        // if isCreated{
        // info "Voting already created"
        // }


        if $participants == 3 {
            // if the type of the participants "in role" - check the role
            var role map
            role = DBFind("roles").Where("id=? and deleted=0", $voters).Row()
            if !role {
                warning Sprintf("Voters role (id: %v) not found", $voters)
            }
            $roleId = Int(role["id"])
        }
    }

    action{
        var votingId int
        // must be installed in voting_CreateNew
        votingId = voting_CreateNew("voting_name,voting_type,description,type_participants,type_decision,now_date,start_time,start_date,end_date,end_time,volume,quorum", $votingName, $type_voting, $desc, $participants, $decision, $Start, $StartTime, $Start, $End, $EndTime, $volume, $quorum)
        if votingId == 0 {
            warning "votingId is zero"
        }
        // the default argument for Contract
        var defaultParams string
        defaultParams = Sprintf(`{"VotingTemplateId":"%v","KeyId":"%v","VotingId":"%v"}`, $TemplateId, $KeyId, votingId)

        if Size($acceptParams) == 0{
            $acceptParams = defaultParams
        }
        if Size($rejectParams) == 0{
            $rejectParams = defaultParams
        }

        if $decision == 1 || $decision == 2{
            // 1. Role candidates with the registration of participants on request
            // 2. Role candidates with manual registration of the participants
            voting_SubjectCandidates("votingID,memberID", votingId, $KeyId)
            voting_SubjectSettings("votingID,roleID,vacancies,contract_accept,contract_accept_params,contract_reject,contract_reject_params", votingId, $candidateId, $vacancies, $contractAccept, $acceptParams, $contractReject,$rejectParams)
        }
        if $decision == 3 {
            // Document approval
            voting_SubjectDocument("votingID,text_document,contract_accept,contract_accept_params,contract_reject,contract_reject_params", votingId,$TextDocument, $contractAccept, $acceptParams, $contractReject, $rejectParams)
        }

        if $decision == 4 {
            // Formal decision
            voting_SubjectFormal("votingID,description,table_to_written,row_id,column_name,written_value,contract_accept,contract_accept_params,contract_reject,contract_reject_params",votingId, $desc, $TableWritten, $RowId, $ColumnName, $NewValue, $contractAccept, $acceptParams, $contractReject, $rejectParams)

        }

        if $decision == 5 {
            // Execution of the contract
            voting_SubjectContract("votingID,contract_accept,contract_accept_params,contract_reject,contract_reject_params",votingId,$contractAccept, $acceptParams, $contractReject, $rejectParams)
        }
        if $participants == 3 {
            // participants: "by role"
            voting_Invite("votingID,var_id", votingId, $roleId)
        }
        if $participants == 1 {
            // if the participants are "all", the invite has already occurred in "voting_CreateNew"
        }
    }
}

