contract sysparams_StartNodeAdd{
    data{
        TcpAddress string
        ApiAddress string
        KeyId int
        PubKey string
        TemplateId int
        Start string "date"
        End string "date"
        EndTime string "date"
        StartTime string "date"
    }
    func warn(name, detail string){
        warning Sprintf("%v invalid (%v)", name, detail)
    }
    func checkApiAddress(addr string){
        var name string
        name = "API Address"
        if Size(addr) == 0 {
            warn(name, "size")
        }
        if !(HasPrefix(addr, "http://") || HasPrefix(addr, "https://")) {
            warn(name, "protocol")
        }
        if Contains(addr, " ") {
            warn(name, "space")
        }
        addr = Replace(addr, "http://", "")
        addr = Replace(addr, "https://", "")
        var parts array
        if Contains(addr, ":") {
            parts = Split(addr, ":")
            if Len(parts) > 2 {
                warn(name, ":")
            }
            var port string
            port = parts[1]
            if port <= 0 {
                warn(name, "port")
            }
            addr = parts[0]
        }
    }
    func checkTcpAddress(addr string){
        var name string
        name = "TCP Address"
        if Size(addr) == 0 {
            warn(name, "size")
        }
        if Contains(addr, " ") {
            warn(name, "space")
        }
        var parts array
        if Contains(addr, ":") {
            parts = Split(addr, ":")
            if Len(parts) > 2 {
                warn(name, ":")
            }
            var port string
            port = parts[1]
            if port <= 0 {
                warn(name, "port")
            }
            addr = parts[0]
        }
        parts = Split(addr, ".")
        if Len(parts) != 4 {
            warn(name, "too many parts")
        }
        var i int
        while i < 4 {
            var n int
            n = Int(parts[i])
            if n < 0 || n > 255 {
                warn(name, "part")
            }
            i=i+1
        }
    }
    func checkPub(pub string){
        if PubToID(pub) == 0 {
            warning "Public key invalid"
        }
    }
    func checkKeyId(keyid int){
        var existed map
        existed = DBFind("keys").Where("id=?", keyid).Row()
        if !existed {
            warning "Key id not found"
        }
    }
    func getNodesString() string{
        // kostyl
        // return json-like string from full_nodes trimmed "[" and "]"
        var nodes string
        nodes = SysParamString("full_nodes")
        if HasPrefix(nodes, "[") {
            nodes = Substr(nodes, 1, Size(nodes)-2)
        }
        return nodes
    }
    conditions{
        var app_id int
        app_id = Int(DBFind("applications").Columns("name,id").Where("name='Basic application'").One("id"))
        var id int
        id = Int(AppParam(app_id,"voting_sysparams_template_id"))
        if $TemplateId != id || $TemplateId <= 0 {
            warning "Template id not found"
        }
        checkKeyId($KeyId)
        checkPub($PubKey)
        checkApiAddress($ApiAddress)
        checkTcpAddress($TcpAddress)
    }

    action{
        var sep, nodes, value int
        sep = ","
        nodes = getNodesString()
        if Size(nodes) == 0{
            sep = ""
        }
        value = Sprintf(`{"tcp_address":"%v", "api_address":"%v", "key_id":"%v", "public_key":"%v"}`, $TcpAddress, $ApiAddress, $KeyId, $PubKey)
        nodes = Sprintf("[%v%v%v]", nodes, sep, value)
        voting_TemplateRun("TemplateId,Start,End,EndTime,StartTime,KeyId,TableWritten,RowId,ColumnName,NewValue,TextDocument", $TemplateId, $Start, $End, $EndTime, $StartTime, 0, "", "", "full_nodes", nodes, "")
    }
}

contract voting_TemplateStore{
    data{
        Title string
        TypeParticipants int
        TypeVoting int
        TypeDecision int
        Volume int
        Quorum int
        Subject string
        Voters string "optional" // role of participant
        Candidates string "optional" // role of candidate
        InitContract string "optional" // Execute before the vote
        Vacancies int "optional"
        ContractAccept string "optional"
        ParamsAccept string "optional"
        ContractReject string "optional"
        ParamsReject string "optional"
    }

    func conditions{
        if $TypeParticipants == 3 {
            if Size($Voters) == 0 || $Voters == "NULL" || $Voters == 0 {
                warning "Voters role not found"
            }
        }
        if Size($Title) <3 {
            warning "Please enter title"
        }
        if $Volume <0 || $Volume > 100 {
            warning "Wrong volume range"
        }
        if $Quorum <0 || $Quorum > 100 {
            warning "Wrong quorum range"
        }

        if $TypeDecision == 1 || $TypeDecision == 2 {
            if $Vacancies < 1 {
                warning "Wrong number vacancies"
            }
            if $Candidates == "NULL" || $Candidates == 0 || Size($Candidates) == 0 {
                warning "Wrong candidate role"
            }
        }

        if Size($InitContract) > 0{
            if GetContractByName($InitContract) == 0 {
                warning "Init Contract not found"
            }
        }
        if Size($ContractAccept) > 0{
            if GetContractByName($ContractAccept) == 0 {
                warning "Contract for accepted decision not found"
            }
        }
        if Size($ContractReject) > 0{
            if GetContractByName($ContractReject) == 0{
                warning "Contract for rejected decision not found"
            }
        }
    }

    func action{
        $result = DBInsert("voting_templates", "title,subject,voters,candidates,init_contract,volume,quorum,type_voting,type_participants,type_decision,vacancies,contract_accept,accept_params,contract_reject,reject_params", $Title, $Subject, $Voters, $Candidates, $InitContract, $Volume, $Quorum, $TypeVoting, $TypeParticipants, $TypeDecision, $Vacancies, $ContractAccept, $ParamsAccept, $ContractReject, $ParamsReject)

    }
}

