		}
	
        DBFind(applications, src_applications).Where("deleted=1").Order("id").Count(countvar).Custom(restore_btn){
            Button(Class: btn btn-link, Page: apps_list, Contract: DelApplication, Params: "ApplicationId=#id#", Body: "Restore")
