contract tests_IfAutocast {
    conditions {
        var stringFloat, stringInt, intFloat bool
        stringFloat = "100" == 100.0 && !("100" != 100.0 || "100" < 90.2 || "100" > 200.2)
        stringInt = "100" == 100 && !("100" != 100 || "100" < 90 || "100" > 200)
        intFloat = !(100 != 100.0 || 100 < 99.9 || 100 > 100.1)
        $result = stringFloat && stringInt && intFloat       
    }
}

contract tests_JsonOperationDB {
    data{}

    action {
        var TABLE string
        TABLE = "tests"
        var flag map
        flag["insert"] = "ok"
        var id int
        id = DBInsert(TABLE, "json", flag)

        flag["update"] = "ok"
        DBUpdate(TABLE, id, "json", flag)

        var found map
        found = DBFind(TABLE).Where("id=? and json->update='ok'", id).Row()
        flag["found"] = "ok"
        DBUpdate(TABLE, Int(found["id"]), "json", flag)

        var result map
        result = DBFind(TABLE).Where("id=?", found["id"]).Row()

        if result["json"] == `{"found": "ok", "insert": "ok", "update": "ok"}`{
            $result = true
        }else{
            $result = false
        }
    }
}

contract tests_LongStringToInt {
    data{}

    action {
        var numStr string
        numStr = "123456789101112131415161718192021222324252627282930"
        Println("numStr", Sprintf("%v", numStr))
        var numInt int
        numInt = Int(numStr)
        Println("numInt", Sprintf("%v", numInt))
        if Sprintf("%v", numStr) == Sprintf("%v", numInt) {
            $result = true
        }else{
            $result = false
        }
    }
}

contract tests_Run {
    data{
        Date string "date"
    }
    conditions {
        var tests array
        tests[0] = "tests_JsonOperationDB"
        tests[1] = "tests_IfAutocast"
        tests[2] = "tests_DecodeBase64String"
        tests[3] = "tests_LongStringToInt"
        $tests = tests
    }
    action {
        var i int
        while i < Len($tests) {
            var result bool
            var params map
            result = CallContract($tests[i], params)
            DBInsert("tests", "name,date,result", $tests[i], $Date, Str(result))
            i=i+1
        }

    }
}

contract tests_voting_Accept{
    data{
        VotingTemplateId string "optional"
        KeyId string "optional"
        VotingId string "optional"
    }

     action{
        var voting, params map
        voting = DBFind("votings").Where("id=?", Int($VotingId)).Row()
        params = JSONToMap(voting["voting"])
        var value1, value2, value3 string
        value1 = "voting"
        value2 = params["description"]
        value3 = Sprintf("ACCEPT template_id:%v voting_id:%v KeyId:%v", $VotingTemplateId, $VotingId, $KeyId)
        DBInsert("tests", "value1,value2,value3", value1,value2,value3)
    }
}

contract tests_voting_Create{
    data{
        Start string "datetime"
        End string "datetime"
        StartTime string "datetime"
        EndTime string "datetime"
    }

    action{
        var volume, quorum, vacancies int
        volume = 50
        quorum = 51
        vacancies = 1

        var SUBJ, voters, candidates, INIT, ACCEPT, ACCEPT_PARAMS, REJECT, REJECT_PARAMS, CANDIDATE_ROLE, VOTER_ROLE string
        SUBJ = "voting template subject"
        INIT = "tests_voting_Init"
        ACCEPT = "tests_voting_Accept"
        ACCEPT_PARAMS = ""
        REJECT = "tests_voting_Reject"
        REJECT_PARAMS = ""
        CANDIDATE_ROLE = "Testing Elective role"
        VOTER_ROLE = "Testing Voter role"

        candidates = DBFind("roles").Where("role_name=? and deleted=0", CANDIDATE_ROLE).One("id")
        if !candidates {
            candidates = Roles_Create("role_name,role_type,icon,company_id", CANDIDATE_ROLE, 2, "", 0)
        }
        voters = DBFind("roles").Where("role_name=? and deleted=0", VOTER_ROLE).One("id")
        if !voters {
            voters = Roles_Create("role_name,role_type,icon,company_id", VOTER_ROLE, 1, "", 0)
            Roles_Assign("rid,member_id", voters, $key_id)
        }


        var p, d, v int // types of participant, decision, voting
        d = 1
        while d <= 5 {
            p = 1
            while p <= 3 {
                if p==2 {
                    // passed with manually entering participants
                    p=p+1
                    continue
                }
                v = 1
                while v <= 2 {
                    var title string
                    title = Sprintf("New Voting d%v:p%v:v%v", d, p, v)

                    var templateId, kid int
                    templateId = voting_TemplateStore("Title,TypeParticipants,TypeVoting,TypeDecision,Volume,Quorum,Subject,Voters,Candidates,InitContract,Vacancies,ContractAccept,ParamsAccept,ContractReject,ParamsReject", title,p,v,d,volume,quorum,SUBJ,Str(voters),Str(candidates),INIT,vacancies,ACCEPT,ACCEPT_PARAMS,REJECT,REJECT_PARAMS)

                    var tableWritten, rowId, columnName, newValue, textDocument string
                    tableWritten = "tests"
                    rowId = "1"
                    columnName = "value1"
                    newValue = Sprintf("It is new value for template #%v", templateId)
                    textDocument = Sprintf("Test text Document for voting template #%v", templateId)

                    voting_TemplateRun("TemplateId,Start,End,EndTime,StartTime,KeyId,TableWritten,RowId,ColumnName,NewValue,TextDocument", templateId, $Start, $End, $EndTime, $StartTime, kid, tableWritten, rowId, columnName, newValue, textDocument)

                    v = v+1
                }
                p = p+1
            }
            d = d+1
        }
    }
}

contract tests_voting_Init{
    data{}

    conditions{
        ContractConditions("MainCondition")
    }

    action{
        DBInsert("tests", "value1,value2,value3", "voting", "INIT", "init OK")
    }
}

contract tests_voting_Reject{
    data{
        VotingTemplateId string "optional"
        KeyId string "optional"
        VotingId string "optional"
    }

    action{
        var voting, params map
        voting = DBFind("votings").Where("id=?", Int($VotingId)).Row()
        params = JSONToMap(voting["voting"])
        var value1, value2, value3 string
        value1 = "voting"
        value2 = params["description"]
        value3 = Sprintf("REJECT template_id:%v voting_id:%v KeyId:%v", $VotingTemplateId, $VotingId, $KeyId)
        DBInsert("tests", "value1,value2,value3", value1,value2,value3)
    }
}
